//=======================
// Utility Functions
//=======================
float random(vec2 v) {
    return fract(sin(dot(v*1., vec2(324.654, 156.546)))*46556.2);
}
mat2 rot(float a) {
    float r = cos(a);
    float f = sin(a);
    return mat2(r, f, -f, r);
}
// Cylinder standing upright on the xz plane
float fCylinder(vec3 p, float r, float h) {
    float d = length(p.xz) - r;
    d = max(d, abs(p.y) - h);
    return d;
}
float sphere(vec3 v, float r) {
    return length(v) - r;
}
float vmax(vec3 v) {
    return max(max(v.x, v.y), v.z);
}
// Box: correct distance to corners
float fBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}
float mixColors(float r, float v, float z) {
    return clamp(.5 + .5*(v - r)/z, 0., 1.);
}
float mixShapes(float v, float f, float r) {
    float z = mixColors(v, f, r);
    return mix(f, v, z) - r*z*(1. - z);
}
float pModPolar(inout vec2 v, float r) {
    float f = 6.28318/r;
    float z = atan(v.y, v.x) + f*.5;
    float m = floor(z/f);
    z = mod(z, f) - f*.5;
    v = vec2(cos(z), sin(z))*length(v);
    return m;
}
void pR45(inout vec2 p) {
    p = (p + vec2(p.y, -p.x))*sqrt(.5);
}
float pMod1(inout float p, float size) {
    float halfSize = size*.5;
    float c = floor((p + halfSize)/size);
    p = mod(p + halfSize, size) - halfSize;
    return c;
}
float fOpUnionColumns(float a, float b, float r, float n) {
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.x -= sqrt(2.)/2.*r;
        p.x += columnradius*sqrt(2.);
        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = length(p) - columnradius;
        result = min(result, p.x);
        result = min(result, a);
        return min(result, b);
    } else {
        return min(a, b);
    }
}
float fOpDifferenceColumns(float a, float b, float r, float n) {
    a = -a;
    float m = min(a, b);
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/n/2.;
        columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.y += columnradius;
        p.x -= sqrt(2.)/2.*r;
        p.x += -columnradius*sqrt(2.)/2.;

        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = -length(p) + columnradius;
        result = max(result, p.x);
        result = min(result, a);
        return -min(result, b);
    } else {
        return -m;
    }
}

Shape character(vec3 c) {
    Shape shape;
    shape.dist = 1000.;
    shape.color = vec4(1.);
    //instiating vars
    vec3 b = c;//body
    vec3 h = c;//head
    vec3 e = c;//eyes
    vec3 eb = c; //eyeball
    vec3 n = c;//nose
    vec3 m = c;//mouth
   	vec3 a = c;//ear
    vec3 w = c;//wing
    vec3 f = c;//arm
    vec3 l = c;//left
    vec3 lf = c; //left foot
    vec3 rf = c; //left foot


    //mouth
    //m.x = abs(m.x)-.3;
    m.y *= cos(sin(m.y*5.)*1.23);
    float mouth = sphere(m - vec3(0., .2 , - 2.),.1); 

    //nose
    vec4 nColor = vec4(0.,0.,0.,1.); //red   
    float nose = fBox(e - vec3(0., .4, - 2.), vec3(.02,.04,.05));

    //eye
    vec4 eColor = vec4(0.,0.,0.,1.); //black
    e.x = abs(e.x) - .3; //mirror
    float eye = sphere(e - vec3(0., .6, - 2.), .1); 


    //eyeball
    vec4 ebColor = vec4(1.,1.,1.,1.); //white
    eb.x = abs(eb.x) - .3; //mirror
    float eyeball = sphere(eb - vec3(-.1, .4,   -3.), 0.03); //animate the blink by using trig 


    //head
    vec4 hColor = vec4(1.,.5,0.,1.); //brown
    float head = sphere(h-vec3(0,.8,0), 1.1);

    //ear
    vec4 aColor = vec4(1.,.6,0.,1.); //brown
    a.x = abs(a.x) - 1.; //mirror
    float ears = sphere(a - vec3(-0.5, 1., - 2.), .2);

    //wing
    b.x *= cos(sin(b.x*.5)*cos(iTime));//stretch
    float body1 = sphere(b - vec3(0., -1.,  0.), 1.); 

    //body
    vec4 bbColor = vec4(1.,1.,1.,1.); //white
    b.y *= cos(sin(b.y*.4)*cos(iTime)*1.4);//stretch  
    float body = sphere(b - vec3(0., -.5,  -1.9), .3); 

    //belly
    vec4 bColor = vec4(1.,.4,0.,1.); //brown
    w.y *= cos(sin(w.y*2.)*1.23);//stretch    
    w.x = abs(w.x)+.4;//mirror
    w.xy *= rot(radians(20.)); //rotate
    float belly = sphere(w - vec3(-.5, -.5,  -2.5), .3);

    bColor /= cos(sin(w.x*10.)) + cos(sin(w.y*10.));

    //arm
    vec4 rColor = vec4(1.,.5,0.,1.); //brown
    f.y *= cos(sin(f.y*2.)*1.23);//stretch 
    float arm = sphere(f - vec3(.4,-.2,-2.-abs(cos(iTime))*.2),.2);

    //left arm
    vec4 lColor = vec4(1.,.5,0.,1.); //brown
    l.y *= cos(sin(l.y*2.)*1.23);//stretch 
    float larm = sphere(l - vec3(-.4,-.2,-2.-abs(sin(iTime))*.2),.2);

    //left foot
    vec4 lfColor = vec4(1.,.5,0.,1.); //brown
    float lfoot = sphere(lf - vec3(.4,-1.2,-2.-abs(sin(iTime))*.2),.2);

    //right foot
    vec4 rfColor = vec4(1.,.5,0.,1.); //brown
    float rfoot = sphere(rf - vec3(-.4,-1.2,-2.-abs(cos(iTime))*.2),.2);
    //float ears = fBox(m - vec3(2., -1, - 2.), vec3(.075, .025, .005));

    shape.dist = min(body, head);
    shape.dist = min(shape.dist, body1);
    shape.dist = min(shape.dist, mouth);
    shape.dist = min(shape.dist, eye);
    shape.dist = min(shape.dist, eyeball);
    shape.dist = min(shape.dist, nose);
    shape.dist = min(shape.dist, belly);
    shape.dist = min(shape.dist, ears);
    shape.dist = min(shape.dist, arm);
    shape.dist = min(shape.dist, larm);
    shape.dist = min(shape.dist, lfoot);
    shape.dist = min(shape.dist, rfoot);


    //shape.dist = fOpUnionColumns(shape.dist, head, .1, 5.);


    shape.color = mix(shape.color, eColor,mixColors(eye,shape.dist,1.));
    shape.color = mix(shape.color, ebColor,mixColors(eyeball,shape.dist,1.));    
    shape.color = mix(shape.color, bColor,mixColors(belly,shape.dist,1.));
    shape.color = mix(shape.color, hColor,mixColors(head,shape.dist,1.));
    shape.color = mix(shape.color, aColor,mixColors(ears,shape.dist,1.)); 
    shape.color = mix(shape.color, rColor,mixColors(arm,shape.dist,.1)); 
    shape.color = mix(shape.color, lfColor,mixColors(lfoot,shape.dist,.1)); 
	shape.color = mix(shape.color, rfColor,mixColors(rfoot,shape.dist,.1)); 
    shape.color = mix(shape.color, lColor,mixColors(larm,shape.dist,.1));     shape.color = mix(shape.color, bbColor,mixColors(body,shape.dist,1.)); 

    return shape;
}

Shape map(vec3 c){
  Shape shape = character(c);
  return shape;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 v = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;


  vec3 cam = vec3(0., 0., -5.);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  	scene.yx *= rot(iTime);
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1; z <= 1.;z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.00001){
      fragColor = c.color*(1. - z); // Hit  - invert pixels
      break;
    }
    scene += f*c.dist;


  }
}

