//=======================
// Utility Functions
//=======================
float random(vec2 v) {
    return fract(sin(dot(v*1., vec2(324.654, 156.546)))*46556.2);
}
mat2 rot(float a) {
    float r = cos(a);
    float f = sin(a);
    return mat2(r, f, -f, r);
}
// Cylinder standing upright on the xz plane
float fCylinder(vec3 p, float r, float h) {
    float d = length(p.xz) - r;
    d = max(d, abs(p.y) - h);
    return d;
}
float sphere(vec3 v, float r) {
    return length(v) - r;
}
float vmax(vec3 v) {
    return max(max(v.x, v.y), v.z);
}
// Box: correct distance to corners
float fBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}
float mixColors(float r, float v, float z) {
    return clamp(.5 + .5*(v - r)/z, 0., 1.);
}
float mixShapes(float v, float f, float r) {
    float z = mixColors(v, f, r);
    return mix(f, v, z) - r*z*(1. - z);
}
float pModPolar(inout vec2 v, float r) {
    float f = 6.28318/r;
    float z = atan(v.y, v.x) + f*.5;
    float m = floor(z/f);
    z = mod(z, f) - f*.5;
    v = vec2(cos(z), sin(z))*length(v);
    return m;
}
void pR45(inout vec2 p) {
    p = (p + vec2(p.y, -p.x))*sqrt(.5);
}
float pMod1(inout float p, float size) {
    float halfSize = size*.5;
    float c = floor((p + halfSize)/size);
    p = mod(p + halfSize, size) - halfSize;
    return c;
}
float fOpUnionColumns(float a, float b, float r, float n) {
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.x -= sqrt(2.)/2.*r;
        p.x += columnradius*sqrt(2.);
        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = length(p) - columnradius;
        result = min(result, p.x);
        result = min(result, a);
        return min(result, b);
    } else {
        return min(a, b);
    }
}
float fOpDifferenceColumns(float a, float b, float r, float n) {
    a = -a;
    float m = min(a, b);
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/n/2.;
        columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.y += columnradius;
        p.x -= sqrt(2.)/2.*r;
        p.x += -columnradius*sqrt(2.)/2.;

        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = -length(p) + columnradius;
        result = max(result, p.x);
        result = min(result, a);
        return -min(result, b);
    } else {
        return -m;
    }
}

Shape character(vec3 c) {
    Shape shape;
    shape.dist = 1000.;
    shape.color = vec4(1.);
    //instiating vars
    vec3 b = c;//body
    vec3 h = c;//head
    vec3 e = c;//eyes
    vec3 n = c;//nose
    vec3 m = c;//mouth
   	vec3 a = c;//ear
    vec3 w = c;//wing


    //mouth
    //m.x = abs(m.x)-.3;
    float mouth = sphere(m - vec3(0., .2 , - 2.),.1); 


    //nose
    vec4 nColor = vec4(1.,.4,0.,1.); //brown    
    float nose = fBox(e - vec3(0., .4, - 2.), vec3(.02,.04,.05));

    //eye
    vec4 eColor = vec4(1.,1.,1.,1.); //white
    e.x = abs(e.x) - .3; //mirror
    float eye = sphere(e - vec3(0., .6, - 2.), .1); 
    float lids = sphere(e - vec3(.0001, .7,   -1.-abs(sin(iTime))), 0.08); //animate the blink by using trig 


    //head
    vec4 hColor = vec4(1.,.5,0.,1.); //brown
    float head = sphere(h-vec3(0,.8,0), 1.1);

    //ear
    vec4 aColor = vec4(1.,.8,0.,1.); //brown
    a.x = abs(a.x) - 1.; //mirror
    float ears = fBox(a- vec3(.2, 2.1,  2.), vec3(.2, .4, .2));

    //wing
    b.x *= cos(sin(b.x*.5)*cos(iTime));//stretch
    float body1 = sphere(b - vec3(0., -1.,  0.), 1.); 

    //body
    b.y *= cos(sin(b.y*.4)*cos(iTime)*1.4);//stretch  
    float body = sphere(b - vec3(0., -.5,  -2.), .3); 

    //belly
    vec4 bColor = vec4(1.,1.,1.,1.); //white
    w.y *= cos(sin(w.y*2.)*1.23);//stretch    
    w.x = abs(w.x)+.4;//mirror
    w.xy *= rot(radians(90.)); //rotate
    float belly = sphere(w - vec3(-.5, -.5,  -2.1), .3);

    bColor /= cos(sin(w.x*10.)) + cos(sin(w.y*10.));


    //float ears = fBox(m - vec3(2., -1, - 2.), vec3(.075, .025, .005));

    shape.dist = min(body, head);
    shape.dist = min(shape.dist, body1);
    shape.dist = min(shape.dist, eye);
    shape.dist = min(shape.dist, nose);
    shape.dist = min(shape.dist, belly);
    shape.dist = min(shape.dist, ears); 
    shape.dist = fOpUnionColumns(shape.dist, lids, .1, 3.);
    shape.dist = fOpUnionColumns(shape.dist, head, .1, 5.);


    shape.color = mix(nColor, eColor,mixColors(eye*0.1,nose,abs(sin(iTime))));
    shape.color = mix(shape.color, bColor,mixColors(belly,shape.dist,1.));
    shape.color = mix(shape.color, hColor,mixColors(head,shape.dist,1.));
    shape.color = mix(shape.color, aColor,mixColors(ears,shape.dist,1.));                                  

    return shape;
}

Shape map(vec3 c){
  Shape shape = character(c);
  return shape;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 v = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;


  vec3 cam = vec3(0., 0., -5.);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  	scene.yx *= rot(iTime);
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1; z <= 1.;z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.00001){
      fragColor = c.color*(1. - z); // Hit  - invert pixels
      break;
    }
    scene += f*c.dist;


  }
}

// Circle Variables
vec2 positionOne = vec2(0.4, 0.5);
vec2 positionTwo = vec2(0.5, 0.5);
vec2 positionThree = vec2(0.6, 0.5);
float radius = 0.2;
const float CIRCLE_FREQ = 1.0;
const float CIRCLE_MAG = 2.0;
// Color Variables
vec3 red = vec3(1.0,0.0,0.0);
vec3 green = vec3(0.0,1.0,0.0);
vec3 blue = vec3(0.0,0.0,1.0);
vec3 bgCol = vec3(1.0,1.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizes screen coords
    vec2 uv = fragCoord/iResolution.xy;
    // Corrects Aspect Ratio x-wise
    float aspectRatio = iResolution.x / iResolution.y;
    // Corrects UV by Aspect Ratio
    uv.x *= aspectRatio;
    // Corrects Circle(s) Position(s) by Aspect Ratio
    positionOne.x *= aspectRatio;
    positionTwo.x *= aspectRatio;
    positionThree.x *= aspectRatio;

    // Initializes Layers for output
    vec4 BGLayer = vec4(bgCol, 1.0);
    vec4 LayerOne = vec4(0.0,0.0,0.0,0.0);
    vec4 LayerTwo = vec4(0.0,0.0,0.0,0.0);
    vec4 LayerThree = vec4(0.0,0.0,0.0,0.0);


    // Modifies middle circle y-value by a moving value
    positionTwo.y = (sin(iTime)+CIRCLE_FREQ)/CIRCLE_MAG;    

    // Draws a circle, and makes it red on Layer One
    float interpOne = step(length(positionOne - uv), radius);
    LayerOne = vec4(red, interpOne);
    // Draws a circle, and makes it Blue on Layer Two
    float interpTwo = step(length(positionTwo - uv), radius);
    LayerTwo = vec4(blue, interpTwo);
    // Draws a circle, and makes it Green on Layer Three
    float interpThree = step(length(positionThree - uv), radius);
    LayerThree = vec4(green, interpThree);
    // Mixes Layers Background, One, Two, and Three to display all three circles
    vec4 Comp = mix(BGLayer, LayerOne, LayerOne.a);
    Comp = mix(Comp, LayerTwo, LayerTwo.a);
    Comp = mix(Comp, LayerThree, LayerThree.a);
    // outputs our mix(es) to screen
    fragColor = Comp;
}

#define fGlobalTime iTime

float noise(float a)
{
  return fract(a*126.38921+13.156);
}

float noise2d(vec2 a)
{
  return fract(sin(dot(a,vec2(12.9345,4.3742)))*131.1563);
}

float verticalNoise(int a){
  float n = noise(float(a));
  return (n*.5+.5) * (n>.5?-1.:1.);
}

float digit(ivec2 p, int d)
{
  int[16] font=int[16](
    0xEAAAE0,
    0x4C44E0,
    0xE2E8E0,
    0xE2E2E0,
    0xAAE220,
    0xE8E2E0,
    0xE8EAE0,
    0xE22220,
    0xEAEAE0,
    0xEAE2E0,
    0xEAEAA0,
    0xCACAC0,
    0xE888E0,
    0xCAAAC0,
    0xE8E8E0,
    0xE8E880
  );
  return float((font[d]>>((3-p.x)+p.y*4))&1);
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  int cellx = int(floor(uv.x * 8.));
  float speed = 1.;
  float verticalShift = fract(verticalNoise(cellx) * fGlobalTime * speed);
  uv.y += verticalShift;
  int celly = int(floor((uv.y) * 8.));
  vec2 subspace = fract(uv*8.);
  bool isNumbers = (celly&1)!=0;

  vec2 cellsize = iResolution.xy/8.;
  ivec2 pixelspace = ivec2(subspace * cellsize);

  if(isNumbers)
  {
    out_color = vec4(digit(pixelspace%ivec2(4,6),int(noise2d(fract(fGlobalTime)+3.*vec2(pixelspace/ivec2(4,6)))*16.)));
    out_color *= step(.5,noise2d(fract(fGlobalTime)+.5*vec2(pixelspace/ivec2(4,6))));
  }
  else
  {
    out_color = vec4(noise2d(subspace) * (noise2d(vec2(cellx,celly)+fract(fGlobalTime))));
    out_color *= noise(uv.x*noise(uv.x+fract(fGlobalTime))+float(celly)*5.1);
    out_color *= noise(uv.y*noise(uv.y+fract(fGlobalTime))+float(cellx)*7.1);
    out_color *= 3.;
  }
}

float hash( vec2 p )
{
	float h = dot(p,vec2(127.1,311.7));	
    return -1.0 + 2.0*fract(sin(h)*43758.5453123);
}

float noise(vec2 p)
{
    vec2 cell = floor(p);

    float BottomLeft = hash(cell);
    float BottomRight = hash(cell + vec2(1.,0.));
    float TopLeft =  hash(cell + vec2(0.,1.));
    float TopRight = hash(cell + vec2(1.,1.));

    vec2 posLocal = fract(p);

    vec2 u = posLocal*posLocal*(3.0-2.*posLocal);

    float BottomLine = mix(BottomLeft,BottomRight,u.x);
    float TopLine = mix(TopLeft,TopRight,u.x);

    float CellInterpolationFinal = mix(BottomLine,TopLine,u.y);

    return CellInterpolationFinal;
}

float perlinNoise(vec2 p,float iteration)
{
    float outValue = 0.;

    for(float i = 0.0;i < iteration;i += 1. )
    {
        float freq = pow(2.,i);
        float Amp = 1. / freq;

        outValue += sin((noise(p * freq) * Amp));
    }
    return outValue;
}


vec2 Turbul(inout vec2 p,float freq,float amp)
{
	p.x += sin(iTime*5. + p.y * freq) * amp;
    p.y += sin(iTime*5. + p.x * freq) * amp;
    return p;
}

void rotate(inout vec2 p,float angle,vec2 rotationOrigin)
{
    p -= rotationOrigin;
    p *= mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
    p += rotationOrigin;
}

vec4 fx(vec2 p)
{
    vec3 col = 0.5 + 0.5*cos(iTime+p.xyx+vec3(0,2,4));

    float noiseVal = 1.;
    float l = length(p*1.)*3.;

    rotate(p,l*l*l,vec2(0.,0.));

    p += perlinNoise(p * 2.,2.)*.1 * noiseVal;
    p -= perlinNoise(p * 4.,2.)*.05 * noiseVal;
    p += perlinNoise(p * 8.,2.)*.025 * noiseVal;
    p -= perlinNoise(p * 8.,2.)*.0125 * noiseVal;

    Turbul(p,20.,0.05 );
    Turbul(p,50.,0.0125 );

    float c = 1. - max( pow(length(p*5.),0.75)*1.,0.);
    c =  1. - abs((sin(iTime) * 0.5 + 0.5) - c)*1.;
    return vec4(c*col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Output to screen
    fragColor = fx(uv)+fx(-uv*1.);
}

