    float l = length(p);
    a = mod(a-per/2., per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

vec2 mo(vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y > p.x) p.xy = p.yx;
    return p;
}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5 * (u+a+abs(mod(u-a+st,2.*st)-st)));
}

float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 );
    return min( a, b ) - h*h*0.25/k;
}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float pulse (float s)
{return exp(-fract(time * tempo) * s);}

float tiktak(float period)
{
    float tik = floor(time*tempo)+pow(fract(time*tempo),3.);
    tik *= 3.*period;
    return tik;
}

float sphe (vec3 p, float r)
{return length(p)-r;}

float od (vec3 p, float d)
{return dot(p, normalize(sign(p)))-d;}

float cyl (vec2 p, float r)
{return length(p)-r;}

float box( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sc (vec3 p, float d)
{
    p = abs(p);
    p = max(p.xyz, p.yzx);
    return min(p.x, min(p.y,p.z)) - d;
}

float prim1 (vec3 p)
{
    return max(-sc(p, 0.5), sphe(p, 1.));
}

float prim2 (vec3 p)
{
    float p1 = prim1(p);
    p.xz *= rot(time);
    p.xy *= rot(time);
    return min (p1, od(p,1.2 - pulse(0.1)));
}

float prim3(vec3 p)
{
    float p1 = prim2(p);
    p.xz *= rot(time*0.5);
    p.xz *= rot(p.y*0.8);
    p.xz = moda(p.xz, 2.*PI/5.);
    p.x -= 1.;
    return smin(p1, cyl(p.xz, 0.1), 0.7);

}

float prim4 (vec3 p)
{
    p.yz *= rot(PI/2.);
    p.xy = mo(p.xy, vec2(1., 2.));
    p.xz = mo(p.xz, vec2(1.));
    return prim3(p);
}

float prim5 (vec3 p)
{
    float per = 6.;
    p.y = mod(p.y - per/2., per) - per/2.;
    return prim4(p);
}

float prim6 (vec3 p)
{
    float p1 = prim5(p);
    float per = 6.;
    p.y = mod(p.y - per/2., per) - per/2.;
    p.xy = moda(p.xy, 2.*PI/5.);
    p.x -= 5.;
    return min(p1,prim5(p));
}

float g = 0.;
float SDF(vec3 p)
{
    float d = prim6(p);
    g+=0.1/(0.1+d*d); 
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    uv += texture(iChannel0, uv+vec2(time*0.2,time*0.1)).r*0.02;

    vec3 ro = vec3(0.001,0.001 + time*tempo,-8.); vec3 p = ro;
    vec3 rd = normalize(vec3(uv,1.));

    float shad = 0.;
    float dither = random(uv);

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/ITER;
            break;
        }
        d *= 0.9 + dither*0.1;
        p += d*rd ;
    }

    float t = length(ro-p);

    vec3 pal = palette
        (length(uv),
         vec3(0.5),
         vec3(0.5),
         vec3(0.5),
         vec3(0.1,0.,0.7));

    vec3 c = vec3(shad) * pal;
    c = mix(c, vec3(0.,0.3,.3), 1.-exp(-0.001*t*t));
    c += g *0.08*(1.-length(uv*0.7));
    fragColor = vec4(c,1.);
}

