// This is where all the Distance Fields (DFs) are added into the scene
// also know as map() in iq's shaders :D
vec2 scene(in vec3 point)
{
    // To make the sphere move along with the camera, for now it has the same speed added to it plus an offset
    // Z is depth in this case ;)
    // For some reason, the shape is off center if set to 0.0 ? 
    vec2 pyramid = vec2(sdPryamid4(point - vec3(0.05, sin(iTime*PI)+4.0, iTime*20.0+1.0), vec3(0.65,0.33,1.0)), PYRAMID);


    // FOR TESTING ONLY
    //vec2 sphere1 = vec2(sdSphere(point-vec3(0.0, sin(iTime*PI)+4.0, iTime*20.0+15.0), 8.0), PYRAMID);

    float mountainsHeight = mix(1.0, smoothstep(-.05, 1., iqnoise(point.xz*.75, 1.0, 1.0)*.25), saturate(abs(point.x)-3.0));
    vec2 mountainsGround = vec2(sdPlane2(point, vec4(0.0, mountainsHeight, 0.0, 0.0)), FLOOR);

    vec2 result = opU(mountainsGround, pyramid);

    return result;
}


//// MORE IQ STUFF
// http://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
// Normals can be estimated from a gradient!
vec3 calculateNormal(in vec3 point)
{
    vec2 h = vec2(EPSILON, 0.0); // Some small value(s)
    return normalize(vec3(scene(point + h.xyy).x - scene(point - h.xyy).x,
                          scene(point + h.yxy).x - scene(point - h.yxy).x,
                          scene(point + h.yyx).x - scene(point - h.yyx).x));
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.12*float(i)*0.25 + 0.01;
        vec3 aopos =  nor * hr + pos;
        float dd = scene( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return saturate(1.0 - 3.0*occ);    
}
//// END IQ STUFF

vec2 raymarch(in vec3 origin, in vec3 ray)
{
    float t = 0.0; // t is the clipping plane where anything below this is removed.
    float id = -1.0; // For the Background
    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec2 dist = scene(origin + ray * t);
        if (dist.x < EPSILON)
        {
            break; // We are inside the surface.
        }
        // Move along the ray in constant steps
        t += dist.x;
        // Since each element has an ID, we want that there too!
        id = dist.y;

        if (t >= MAX_DIST)
        {
            id = BACKGROUND;
            return vec2(MAX_DIST, id); //We are too far away!
        }
    }
    return vec2(t, id);
}

vec4 render(in vec3 rayOrigin, in vec3 rayDirection)
{
    // INITIALISE finalColor so it be used later and fed into 
    vec3 finalColor = vec3(0.0);

    // BACKGROUND
    vec3 bgColor = mix(vec3(0.345,0.212,0.388), vec3(0.145,0.357,0.612), saturate(rayDirection.x));
    float stars = smoothstep(0.745, 1.0, iqnoise(rayDirection.xy*UV_SCALE, 1.0, 1.0)); // ORIGINALLY USED UVs BUT USING RAY DIRECTION IS WAY COOLER
    vec3 scene_background = 0.2*stars + bgColor;

    // SCENE THAT ISNT THE BACKGROUND (BUT BEFORE "POST PROCESS" STUFF)
    vec2 result = raymarch(rayOrigin, rayDirection);
    if (result.y > BACKGROUND)
    {

        vec3 point = result.x * rayDirection + rayOrigin;
        vec3 normal = calculateNormal(point);

        vec3 lightDir = normalize(vec3(1.0, 1.0, -4.0));
        float nDotL = saturate(dot(normal, lightDir));

        if (result.y == FLOOR)
        {
            vec2 gridUVs = mod(point.xz, 1.5); //GRID SIZE
            gridUVs = round(mad(GRID_SIZE, 0.5, gridUVs)); // GRID LINE SIZE
            float grid = saturate(mad(-gridUVs.x, gridUVs.y, 1.0));

            vec3 gridColor = vec3(0.345,0.212,0.388);

            float fog = saturate(saturate(1.0-exp(-result.x*0.1)-0.8)*8.0);

            finalColor = mix(gridColor+scene_background, scene_background, saturate(max(fog, 1.-grid)));
            finalColor *= sqrt(calcAO(point, normal)); // Only want AO on FLOOR but not as intense
        }
        else
        {
            /// CURRENTLY ONLY FOR PYRAMID
            vec3 sunColor = vec3(1.0, 0.5, 0.0);
            vec3 ambientColor = vec3(0.0, 0.0, 0.0);
            //finalColor = saturate(mix(nDotL * sunColor, finalColor, 1.0-exp(-result.x*0.1))); // SIMPLE FOG TEST
            finalColor = pow(saturate(nDotL * sunColor +ambientColor), vec3(0.4545));  // Simple Gamma Correction
            //finalColor = -normal.zzz; // FOR INSIDE MASK EVENTUALLY
            //finalColor = (result.yyy-1.5)*fract(-iTime*0.5)*2.5-0.1; //PULSING TEST
        }
    }
    else
    {
        finalColor = scene_background;
    }
    return vec4(finalColor, 1.0);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Remap from [0-1] to [-1-1] so that 0 is the middle point in
    // Also apply aspect ratio correction too
    vec2 orig_uv = fragCoord.xy / iResolution.xy;
    vec2 uv = mad(2.0, orig_uv, -1.0);
    uv.x *= iResolution.x / iResolution.y;

    // RAYMARCH STUFF - THE SCENE
    vec3 ray_orig = vec3(0.0, 3.0, -5.0);
    ray_orig += vec3(0.0, 0.0, iTime*20.);
    vec3 ray_dir = normalize(vec3(uv, 1.16)); // ray_dir.z is FOV
    vec3 sceneColor = render(ray_orig, ray_dir).xyz;

    // SCANLINES
    vec3 linesColor = vec3(0.0, 0.04, 0.08);
    float linesAlpha = sin(uv.y * UV_SCALE + (iTime * SPEED));
    sceneColor = (linesColor * linesAlpha) + sceneColor;

    // VIGNETTE
    float vignette = sqrt(orig_uv.x * orig_uv.y *(1.0-orig_uv.x) * (1.0-orig_uv.y) * 16.0);
    sceneColor *= vignette;
    fragColor = vec4(sceneColor, 1.0);
}

const float scale = 20.0;

const float freqA = 0.4;
const float freqB = 0.5;
const float freqC = 0.7;
const float freqD = 0.2;

const float freqModA = 0.2;
const float freqModB = 0.5;
const float freqModC = 0.3;
const float freqModD = 0.2;

float distanceToSegment(vec2 a, vec2 b, vec2 v) {
    vec2 vA = v - a;
    vec2 bA = b - a;
    float projection = clamp(dot(vA, bA) / dot(bA, bA), 0.0, 1.0);
	return length(vA - bA * projection);	
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / scale;

    vec2 offset = 2.0 * fract(uv) - 1.0;
    vec2 vector = vec2(
        0.5 * cos(iTime*freqA + floor(uv.x) * freqModA) +
        0.5 * sin(iTime*freqB + floor(uv.y) * freqModB),
        0.5 * sin(iTime*freqC + floor(uv.x) * freqModC) +
        0.5 * cos(iTime*freqD + floor(uv.y) * freqModD)
    );

    float strength = 1.0-distanceToSegment(vec2(0.0), vector, offset)*8.0;
    fragColor = vec4(0.5 * strength);
}

#define PI 3.1415926535897932384626433832795
#define RENDER_DEPTH 800
#define CLOSE_ENOUGH 0.00001

#define GRADIENT(pt, func) vec3( \
    func(vec3(pt.x + 0.0001, pt.y, pt.z)) - func(vec3(pt.x - 0.0001, pt.y, pt.z)), \
    func(vec3(pt.x, pt.y + 0.0001, pt.z)) - func(vec3(pt.x, pt.y - 0.0001, pt.z)), \
    func(vec3(pt.x, pt.y, pt.z + 0.0001)) - func(vec3(pt.x, pt.y, pt.z - 0.0001)))

//Array of lights in our scene
const vec3 LIGHT_POS[] = vec3[](vec3(5.0));

//---------------------------------------------------------------------------------------
// Transformations
//---------------------------------------------------------------------------------------

mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0.0, 1.0, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1.0)
    );
}

//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float cube(vec3 p, vec3 dim) {
    vec3 d = abs(p) - dim;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdPlane(vec3 p, vec3 n) {
    return dot(p, normalize(n));
}

float cylinder(vec3 p, float r) {
 	return length(p.xz) - r;
}

float hemisphere(vec3 p, float r) {
 	float sphere = sphere(p, r);
    float plane = sdPlane(p, vec3(0.0, -1.0, 0.0));
    return max(sphere, plane);
}

float cone(vec3 p, vec2 hr) {
    vec2 nhr = normalize(hr);
    float q = length(p.xz);
    return dot(nhr, vec2(q, p.y));
}


float igloo(vec3 p) {
    mat4 r = inverse(rotateZ(PI/2.0));
    float c = max(cylinder((r * vec4(p, 1.0)).xyz , 0.4), sdPlane((r * vec4(p, 1.0)).xyz,(r * vec4(vec3(0, -1.0, 0), 1.0)).xyz));
    float p1 = sdPlane((r * vec4(p - vec3(0.0, 0.0, 0.0), 1.0)).xyz,(r * vec4(vec3(1.0, 0.0, 0.0), 1.0)).xyz);
    float p2 = sdPlane((r * vec4(p + vec3(1.25, 0.0, 0.0), 1.0)).xyz,(r * vec4(vec3(-1.0, 0.0, 0.0), 1.0)).xyz);
    float capped = max(p1, max(c, p2));
    float h =  max(hemisphere(p, 1.0), sdPlane(p, vec3(0, -1.0, 0)));
    return min(capped, h);
}

float tetrahedron(vec3 p) {
    vec3 p1 = p - vec3(1.0, 1.0, 1.0);
    vec3 p2 = p - vec3(1.0, -1.0, -1.0);
    vec3 p3 = p - vec3(-1.0, 1.0, -1.0);
    vec3 p4 = p - vec3(-1.0, -1.0, 1.0);

    vec3 normal1 = cross(p2 - p1, p3 - p1);
    vec3 normal2 = cross(p4 - p1, p2 - p1);
    vec3 normal3 = cross(p3 - p1, p4 - p1);
    vec3 normal4 = cross(p4 - p2, p3 - p2);

    float plane1 = sdPlane(p1, normal1);
    float plane2 = sdPlane(p1, normal2);
    float plane3 = sdPlane(p1, normal3);
    float plane4 = sdPlane(p2, normal4);


    return max(max(plane1, plane2), max(plane3, plane4));   
}

float corkscrew(vec3 p) {
   p.x = p.x - cos(p.y/0.5);
   p.z = p.z - sin(p.y/0.5);
   float cy = cylinder(p, 1.0);
   return cy;
}

float scene(vec3 p) {
    float flr = sdPlane(p - vec3(0, -3.0, 0), vec3(0, 1, 0));
 	//return min(igloo(p), flr);  
    //return min(tetrahedron(p), flr);
    return min(corkscrew(p), flr);
}

//---------------------------------------------------------------------------------------
// Auxiliary helper functions
//---------------------------------------------------------------------------------------

vec3 getNormal(vec3 pt) {
    return normalize(GRADIENT(pt, scene));
}

//---------------------------------------------------------------------------------------
// Illumination and Shading
//---------------------------------------------------------------------------------------

float shade(vec3 eye, vec3 pt, vec3 n) {
    float val = 0.0;

    //Ambient Lighting
    val += 0.2;

    //Cast ray towards our light sources and add it to the illumination depending
    //on the angle with the normal to the surface
    for (int i = 0; i < LIGHT_POS.length(); i++) {
        vec3 l = normalize(LIGHT_POS[i] - pt); 
        val += max(dot(n, l), 0.0);
    }

    return val;
}

vec3 illuminate(vec3 camPos, vec3 rayDir, vec3 pt) {
    vec3 c, n;
    //Using the SDF normal approximation
    n = getNormal(pt);

    //Everything is white
    c = vec3(1.0);
    return shade(camPos, pt, n) * c ;
}

//---------------------------------------------------------------------------------------
// Ray Marching Algorithm
//---------------------------------------------------------------------------------------

vec3 castRay(vec3 camPos, vec3 rayDir) {
    //Using the ray marching approach
    int step = 0;
    float t = 0.0;

    for (float d = 1000.0; step < RENDER_DEPTH && abs(d) > CLOSE_ENOUGH; t += abs(d)) {
        d = scene(camPos + t * rayDir)/2.0;
        step++;
    }

    if (step == RENDER_DEPTH) {
        return vec3(0.0, 0.0, 0.0); //getBackground(rayDir);
    } else {
        return illuminate(camPos, rayDir, camPos + t * rayDir);
    }
}

//---------------------------------------------------------------------------------------
// Set-up
//---------------------------------------------------------------------------------------

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    //The direction in which the camera is pointing
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

//---------------------------------------------------------------------------------------
// Output
//---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //Normalize the 2d coordinates [-1, 1]
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

    //Fix the x axis to original resolution
    p.x *= iResolution.x / iResolution.y;

    //Setting the camera - origin, lookat
    vec3 origin = vec3(10.0*sin(iTime), 1.8, 10.0*cos(iTime));
    vec3 look = vec3(0.0);


    mat3 camera = setCamera(origin, look, 0.0);
    vec3 direction = camera * normalize(vec3(p.xy, 2.0));

    vec3 color = castRay(origin, direction);

    fragColor = vec4(color, 0.0);
}

