Shape Ship(vec3 c){
  Shape shape;

  c.z = repeat(c.z, 1.22); // Repeat
  c.x = abs(c.x)-0.5; // Mirror

  // Ship
  vec3 shipOffset = vec3(1.2, 0.1, 0.);

  	// Walls
  	vec4 sColor = vec4(.1, 0.4, 0.4, 1.);
    //vec4 sColor = vec4(.5, .3, .1, 1.);
  	float walls = fBox(c-shipOffset, vec3(0.2, 6., 0.5));

  	// Windows
  	vec4 wColor = vec4(.2, .2, .2, 1.);
  	float windows = sphere(c-shipOffset, .5);

    // Seats
    vec4 fColor = vec4(.5, .2, .2, 1.);
    float seats = fBox(c-vec3(0., -1.5, 0.), vec3(.5, 1., 0.));

    // Engraving
    float engraving = fBox(c-vec3(1., 1.2, 0.), vec3(.4, .15, 4));


  shape.dist = fOpTongue(walls, windows, .08, .1);
  shape.dist = min(shape.dist, seats);
  shape.dist = max(shape.dist, -engraving);


  shape.color = mix(sColor, wColor, mixColors(windows, walls, 0.));    
  shape.color = mix(shape.color, fColor, mixColors(seats, shape.dist, 2.));

  return shape;
}


