    vec3 color1 = vec3(.4, .4, .6);
    float angleRot = 0.;
    float nbrPieces = 10.;
    for (int i=0; i<40; i++) {
        col += colorEmitterCircles(center, emittRadius, radiusCircles, color1, angleRot, nbrPieces, uv);
        angleRot += 0.1*iTime/2.;
    }

    center = vec2(0.5, 0.);
    radiusCircles = 0.01;
    angleRot = 0.;
