mat3 makeBase( in vec3 w )
{
	float k = inversesqrt(1.0-w.y*w.y);
    return mat3( vec3(-w.z,0.0,w.x)*k, 
                 vec3(-w.x*w.y,1.0-w.y*w.y,-w.y*w.z)*k,
                 w);
}

// http://iquilezles.org/www/articles/intersectors/intersectors.htm
vec2 sphIntersect( in vec3 ro, in vec3 rd, in float rad )
{
	float b = dot( ro, rd );
	float c = dot( ro, ro ) - rad*rad;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
    h = sqrt(h);
	return vec2(-b-h,-b+h);
}

// modified Keinert et al's inverse Spherical Fibonacci Mapping
vec4 inverseSF( in vec3 p, in float n )
{
    const float PI = 3.14159265359;
	const float PHI = 1.61803398875;

    float phi = min(atan(p.y,p.x),PI);
    float k   = max(floor(log(n*PI*sqrt(5.0)*(1.-p.z*p.z))/log(PHI+1.)),2.0);
    float Fk  = pow(PHI,k)/sqrt(5.0);
    vec2  F   = vec2(round(Fk),round(Fk*PHI));
    vec2  G   = PI*(fract((F+1.0)*PHI)-(PHI-1.0));    

    mat2 iB = mat2(F.y,-F.x,G.y,-G.x)/(F.y*G.x-F.x*G.y);
    vec2 c = floor(iB*0.5*vec2(phi,n*p.z-n+1.0));

    float ma = 0.0;
    vec4 res = vec4(0);
    for( int s=0; s<4; s++ )
    {
        vec2 uv = vec2(s&1,s>>1);
        float i = dot(F,uv+c);
        float phi = 2.0*PI*fract(i*PHI);
        float cT = 1.0 - (2.0*i+1.0)/n;
        float sT = sqrt(1.0-cT*cT);
        vec3 q = vec3(cos(phi)*sT, sin(phi)*sT,cT);
        float a = dot(p,q);
        if (a > ma)
        {
            ma = a;
            res.xyz = q;
            res.w = i;
        }
    }
    return res;
}

float map( in vec3 p, out vec4 color, const in bool doColor )
{
    float lp = length(p);
    float dmin = lp-1.0;


    float pp = 0.5+0.5*sin(60.0*lp); pp *= pp; pp *= pp; pp *= pp; pp *= pp;
    dmin = min(dmin,p.y+1.0+0.02*pp);



    color = vec4(0.4,0.5,0.3,1.0)*0.9;

    float s = 1.0;

    //dmin = min( dmin,lp-2.15 );

    for( int i=0; i<2; i++ )
    {
        float h = float(i)/float(2-1);

        // Trick. Do not check the 2x2 neighbors, just snap to the
        // closest point. This is wrong and produces discontinuities
        // in the march, but it's okeish for the purposes of this shader
        vec4 fibo = inverseSF(normalize(p), 65.0+35.0*h);

        // snap
        p -= fibo.xyz;

        // orient to surface
        p *= makeBase(normalize( fibo.xyz + 0.08*sin(fibo.y + 2.0*iTime + vec3(0.0,2.0,4.0))));

        // scale
        float scale = 7.0 + 3.0*sin(111.0*fibo.w);
        if( i==0 ) scale += 4.0*(1.0-smoothstep(-0.5,-0.4,fibo.y));
        scale *= 1.0 + 3.0*smoothstep(0.9,1.0,cos(0.25*iTime + fibo.w*141.7));
        p *= scale;

        // translate and deform
        p.z -= 2.3 + length(p.xy)*1.1*abs(sin(fibo.w*212.1));

        //-----

        s *= scale;
		// distance to line segment/capsule 
        float d = length( p - vec3(0.0,0.0,clamp(p.z,-6.0,0.0)) ) - 0.1;
        d /= s;

        if( d<dmin )
        {
            if( doColor )
            {
                color.w *= smoothstep(0.0, 5.0/s, dmin-d);
                if( i==0 ) color = vec4(0.4,0.5,0.4,1.0);
                color.xyz += 0.3*(1.0-0.45*h)*sin(fibo.w*211.0+vec3(0.0,1.0,2.0));
                color.xyz = max(color.xyz,0.0);
            }
            dmin = d;
        }
        else
        {
          color.w *= 0.4*(0.1 + 0.9*smoothstep(0.0, 1.0/s, d-dmin));
        }
    }

    return dmin;
}


// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float ep )
{
    vec4 kk;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep, kk, false ) + 
					  e.yyx*map( pos + e.yyx*ep, kk, false ) + 
					  e.yxy*map( pos + e.yxy*ep, kk, false ) + 
					  e.xxx*map( pos + e.xxx*ep, kk, false ) );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmax, const float k )
{
    vec2 bound = sphIntersect( ro, rd, 2.15 );
    tmax = min(tmax,bound.y);

	float res = 1.0;
    float t = 0.0;
    //float t = max(0.0,bound.x);
    for( int i=0; i<45; i++ )
    {
    	vec4 kk;
		float h = map( ro + rd*t, kk, false );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float an = (iTime-10.0)*0.05 - 0.4;

    // camera	
    vec3 ro = vec3( 4.5*sin(an), 0.0, 4.5*cos(an) );
    vec3 ta = vec3( 0.0, 0.2, 0.0 );
    // camera-to-world transformation
    mat3 ca = makeBase( normalize(ta-ro) );

    // render    
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    // ray direction
    vec3 rd = ca * normalize( vec3(p.xy,2.3) );

    // background
    vec3 bak = vec3(0.1,0.14,0.18)*1.2 + 0.15*rd.y;

    vec4 col = vec4(0.0);

    // bounding volume
    vec2 bound = sphIntersect( ro, rd, 2.15 );
    if( bound.x>0.0 )
    {
        // raymarch
        vec4 kk;
        float t = bound.x;
        for( int i=0; i<350; i++ )
        {
            vec3 pos = ro + t*rd;

            // evaluate distance
            vec4 mate;
            float h = map(pos,mate,true);

            // color contribution            
            float px = t*0.012;
            if( h<px )
            {
                // surface normal                
                vec3 nor = calcNormal(pos, px*0.5);

                // start lighting                
                vec3 lcol = vec3(0.0);

                // key ligh
                {
                    //dif
                    vec3 lig = normalize(vec3(1.0,1.0 ,0.7));
                    float dif = clamp(0.5+0.5*dot(nor,lig),0.0,1.0);
                    float sha = calcSoftshadow( pos+0.01*lig, lig, 2.0, 6.0 );
                    lcol += mate.xyz*dif*vec3(3.0,0.7,0.5)*1.2*vec3(sha,sha*0.3+0.7*sha*sha,sha*sha);
                    // spec
                    vec3 hal = normalize(lig-rd);
                    float spe = clamp( dot(nor,hal), 0.0, 1.0 );
                    float fre = clamp( dot(-rd,lig), 0.0, 1.0 );
                    fre = 0.05 + 0.95*pow(fre,5.0);
                    spe *= spe; spe *= spe;
                    col += 1.0*spe*dif*sha*fre*mate.w;
                }

                // back light
                {
                    vec3 lig = normalize(vec3(-1.0,0.0,0.0));
                    float dif = clamp(0.5+0.5*dot(nor,lig),0.0,1.0);
                    lcol += mate.rgb*dif*vec3(1.0,0.9,0.6)*0.06*mate.w;
                }

                // dome light
                {
                    float dif = clamp(0.3+0.7*nor.y,0.0,1.0);
                    lcol += mate.xyz*dif*1.0*vec3(0.1,0.2,0.4)*mate.w*(0.2+0.8*mate.w);
                }

                // fake sss
                {
                    float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
                    lcol += 0.8*vec3(1.0,0.3,0.2)*mate.xyz*mate.xyz*fre*fre*mate.w;
                    //lcol += 0.2*mate.xyz*mate.xyz*fre*fre*mate.w;
                }

                // grade
                lcol = 0.85*pow( lcol, vec3(0.75,0.9,1.0) );


                // composite front to back
                float al = clamp(1.0-h/px,0.0,1.0);
                lcol.rgb *= al;
    			col = col + vec4(lcol,al)*(1.0-col.a);
                if( col.a>0.995 || abs(h)<0.0001 ) break;
            }


            // march ray            
            t += h*0.5;
            if( t>bound.y ) break;
            //if( col.a>0.995 || abs(h)<0.0001 || t>bound.y ) break;
        }
    }


    // composite with background    
    vec3 tot = bak*(1.0-col.w) + col.xyz;

    // gain
    tot *= 0.7 + 0.3*smoothstep( 0.0, 15.0, abs(iTime-25.0) );

    // gamma
    tot = pow( tot, vec3(0.4545) );


    // vignetting
 	vec2 q = fragCoord/iResolution.xy;
    tot *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.2 );

    // output    
    fragColor = vec4( tot, 1.0 );
}

float hash(in vec3 p)
{
    p = fract(p * vec3(821.35, 356.17, 671.313));
    p += dot(p, p+23.5);
    return fract(p.x*p.y*p.z);
}

float noise(in vec3 p)
{
    vec3 ip = floor(p);
    vec3 fp = fract(p);

    float a = hash(ip + vec3(0, 0, 0));
    float b = hash(ip + vec3(1, 0, 0));
    float c = hash(ip + vec3(0, 1, 0));
    float d = hash(ip + vec3(1, 1, 0));
    float e = hash(ip + vec3(0, 0, 1));
    float f = hash(ip + vec3(1, 0, 1));
    float g = hash(ip + vec3(0, 1, 1));
    float h = hash(ip + vec3(1, 1, 1));

    vec3 t = smoothstep(vec3(0), vec3(1), fp);
    return mix(mix(mix(a, b, t.x), mix(c, d, t.x), t.y),
               mix(mix(e, f, t.x), mix(g, h, t.x), t.y), t.z);
}

float fbm(in vec3 p)
{   
    float res = 0.0;
    float amp = 0.5;
    float freq = 2.0;
    for (int i = 0; i < 5; ++i)
    {
        res += amp * noise(freq * p);
        amp *= 0.5;
        freq *= 2.0;
    }
    return res;
}

float bi_fbm(in vec3 p)
{
    return 2.0 * fbm(p) - 1.0;
}

vec3 warp(in vec3 p)
{
    p = p + bi_fbm(0.4*p + mod(0.5*iTime, 100.0));
    p = p + bi_fbm(0.4*p - mod(0.3*iTime, 100.0));
    return p;
}

float map(in vec3 p)
{
    p = mix(p, warp(p), 0.5 * (1.0 + cos(iTime)));
    return 0.5 * (length(p) - 1.0);
}

vec3 map_n(in vec3 p)
{
    vec2 e = vec2(0, 0.001);
    return normalize(vec3(map(p + e.yxx), map(p + e.xyx), map(p + e.xxy)) - map(p));
}

vec3 color_map(in float t)
{
    vec3 a = vec3(1, 0, 0);
    vec3 b = vec3(1, 0.3, 0);
    vec3 c = vec3(1, 0.7, 0);
    vec3 d = vec3(1, 0.3, 0);

    if (t < 0.333)
    {
        return mix(a, b, 3.0*t);
    }
    else if (t < 0.666)
    {	
        return mix(b, c, 3.0*(t - 0.3333));
    }
    else
    {
        return mix(c, d, 3.0*(t - 0.6666));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 at = vec3(0);
    vec3 ro = vec3(0, 0, -3);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(cam_x * uv.x + cam_y * uv.y + 1.73 * cam_z);

    int iter = 0;
    int matid = -1;
    float t = 0.001;
    float t_max = 20.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        float d = map(ro + t*rd);
        if (d < 0.001)
        {
            matid = 0;
            iter = i;
            break;
        }
        t += d;
    }

    float occ = 1.0 - float(iter) / 256.0;

    vec3 sky = vec3(0.6, 0.7, 0.8);
    vec3 col = sky;
    if (matid != -1)
    {
        vec3 p = ro + t*rd;
        vec3 n = map_n(p);
        vec3 l = normalize(vec3(0.5, 0.4, -0.5));
        vec3 sun = vec3(1.5);
        vec3 albedo = vec3(0.9) * color_map(fbm(warp(p)));

        vec3 direct_light = max(0.0, dot(n, l)) * sun;
        vec3 indirect_light = pow(occ, 10.0) * (0.2 * sky);

        col = (indirect_light + direct_light) * albedo;
    }

    col = 1.0-exp(-col);
    col = sqrt(col);
    fragColor = vec4(col,1.0);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec3 opRep( in vec3 p, in vec3 c)
{
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

float sdf(in vec3 p, out int matid)
{
    vec2 c = vec2(sin(iTime) + 2.5);
	p.xz = mod(p.xz, c) - 0.5 * c;

    float sphere_sdf = length(p - vec3(0, 1, 0)) - 1.0;
    float box_sdf = sdBox(p - vec3(0, 1, 0), vec3(0.8));
    float obj_sdf = max(-sphere_sdf, box_sdf);
    float floor_sdf = abs(p.y);

    float sdf = min(obj_sdf, floor_sdf);
    if (sdf == obj_sdf)
    {
        matid = 1;
    }
    else
    {
        matid = 0;
    }

    return sdf;
}

vec3 sdf_n(in vec3 p)
{
    vec3 res = vec3(0);

    int ignored;
    res.x = sdf(p + vec3(0.001, 0, 0), ignored) - sdf(p, ignored);
    res.y = sdf(p + vec3(0, 0.001, 0), ignored) - sdf(p, ignored);
    res.z = sdf(p + vec3(0, 0, 0.001), ignored) - sdf(p, ignored);

    return normalize(res);
}

float intersect(in vec3 ro, in vec3 rd, out int matid)
{
	float t = 0.0;
    matid = -1;
    for (int i = 0; i < 256; ++i)
    {
        int curr_matid = -1;
		float d = sdf(ro + t*rd, curr_matid);
        if (d < 0.0001)
        {
            matid = curr_matid;
            break;
        }
        t += d;
    }
    return t;
}

float shadow(in vec3 p, in vec3 l)
{
    float t = 0.15;
    float t_max = 20.0;

    float res = 1.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        int ignored;
        float d = sdf(p + t*l, ignored);
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 8.0 * d / t);
    }

    return res;
}

float ao(in vec3 p, in vec3 n)
{
    float e = 0.1;
    float res = 0.0;

#define AO_ITER 5

    int ignored;
    float weight = 0.5;
    for (int i = 1; i <= AO_ITER; ++i)
    {
        float d = e * float(i);
        res += weight * (1.0 - (d - sdf(p + d * n, ignored)));
        weight *= 0.5;
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float cam_d = 3.0;
    float time = 0.3 * iTime;
    vec3 ro = vec3(cam_d*sin(time), 2.5, cam_d*cos(time));
    vec3 at = vec3(0, 1, 0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.73 * cam_z);

    vec3 col = vec3(0);

    int matid = -1;
    float t = intersect(ro, rd, matid);
    if (matid != -1)
    { 
        vec3 l = normalize(vec3(0.5, 0.5, -0.2));
        vec3 n = sdf_n(ro + t*rd);
        vec3 sun_light = vec3(1);
        float projected_ratio = max(0.0, dot(l, n));
        vec3 direct_light = shadow(ro + t*rd, l) * sun_light * projected_ratio;
        vec3 indirect_light = ao(ro + t*rd, n) * vec3(0.3);

        vec3 albedo = vec3(0.9);      
		col = albedo * (0.7 * direct_light + indirect_light);

        col = mix(col, vec3(0), pow(t / 30.0, 2.0));
    }

    fragColor = vec4(col, 1.0);
}

