int largestPrimeLessThan(int n) {
    if (n < 3) return 2;
	bool prime[2048];
    for (int i = 0; i < prime.length(); i++) {
    	prime[i] = true;
    }    
    for (int p = 2; p * p <= n; p++) { 
        if (prime[p]) { 
            for (int i = p * 2; i <= n; i += p) 
                prime[i] = false; 
        } 
    } 
	for (int i = n - 1; i >= 0; i--) {
    	if (prime[i]) return i;
    }    
}


