        	col *= (1. + 0.1 * sin( 10. * atan(q.y, q.x)));	

        	q.x += 0.4; 

        	col = mix(white, crimson, sqrt(sin(abs(q.y) + abs(q.x))));
        	col.b *= (sin(iTime) + 1.);

        	if(mod(iTime, 64.) < 32.) sign = -1.;
        	q = rotate(q, sign * iTime );
