        	    float by = float(p.y * rows);
        	    float place = bx / dw;
        	    float dx = bx - floor(place) * dw;
        	    float dy = floor(by);
        	    uint digit = bitmap[int(place)]; 
        	    int bitx = int(dy * dw + dx);
        	    int charSize = int(dw * dh);
        	    bitx = charSize - bitx ;
        	    col *= (1. - float((digit >> bitx) & 1u));        
