    // and finally return the colour:
    fragColor = vec4(col, 1.0);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float line(vec2 uv, float m, float b, float thickness, float fuzz) {
    float y = m * uv.x + b;
    float d = thickness - distance(uv, vec2(uv.x, y));
    d =  smoothstep(0., thickness, d);
    if (uv.y >= y - thickness && uv.y <= y + thickness) {
        return mix(1., d, fuzz);
    }
    return 0.;
}

