void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float numLines = 30. * smoothstep(0.0, 1., abs(sin(iTime*0.15)));
    vec2 uv = fragCoord/iResolution.xy;
    uv = translate(uv, vec2(0.5, 0.5));
    uv *= rotate2d(-iTime);
	vec3 col;
    for (float i = -numLines; i < numLines; i++) {
        vec2 shift = uv * rotate2d(i * 2. * 3.14 / numLines);
        shift += translate(uv, vec2(0., 0.1));
        col += vec3(
            line(shift, 1.5/iResolution.x)
        ) * (i < 0. ? p : b);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

