#define PI 3.14159
#define TAU PI*2.

vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    id.x = 3.14*floor((a-an*.5)*count)/count;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}

