{
    float	mind = 1e5;
    float var = .0, var1 = .0, var2 = .0;

    vec3	pr = p;
	pr.z-=-10.;
    rotate(pr.zy, iTime*.1250*.0+iMouse.x*.01*2.+.0);
    rotate(pr.xz, iTime*.2*4.0*.0+iMouse.y*.01*2. +0.*1.57);

    // compute angle x^y
    var1 = atan(pr.x, pr.y);
    // prepare space for toris creation
    vec2 q = vec2(length(pr.xy)-5.0, pr.z-var1*1.*1.0 );

    // I use this for texcoord in torus deformed space
    var2 = atan(q.x, q.y);
    vec2 tcd = 2.*vec2(vec2(1.0*var2/6.28, +2.0*var1/6.28).xy*1.);
    // hexagon function courtesy of iq
    var = 1.-hexagon(tcd*13.*1.);
    // this value will be used to rotate the space
    // since it will go into a sine, remap it [-3.14 : +3.14]
    // we take value from texture
    id.y = length(texture(iChannel0, .2050*vec2(+var2*1.0, q.y*-.1250*0.+1.0*var1).xy*1.
                    ).x)*6.28-3.14;
    // this is the triangle pattern
    tcd = skew(tcd);
    tcd = fract(tcd*26.);
    // add triangle pattern to rotate value
    id.y += 10.*max(step(tcd.x,tcd.y)*1.*sin(iTime*.125+id.z*200.+1.57), .0);
    // add hexagon id to rotate value
    id.y += id.z*10.;
    // make a sine of rotate value
    id.y = 1.14*sin(id.y*.5+iTime*.5125*3.0)/max(1., 1.+sin(var1+iTime*.0) );

    // rotate the space of the toris that will be created
    rotate(q.xy, id.y-iTime*.125*-.0 +var1*.0);

    // making 8 toris, give them a different color each (id given in modA function)
    q.xy = modA(q.xy, 8.);
    // pull the toris appart from toris space center depending on angle x^y
    q.x += -.75-.505*var1;

    // compute length of toris
	mind = mylength(q)-.325205+.0+1.0*-.12500*var1-.06*abs(sin(id.z*1.+iTime*3.50))*2.0;//+.25*sin(iTime+id.x*5.+var1*0.);

    // we NEED a cap or else depending on view direction we'll have a load of artifacts
    // this is due to atan(x, y) near y == 0.
    // so compute length of a toris to put on the open end of the horn
    vec2 qq = vec2(length(pr.yz-vec2(-5., 3.14*1.0))-2.35, pr.x-.01);
    float ming = max(abs(qq.x)-.7, abs(qq.y)-.025 )-0.;

    mind = min(mind, ming);

    // this is one way to do transparency in raymarching
    mind = mix(mind, abs(mind)+.01, .5+.5*sin(iTime*.25+id.z));

    // return distfield * fudgevalue to lessen artifacts
    return(mind)*1.*.5;
}

vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	ret = vec4(0.0, 0.0, 0.0, 0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x*1.;
        float	d = 5.;
        // this is the lighting
        // the color is choosen by the id of the torus with basic sine coloring
        h += (1.0*1.*
            .00125/max(.1, dist.x*dist.x*.1+.0250502501*8.0) 
             )
            *
            vec3
        (
    		1.+(sin(1.0*(id.x)+0.00) )
            ,
            1.+(sin(1.0*(id.x)+1.04) )
            ,
            1.+(sin(1.0*(id.x)+2.08) )
        )
            ;
        if ( dist.x < E || dist.y >= FAR)
            break;
        ret.x++;
    }
    ret.w = dist.y;
    ret.y = dist.x;
    return (ret);
}

// Utilities

float	mylength(vec3 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y + p.z;
    ret = pow(ret, 1./8.);

    return ret;
}

float	mylength(vec2 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y;
    ret = pow(ret, 1./8.);

    return ret;
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}


vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

	return normalize(vec3(
           march(pos+eps.xyy, dir).w - march(pos-eps.xyy, dir).w,
           march(pos+eps.yxy, dir).w - march(pos-eps.yxy, dir).w,
           march(pos+eps.yyx, dir).w - march(pos-eps.yyx, dir).w ));
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

