Shape Hallway(vec3 c){
  Shape shape;
    vec4 color = vec4(.65);
    vec3 a = c;
    vec3 l = c;
    vec3 f = c;
    vec3 s = c;
    vec3 co = c;

    c.z = repeat(c.z, 2.);
    a.z = repeat(a.z, 1.5);
    l.z = repeat(l.z, 3.);
    f.z = repeat(f.z, 2.);
    s.z = repeat(s.z, 3.);
    co.z = repeat(co.z, 2.);

    c.x = abs(c.x)-.5;
    vec4 colColor = vec4(.4, .4, .1, .9);
    float b1 = fBox(c-vec3(0.7, .0, 0.), vec3(.2, 1.1, 0.2)); //Columns
    float b2 = fBox(c-vec3(0.7, .8, 0.), vec3(.3, 0.1, 1.)); //Connectors

    co.x = abs(co.x)-.5;
    co.z = abs(co.z)-.4;
    float c1 = fBox(co-vec3(.5, -.1, -0.2), vec3(.1, .617, .1)); //Corner Cutouts

    a.xy *= rot(-a.x*.5);
    vec4 roofColor = vec4(.5, .5, .3, 1.);
    float b3 = fBox(a-vec3(0., 1.1, 0.), vec3(.42, .1, .8)); //Roof

    vec4 flColor = vec4(.8, .8, 0., .7);
    float fl = fBox(f-vec3(0., -.9, 0.), vec3(1.4, .05, 1.)); //Floor

    l.yz *= rot(radians(90.));
    vec4 lColor = vec4(1., 1., 0., 1.);
    float lights = sdCone(l-vec3(0., 0., -.8), vec2(.7, .3));

    vec4 sColor = vec4(1., 1., 0., 1.);
    float sOrb = sphere(s-vec3(0., .9, 0.), .05);
    s.z = repeat(s.z, 1.2);
    s.x = abs(s.x)-.2;
    s.z = abs(s.z)-.3;
    s.xz *= random(s.xz)*2.;
    float sDrops = sphere(s-vec3(0., -.8, 0.), .01);

    shape.dist = fOpUnionStairs(b1, b2, .2, 5.);
    shape.dist = fOpUnionChamfer(b3, shape.dist, .05);
    shape.dist = fOpUnionStairs(shape.dist, fl, .2, 3.);
    shape.dist = min(shape.dist, lights);
    shape.dist = fOpUnionRound(shape.dist, sOrb, .1);
    shape.dist = max(shape.dist, -c1);
    shape.dist = min(shape.dist, sDrops);

    shape.color = color;
    shape.color = mix(roofColor, colColor, mixColors(b1, b3, .5));
    shape.color = mix(shape.color, flColor, mixColors(fl, shape.dist, .5));
    shape.color = mix(shape.color, lColor, mixColors(lights, shape.dist, .5));
    shape.color = mix(shape.color, sColor, mixColors(sOrb, shape.dist, .5));

  return shape;
}


Shape map(vec3 c){
  Shape hall = Hallway(c);
  return hall;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., iTime);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.0001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;

  }
}

#define MAX_DIST 500.0

#define INTEGRATE_UNBOUNDED_STUFF 1
// set to 0 to make the density of the glowy stuff 1/(distance^2)
// by default it's 1/distance, which means the total glow contribution grows with MAX_DIST
// I think it looks better with 1/distance
// but, hey, neither of these functions come from "physics"
// (or, if they do, it is only by coincidence)
//
// p.s. looks really nice when MAX_DIST is huge (thanks @CLPB for pointing this out)

const vec3 light = vec3(0.48, 0.64, -0.6);

vec3 ball1;
vec3 ball2;
vec3 ball3;

vec3 radii;

float sdf1(in vec3 pt) {
    return length(max(vec3(0.0), abs(pt - ball1) - 0.5 * radii.x)) - 0.25 * radii.x;
}

float sdf2(in vec3 pt) {
    vec2 tor = vec2(length(pt.xy - ball2.xy) - 0.75 * radii.y, pt.z);
    return length(tor) - 0.25* radii.y;
}

float sdf3(in vec3 pt) {
    return length(pt - ball3) - radii.z;
}

vec3 sdfs(in vec3 pt) {
    return vec3(sdf1(pt), sdf2(pt), sdf3(pt));
}

float sdf(in vec3 pt) {
    return min(sdf1(pt), min(sdf2(pt), sdf3(pt)));
}

float min_comp(in vec3 comps) {
    return min(comps.x, min(comps.y, comps.z));
}

vec3 sdf_grad(in vec3 pt) {
    float f = sdf(pt);
    const float h = 0.001;
    const float h_inv = 1000.0;

    return vec3(sdf(pt + vec3(h, 0.0, 0.0)) - f,
                sdf(pt + vec3(0.0, h, 0.0)) - f,
                sdf(pt + vec3(0.0, 0.0, h)) - f);
}

float raymarch(in vec3 orig, in vec3 dir, out vec3 integral) {
    integral = vec3(0.0);
    float curr = 0.0;
    const float step_ratio = 0.25;
    vec3 curr_sdf = sdfs(orig);
    float dist = step_ratio * min_comp(curr_sdf);
    vec3 next_sdf = sdfs(orig + dir * dist);
    // integral from 0 to d of 1/(a+bx) =
    // screw it, just average some things.
    integral = dist * (0.25 / curr_sdf + 1.0 / (curr_sdf + next_sdf) + 0.25 / next_sdf);
    float total_dist = dist;
    const vec3 thresh = vec3(0.004);
    for (int i = 0; i < 128; ++i) {
        curr_sdf = next_sdf;
        dist = step_ratio * min_comp(curr_sdf);
        total_dist += dist;
        next_sdf = sdfs(orig + total_dist * dir);
        vec3 mid = 0.5 * (curr_sdf + next_sdf);
#if INTEGRATE_UNBOUNDED_STUFF        
        integral += dist * (0.25 / max(thresh, curr_sdf) + 
                            0.5 / max(thresh, mid ) + 
                            0.25 / max(thresh, next_sdf));
#else
        integral += dist * (0.25 / max(thresh, curr_sdf * curr_sdf) + 
                            0.5 / max(thresh , mid * mid ) + 
                            0.25 / max(thresh, next_sdf * next_sdf));
#endif        
        if (min_comp(next_sdf) < 1.0e-3 || total_dist > MAX_DIST) {
            return total_dist;
        }
    }
    return total_dist;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 orig = vec3(0.0, 0.1, -1.25);
    vec3 dir = normalize(vec3(uv, 1.0));

    mat3 tilt = mat3(1.0, 0.0, 0.0,
                0.0, 0.28, -0.96,
                0.0, 0.96, 0.28); 
    orig = tilt * orig;
    dir = tilt * dir;

    float theta = 0.6 * iTime;
    float ct = cos(theta);
    float st = sin(theta);

    float rad = 0.6;

    ball1 = rad * vec3(ct, st, 0.0);

    ct = cos(theta + 2.0 * 3.141592654 / 3.0);
    st = sin(theta + 2.0 * 3.141592654 / 3.0);
    ball2 = rad * vec3(ct, st, 0.0);

    ct = cos(theta - 2.0 * 3.141592654 / 3.0);
    st = sin(theta - 2.0 * 3.141592654 / 3.0);
    ball3 = rad * vec3(ct, st, 0.0);

    vec3 integral;

    radii = 0.3 + 0.2 * vec3(texture(iChannel0, vec2(0.0, 0.0)).r,
                             texture(iChannel0, vec2(0.5, 0.0)).r,
                             texture(iChannel0, vec2(1.0, 0.0)).r);

    float raydist = raymarch(orig, dir, integral);

#if INTEGRATE_UNBOUNDED_STUFF    
    vec3 col = smoothstep(0.15 * (1.0 *  radii), vec3(0.0), 0.45/(integral));
#else
    vec3 col = smoothstep(0.15 * (1.0 *  radii), vec3(0.0), 1.5/(integral));    
#endif    

    if (raydist < MAX_DIST) {
        vec3 pt = orig + raydist * dir;
        vec3 norm = normalize(sdf_grad(pt));
        vec3 bounce = normalize(reflect(dir, norm));
        col += 0.25 * smoothstep(0.95, 1.0, dot(bounce, light)) * vec3(0.9, 0.8, 1.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

//CBS
//Parallax scrolling fractal galaxy.
//Inspired by JoshP's Simplicity shader: https://www.shadertoy.com/view/lslGWr

// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/
float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}
float field(in vec3 p,float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 26; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

// Less iterations for second layer
float field2(in vec3 p, float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 18; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

vec3 nrand3( vec2 co )
{
	vec3 a = fract( cos( co.x*8.3e-3 + co.y )*vec3(1.3e5, 4.7e5, 2.9e5) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e5, 0.1e5) );
	vec3 c = mix(a, b, 0.5);
	return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y);
	vec3 p = vec3(uvs / 4., 0) + vec3(1., -1.3, 0.);
	p += .2 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));

	float freqs[4];
	//Sound
	freqs[0] = noise(vec3( 0.01*100.0, 0.25 ,iTime/10.0) );
	freqs[1] = noise(vec3( 0.07*100.0, 0.25 ,iTime/10.0) );
	freqs[2] = noise(vec3( 0.15*100.0, 0.25 ,iTime/10.0) );
	freqs[3] = noise(vec3( 0.30*100.0, 0.25 ,iTime/10.0) );

	float t = field(p,freqs[2]);
	float v = (1. - exp((abs(uv.x) - 1.) * 6.)) * (1. - exp((abs(uv.y) - 1.) * 6.));

    //Second Layer
	vec3 p2 = vec3(uvs / (4.+sin(iTime*0.11)*0.2+0.2+sin(iTime*0.15)*0.3+0.4), 1.5) + vec3(2., -1.3, -1.);
	p2 += 0.25 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));
	float t2 = field2(p2,freqs[3]);
	vec4 c2 = mix(.4, 1., v) * vec4(1.3 * t2 * t2 * t2 ,1.8  * t2 * t2 , t2* freqs[0], t2);


	//Let's add some stars
	//Thanks to http://glsl.heroku.com/e#6904.0
	vec2 seed = p.xy * 2.0;	
	seed = floor(seed * iResolution.x);
	vec3 rnd = nrand3( seed );
	vec4 starcolor = vec4(pow(rnd.y,40.0));

	//Second Layer
	vec2 seed2 = p2.xy * 2.0;
	seed2 = floor(seed2 * iResolution.x);
	vec3 rnd2 = nrand3( seed2 );
	starcolor += vec4(pow(rnd2.y,40.0));

	fragColor = mix(freqs[3]-.3, 1., v) * vec4(1.5*freqs[2] * t * t* t , 1.2*freqs[1] * t * t, freqs[3]*t, 1.0)+c2+starcolor;
}

