//drawing a band
float Band(float t, float start, float end, float blur){
    float step1 = smoothstep(start - blur, start + blur, t); //start of band
    float step2 = smoothstep(end + blur, end - blur, t); //end of band

    return step1*step2; //multiplying both sides to make the band 
}

//drawing a rectangle
float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
	float band1 = Band(uv.x, left, right, blur); //vertical band
    float band2 = Band(uv.y, bottom, top, blur); //horizontal band
    return band1 * band2; //multiplying the bands to make a rectangle

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime*1.5;

    uv -= .5; //-0.5 <> 0.5
    uv.x *= iResolution.x/iResolution.y;
    uv*= 7.;
    uv = fract(uv)-.5;
    uv *= smoothstep(uv.x, uv.x, .8);

    vec3 color = vec3(0.1, 0.35, 1.);
    vec3 col = vec3(1.);

    float mask = 0.;
    float mask2 = 0.;

    float x = uv.x;
    float m = sin(t+x*6.2)*.15;
    float m1 = cos(t-x*6.2)*.15; //cos curve for second wave
    float y = uv.y-m;
    float y1 = uv.y-m1;


    mask = Rect(vec2(x,y), -1., 1., -.35, -.15, .01); //top sine wave
    mask2 = Rect(vec2(x,y1), -1., 1., .15, .35, .01); //bottom cosine wave

    float c = mix(uv.x, uv.x, iTime);

    //color = color/random(uv)-random(uv)+c;
    //color += smoothstep(color.x*color.y, color.x*color.y, length(uv.xy));
    color = mix(color/random(uv), color-random(uv), .5);

    col = color+mask+mask2; 


    // Output to screen
    fragColor = vec4(col,color);

}

//self: https://www.shadertoy.com/view/MldfD7
