float mixColors(float r, float v, float z){ //mixes colors together
  return clamp(0.5+0.5*(v-r)/z, 0., 1.);
}

float mixShapes(float v, float f, float r){ //mixes the shapes together
  float z = mixColors(v, f, r);
  return mix(f,v,z)-r*z*(1.-z);
}

float pModPolar(inout vec2 v, float r){ 
  float f = 6.28318/r;
  float z = atan(v.y, v.x)+f*0.5;
  float m = floor(z/f);
  z = mod(z, f)-f*0.5;
  v = vec2(cos(z), sin(z))*length(v);
  return m;
}
