Shape snakes(vec3 c){
  Shape shape;
  float snake;
  shape.dist = 100.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Shape Attributes
  float twist = 10.; // Snake Repeat
  float snakeOffset = 9.; // Snake Offset


  // Stems
  vec3 i = c;
    i.xy *= rot(c.x*0.09+iTime*.5); //rotation
    float detail = pModPolar(i.xz, twist);  
    float x = mix(10., 4., mod(detail, 8.)); // Larger Ripples
    i.x -= snakeOffset; // Offset snakes
    i.x = repeat(i.x, 20.); //repeat them

  	float snakeWidth = sin(iTime*2.)+5.; // width of stems
  	snake = pole(i.xz, snakeWidth+0.002*cos(i.y*x));
    vec4 color = vec4(0.0, 0.95, 0.85, 1.); //color of seaweed

  shape.dist = snake;
  shape.color = color;

  return shape;
}


Shape map(vec3 c){
  Shape snakes = snakes(c);
  return snakes;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y; //coordinate system


  vec3 cam = vec3(0., 10., -400.); //camera location
  vec3 f = normalize(vec3(v, 6.));
  vec3 scene = cam;
  fragColor = vec4(.1, .7, .1, 1.); //background color

  // Ray Marcher
  for(float x = 0.1 ; x <= 1.; x += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.01){
      fragColor = c.color*(.66-x); // Hit  - invert pixels- "brightness" of inverting
      break;
    }
    scene += f * c.dist;


  }
}

