    //create window1
    vec4 windowLayer = vec4(0.0);
    windowPos.y *= aRatio;
    if((length(windowPos.x - uv.x) < windowLength) && (length(windowPos.y - uv.y) < windowLength))
    {
        windowLayer = vec4(windowColor, 0.7);
    }
    //----------------------------------------------------------------------------------------------------------------------
    //create window2
    vec4 window2Layer = vec4(0.0);
    window2Pos.y *= aRatio;
    if((length(window2Pos.x - uv.x) < window2Length) && (length(window2Pos.y - uv.y) < window2Length))
    {
        window2Layer = vec4(window2Color, 0.7);
    }
    //----------------------------------------------------------------------------------------------------------------------
    //create chinmey
    chimnyPos.y *= aRatio;
    vec4 chinmeyLayer = vec4(0.0);
    if((length(chimnyPos.x - uv.x) < chimnyLengthX) && (length(chimnyPos.y - uv.y) < chimnyLengthY))
    {
        chinmeyLayer = vec4(chimneyColor, 1.0);
    }
    //----------------------------------------------------------------------------------------------------------------------
    //create sunset
    vec4 sunSet = vec4(1.0, uv.y, 0.3, 1.0);
   	sunSet.rg *= abs(cos(iTime) * brightness);


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 Comp = mix(BG, sunSet, sunSet.a);
    Comp = mix (Comp, houseLayer, houseLayer.a);
    Comp = mix (Comp, windowLayer, windowLayer.a);
    Comp = mix (Comp, window2Layer, window2Layer.a);
    Comp = mix (Comp, chinmeyLayer, chinmeyLayer.a);





   	fragColor = Comp;
}

#define STEPS 100
#define STOPDIST 1e-3
#define FUDGE 1.
#define ITERS 4
#define PI 3.141592653589793

vec3 color=vec3(1.,1.,1.);
vec3 cam_pos;
float total_len;

vec3 peano_transform(vec3 p){
    const float blockiness = ITERS>0 ? .8 : 1.;
    const float gap=pow(2.,float(-ITERS))/(1.-pow(2.,float(-ITERS))+blockiness);
    const float final_piece=2.*(pow(2.,float(-ITERS))-gap*(1.-pow(2.,float(-ITERS)))) + PI*.5*gap;

    float sublen=total_len=pow(8.,float(ITERS))*final_piece;
    float s=1.;
	float add=0.;
    float rev=1.;
    float flip=1.;
    for(int i=0;i<ITERS;++i){
        sublen*=1./8.;
        float a=(s+gap)*.5;
        vec3 sp=sign(p);
        if(sp==vec3(-1.,-1.,-1.)){p=vec3(+p.z+a,+p.y+a,+p.x+a); add-=rev*sublen*3.5; flip*=-1.; }
        if(sp==vec3(+1.,-1.,-1.)){p=vec3(+p.z+a,+p.x-a,+p.y+a); add-=rev*sublen*2.5; }
        if(sp==vec3(+1.,+1.,-1.)){p=vec3(+p.z+a,+p.x-a,-p.y+a); add-=rev*sublen*1.5; rev*=-1.; }
        if(sp==vec3(-1.,+1.,-1.)){p=vec3(-p.y+a,-p.x-a,+p.z+a); add-=rev*sublen*0.5; flip*=-1.; }
        if(sp==vec3(-1.,+1.,+1.)){p=vec3(-p.y+a,-p.x-a,-p.z+a); add+=rev*sublen*0.5; flip*=-1.; rev*=-1.; }
        if(sp==vec3(+1.,+1.,+1.)){p=vec3(-p.z+a,+p.x-a,-p.y+a); add+=rev*sublen*1.5; }
        if(sp==vec3(+1.,-1.,+1.)){p=vec3(-p.z+a,+p.x-a,+p.y+a); add+=rev*sublen*2.5; rev*=-1.; }
        if(sp==vec3(-1.,-1.,+1.)){p=vec3(+p.y+a,-p.z+a,+p.x+a); add+=rev*sublen*3.5; rev*=-1.; }
        s=(s-gap)*.5;
    }
    if(p.y>-gap && p.z<gap) p=vec3(p.x,length(p.yz-vec2(-gap,gap))-gap,(atan(p.y+gap,gap-p.z)-PI*.25)*gap);
    else if(-p.y>p.z) p=vec3(p.x,-p.z,p.y+(1.-PI*.25)*gap);
    else p.z+=(PI*.25-1.)*gap;
    p.z=p.z*rev+add;
    p.x*=flip;
    return p;
}

float sdf_particle(vec3 p){
    return length(p.xy)-.02;
    float z=p.z*7.+iTime;
    p.xy*=mat2(cos(z*1.3),sin(z*1.3),-sin(z*1.3),cos(z*1.3));
    p.yz*=mat2(cos(z*1.7),sin(z*1.7),-sin(z*1.7),cos(z*1.7));
    p.zx*=mat2(cos(z*2.1),sin(z*2.1),-sin(z*2.1),cos(z*2.1));
    p=abs(p);
    //return max(max(p.x,p.y),p.z)-.02;
    return length(p)-.02;
}

float sdf_inner(vec3 p){
    const float period=.05;
    float z=p.z+iTime*.1;
    p.z=mod(z,period)-.5*.05;
    if(mod(floor(z/period),10.)==0.) color=vec3(.8,.2,.2)*4.;
    else color=vec3(.2,.8,.2)*1.5;
    return sdf_particle(p);

    const float blockiness = ITERS>0 ? .5 : 1.;
    const float gap=pow(2.,float(-ITERS))/(1.-pow(2.,float(-ITERS))+blockiness);
    const float final_piece=2.*(pow(2.,float(-ITERS))-gap*(1.-pow(2.,float(-ITERS)))) + PI*.5*gap;
    //color=vec3(fract(p.z), fract(p.z*2.3), fract(p.z*1.7))*2.;

    //float ang=sin(p.z+iTime*1.3)*sin(iTime*3.);
    float ang=p.z*2.;
    float thickness=pow(max(0.,sin(p.z*3.+iTime*3.)-.95)*20.,.4)*.03+.02;
    //float thickness=step(.93,sin(p.z*3.+iTime*3.))*.03+.02;
    //float thickness=.04;
    p.xy*=mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
    p.xy=abs(p.xy);
    //return length(p.xy)-thickness;
    return max(p.x,p.y)-thickness;
}

float sdf_main(vec3 p){
    vec3 ap=abs(p);
    float d_bbox=-1.1+max(max(ap.x,ap.y),ap.z);
    float d_cam=.2-distance(p,cam_pos);
    float d_cuts=max(d_bbox,d_cam);

    float d_curve=sdf_inner(peano_transform(p));
    if(d_cuts>d_curve && abs(d_curve)<1e-2) color=vec3(3.);
    return max(d_cuts,d_curve);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy*2.-iResolution.xy)/iResolution.x;
    vec2 mouse=(iMouse.xy*2.-iResolution.xy)/iResolution.x;
    vec2 click=(iMouse.zw*2.-iResolution.xy)/iResolution.x;

    vec2 pan;
    if(iMouse.z<=0.){
        //pan=vec2(iTime/3., sin(iTime/2.)*.4); // inside view
        pan=vec2(iTime/1., sin(iTime/2.)*.4); // outside view
    }else{
     	pan=mouse-click;
        pan.x*=6.;
        pan.y*=1.5;
    }

    //vec3 pos0=vec3(sin(pan.x),pan.y,cos(pan.x))*.8; // inside view
    vec3 pos0=vec3(sin(pan.x),pan.y,cos(pan.x))*3.; // outside view
    cam_pos=pos0;
    //vec3 dir=normalize(cross(-pos0,vec3(0.,1.,0.))); // inside view
    vec3 dir=normalize(-pos0); // outside view
    vec3 xdir=normalize(cross(vec3(0.,1.,0.),dir));
    vec3 ydir=normalize(cross(xdir,dir));
    dir+=uv.x*xdir+uv.y*ydir;
    dir=normalize(dir);

    vec3 pos=pos0;
    for(int i=0;i<STEPS;++i){
    	float d=sdf_main(pos)*FUDGE;
        pos+=dir*d;
        if(abs(d)<STOPDIST){
            fragColor=vec4(vec3(sqrt(float(i)/float(STEPS)))*color,1.);
            return;
        }
        if(length(pos)>1e3){
            fragColor=vec4(vec3(sqrt(float(i)/float(STEPS)))*vec3(1.,.5,.7),1.);
            return;
        }
    }
	fragColor = vec4(.2,.5,.7, 1.0);
}

#define T_MAX 15.0

struct point_light
{
    vec3 p;
    vec3 i;
};

vec4 quat(in vec3 v, in float a)
{
    return vec4(v * sin(a / 2.0), cos(a / 2.0));
}

vec4 quat_inv(in vec4 q)
{
    return vec4(-q.xyz, q.w);
}

vec4 p2q(in vec3 p)
{
    return vec4(p, 0);
}

vec4 q_mul(in vec4 q1, in vec4 q2)
{
    return vec4(q1.w*q2.x + q1.x*q2.w + q1.y*q2.z - q1.z*q2.y, 
                q1.w*q2.y - q1.x*q2.z + q1.y*q2.w + q1.z*q2.x, 
                q1.w*q2.z + q1.x*q2.y - q1.y*q2.x + q1.z*q2.w, 
                q1.w*q2.w - q1.x*q2.x - q1.y*q2.y - q1.z*q2.z);
}

vec3 rotate(in vec3 p, in vec3 v, in float a)
{
    vec4 q = quat(v, a);
    return q_mul(q_mul(q, p2q(p)), quat_inv(q)).xyz;
}

vec3 rotateq(in vec3 p, in vec4 q)
{
    return q_mul(q_mul(q, p2q(p)), quat_inv(q)).xyz;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec3 opRep( in vec3 p, in vec3 c)
{
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

float obj_sdf(in vec3 p)
{
    vec4 r1 = quat(vec3(0,1,0), 0.0);
    vec4 r2 = quat(vec3(0,1,0), 1.7);
    vec4 r = normalize(mix(r1, r2, 0.5 + 0.5*sin(iTime)));
    p = rotateq(p, r);

    vec3 dp = vec3(0, 1, 0);
	float sphere_sdf = length(p - dp) - 1.0;
    float box_sdf = sdBox(p - dp, vec3(0.8));
    float res = max(-sphere_sdf, box_sdf);
    return res;
}

float sdf(in vec3 p, out int matid)
{
    float obj_sdf = obj_sdf(p);
    float floor_sdf = abs(p.y);

    float sdf = min(obj_sdf, floor_sdf);
    if (sdf == obj_sdf)
    {
        matid = 1;
    }
    else
    {
        matid = 0;
    }

    return sdf;
}

vec3 sdf_n(in vec3 p)
{
    vec3 res = vec3(0);

    int ignored;
    res.x = sdf(p + vec3(0.001, 0, 0), ignored) - sdf(p, ignored);
    res.y = sdf(p + vec3(0, 0.001, 0), ignored) - sdf(p, ignored);
    res.z = sdf(p + vec3(0, 0, 0.001), ignored) - sdf(p, ignored);

    return normalize(res);
}

float intersect(in vec3 ro, in vec3 rd, out int matid)
{
	float t = 0.0;
    matid = -1;
    for (int i = 0; i < 256; ++i)
    {
        if (t > T_MAX)
        {
            break;
        }

        int curr_matid = -1;
		float d = sdf(ro + t*rd, curr_matid);
        if (d < 0.0001)
        {
            matid = curr_matid;
            break;
        }
        t += d;
    }
    return t;
}

float dir_shadow(in vec3 p, in vec3 l)
{
    float t = 0.15;
    float t_max = 20.0;

    float res = 1.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        int ignored;
        float d = sdf(p + t*l, ignored);
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 8.0 * d / t);
    }

    return res;
}

float point_shadow(in vec3 p, in vec3 light_p)
{
    vec3 l = normalize(light_p - p);

    float t = 0.15;
    float t_max = distance(light_p, p);

    float res = 1.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        int ignored;
        float d = sdf(p + t*l, ignored);
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 64.0 * d / t);
    }

    return res;
}

float ao(in vec3 p, in vec3 n)
{
    float e = 0.1;
    float res = 0.0;

#define AO_ITER 5

    int ignored;
    float weight = 0.5;
    for (int i = 1; i <= AO_ITER; ++i)
    {
        float d = e * float(i);
        res += weight * (1.0 - (d - sdf(p + d * n, ignored)));
        weight *= 0.5;
    }

    return res;
}

vec3 render(in vec2 fragCoord)
{
   	vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float cam_d = 3.0;
    float time = 0.6;
    vec3 ro = vec3(cam_d*sin(time), 2.5, cam_d*cos(time));
    vec3 at = vec3(0, 1, 0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.73 * cam_z);

    vec3 sky = vec3(0.02);
    vec3 col = vec3(0);

    int matid = -1;
    float t = intersect(ro, rd, matid);
    if (matid != -1)
    { 
        vec3 p = ro + t*rd;
        vec3 n = sdf_n(p);

#define PLIGHT_COUNT 1 
        point_light plights[PLIGHT_COUNT];
        plights[0] = point_light(vec3(2, 1, 1), vec3(20));

        vec3 direct_light = vec3(0);
        for (int plight_index = 0; plight_index < PLIGHT_COUNT; ++plight_index)
        {
            vec3 light_i = plights[plight_index].i;
            vec3 light_p = plights[plight_index].p;
        	float light_r = dot(light_p - p, light_p - p);
        	vec3 l = normalize(light_p - p);

        	direct_light += point_shadow(p, light_p) * max(0.0, dot(n, l)) * light_i / (light_r * light_r);
        }

        vec3 indirect_light = ao(p, n) * sky;

        vec3 albedo = vec3(0.9);      
		col = albedo * (0.7 * direct_light + indirect_light);

        col = mix(col, vec3(0), clamp(pow(t / T_MAX, 2.0), 0.0, 1.0));
    }

    return col;
}

vec3 tonemap(in vec3 col)
{
    return col / (1.0 + col);
}

