#define EPS 0.01

vec3 cameraPos = vec3(0.0, 2.0, 1.5);
vec4 plane = vec4(0.0, 1.0, 0.0, 2.0);
vec3 lightDir = vec3(1.0, 1.0, 1.0);
vec3 skyCol = vec3(0, 0.745, 0.9);

const float heightFactor = 3.0; // height factor. will multiply to texture texel.

float distPlane(in vec3 p, vec4 n)
{
    return dot(p, n.xyz) * n.w;
}

float distFunc(in vec3 p)
{
    float d = distPlane(p, plane);
    vec4 tex = texture(iChannel0, mod(p.xz * 0.2, 1.0));
    tex *= heightFactor;
	return d - tex.x;
}

vec3 getNormal(in vec3 p)
{
    const float e = EPS;
    const vec3 dx = vec3(e, 0, 0);
    const vec3 dy = vec3(0, e, 0);
    const vec3 dz = vec3(0, 0, e);

    float d = distFunc(p);

    return normalize(vec3(
    	d - distFunc(p - dx),
    	d - distFunc(p - dy),
    	d - distFunc(p - dz)
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec2 p = (fragCoord.xy * 2.0 - R.xy) / min(R.x, R.y);

    vec4 m = iMouse / iResolution.xxxx;

    vec3 origin = cameraPos;
    float t = iTime * 0.01;
    const float cd = 3.0;
    const float sp = 2.0;
    float x = cos(t * sp) * cd;
    float z = sin(t * sp) * cd;

    vec3 target = vec3(0, 2.5, 0);

    vec3 cPos = origin + vec3(x, 0, z);
    //vec3 cDir = normalize(vec3(m.x, -m.y, -1.0));
    vec3 cDir = normalize(cPos - target);
    vec3 cSide = normalize(cross(cDir, vec3(0, 1, 0)));
    vec3 cUp = normalize(cross(cSide, cDir));

    float targetDepth = 1.3;

    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);

    const int maxSteps = 128;

    float d = 0.0;
    float depth = 0.0;
    vec3 pos;

    vec3 col = vec3(0.0);

    for (int i = 0; i < maxSteps; i++)
    {
        pos = cPos + ray * depth;
        d = distFunc(pos) * 0.1;

        if (d <= EPS)
        {
            break;
        }

        depth += d;
    }

    if (d <= EPS)
    {
        vec3 n = getNormal(pos);
        float diff = dot(n, normalize(lightDir));
        float fog = 1.0 - exp(-depth * 0.12);
        col = mix(vec3(diff), skyCol, fog);
    }
    else
    {
        col = skyCol;
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

// FRAGMENT SHADER FOR SHADERTOY

// Constants.
const int NUM_LIGHTS = 2;
const int NUM_MATERIALS = 6;
const int NUM_PLANES = 2;
const int NUM_SPHERES = 4;
const int NUM_CONES = 1;

const vec3 BACKGROUND_COLOR = vec3( 0.3, 0.5, 1.0 );

 // Vertical field-of-view angle of camera. In radians.
const float FOVY = 50.0 * 3.1415926535 / 180.0; 

// Use this for avoiding the "epsilon problem" or the shadow acne problem.
const float DEFAULT_TMIN = 10.0e-4;

// Use this for tmax for non-shadow ray intersection test.
const float DEFAULT_TMAX = 10.0e6;

// Equivalent to number of recursion levels (0 means ray-casting only).
// We are using iterations to replace recursions.
const int NUM_ITERATIONS = 2;

// Define new struct types.
struct Ray_t {
    vec3 o;  // Ray Origin.
    vec3 d;  // Ray Direction. A unit vector.
};

struct Plane_t {
    // The plane equation is Ax + By + Cz + D = 0.
    float A, B, C, D;
    int materialID;
};

struct Sphere_t {
    vec3 center;
    float radius;
    int materialID;
};

struct Cone_t {
	// Half angle
	float cosa;
	// Height
	float h;
	// Tip position
	vec3 c;
	// Axis
	vec3 v;
	int materialID;
};

struct Light_t {
    vec3 position;  // Point light 3D position.
    vec3 I_a;       // For Ambient.
    vec3 I_source;  // For Diffuse and Specular.
};

struct Material_t {
	// Ambient coefficient.
    vec3 k_a;   
    // Diffuse coefficient.
    vec3 k_d;   
    // Reflected specular coefficient.
    vec3 k_r;   
    // Global reflection coefficient.
    vec3 k_rg;  
    // The specular reflection exponent. Ranges from 0.0 to 128.0. 
    float n;    
};

// Global scene data.
Plane_t Plane[NUM_PLANES];
Sphere_t Sphere[NUM_SPHERES];
Cone_t Cone[NUM_CONES];
Light_t Light[NUM_LIGHTS];
Material_t Material[NUM_MATERIALS];

// Initializes the scene.
void InitScene()
{
    // Horizontal plane.
    Plane[0].A = 0.0;
    Plane[0].B = 0.0;
    Plane[0].C = 1.0;
    Plane[0].D = 0.0;
    Plane[0].materialID = 4;

    // Vertical plane.
    Plane[1].A = -1.0;
    Plane[1].B = 0.0;
    Plane[1].C = 0.0;
    Plane[1].D = 5.1;
    Plane[1].materialID = 0;

    // Center silver sphere.
    Sphere[0].center = vec3( -2., 0., 2.5);
    Sphere[0].radius = 2.5;
    Sphere[0].materialID = 1;

    // Blue sphere.
    Sphere[1].center = vec3( 3. * cos(iTime) - 2., 3. * sin(iTime), 1.5 );
    Sphere[1].radius = 0.5;
    Sphere[1].materialID = 2;

    // Big sphere shell.
    Sphere[2].center = vec3( 0., 0., 0. );
    Sphere[2].radius = 16.;
    Sphere[2].materialID = 4;

    // Green sphere.
    Sphere[3].center = vec3( .5 * cos(3. * iTime) + 2., .5 * sin(3. * iTime), 0.4 );
    Sphere[3].radius = .3;
    Sphere[3].materialID = 5;

    // Cone
    Cone[0].cosa = 0.95;
    Cone[0].h = 3.4;
    Cone[0].c = vec3(2.0, 0.0, 0.0);
    Cone[0].v = vec3(0.0, 0.0, 1.0);
    Cone[0].materialID = 3;

    // Vertical plane: Silver material.
    Material[0].k_d = vec3( 0.5, 0.5, 0.4 );
    Material[0].k_a = 0.9 * Material[0].k_d;
    Material[0].k_r = 1.2 * Material[0].k_d;
    Material[0].k_rg = 0.5 * Material[0].k_r;
    Material[0].n = 64.0;

    // Center sphere: Silver material.
    Material[1].k_d = vec3( 0.5, 0.5, 0.5);
    Material[1].k_a = 0.2 * Material[1].k_d;
    Material[1].k_r = 4.0 * Material[1].k_d;
    Material[1].k_rg = 0.5 * Material[1].k_r;
    Material[1].n = 128.0;

    // Blue sphere: Blue plastic material.
    Material[2].k_d = vec3( 0.2, 0.2, 0.8 );
    Material[2].k_a = 0.4 * Material[2].k_d;
    Material[2].k_r = vec3( 0.5, 0.5, 0.5);
    Material[2].k_rg = 0.5 * Material[2].k_r;
    Material[2].n = 64.0;

    // Cone: Gold material.
    Material[3].k_d = vec3( 0.8, 0.7, 0.1 );
    Material[3].k_a = 0.2 * Material[1].k_d;
    Material[3].k_r = 2.0 * Material[1].k_d;
    Material[3].k_rg = 0.5 * Material[1].k_r;
    Material[3].n = 64.0;

    // Horizontal plane: Red material.
    Material[4].k_d = vec3( 0.2, 0.1, 0.1 );
    Material[4].k_a = 0.2 * Material[0].k_d;
    Material[4].k_r = 0.2 * Material[0].k_d;
    Material[4].k_rg = 0.1 * Material[0].k_r;
    Material[4].n = 128.0;

    // Green sphere: Green plastic material.
    Material[5].k_d = vec3( 0.4, 0.8, 0.4 );
    Material[5].k_a = 0.4 * Material[5].k_d;
    Material[5].k_r = vec3( 0.5, 0.5, 0.5);
    Material[5].k_rg = 0.5 * Material[5].k_r;
    Material[5].n = 64.0;

    // Light 0.
    Light[0].position = vec3( 4.0, -2.0, 9.0 );
    Light[0].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[0].I_source = vec3( 1.0, 1.0, 1.0 );

    // Light 1.
    Light[1].position = vec3( 2.0, 8.0, 8.0 );
    Light[1].I_a = vec3( 0.1, 0.1, 0.1 );
    Light[1].I_source = vec3( 1.0, 1.0, 1.0 );
}

// Computes intersection between a plane and a ray.
// If there is such an intersection, outputs the value of t, the position
// of the intersection (hitPos) and the normal vector at the intersection 
// (hitNormal).
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal ) 
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;

    // We have a hit -- output results.
    t = t0;
    hitPos = ray.o + t0 * ray.d;
    hitNormal = normalize( N );
    return true;
}

// Computes intersection between a plane and a ray for shadow ray intersection.
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;
    return true;
}

// Computes intersection between a sphere and a ray.
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal ) 
{
	// A vector from sphere center towards ray origin
    vec3 V = ray.o - sph.center;
    float a0 = dot(V,V) - sph.radius * sph.radius;
    float DdotV = dot(ray.d, V);

    if (DdotV <= 0.0) {
    	// When the ray shoots towards the sphere
    	float discr = DdotV * DdotV - a0;
    	if (discr >= 0.0) {
    		// Smaller intersection point
    		t = -DdotV - sqrt(discr);
    		hitPos = ray.o + t * ray.d;
    		hitNormal = normalize(hitPos - sph.center);
    		return (t >= tmin && t<= tmax);
    	}
    }
	return false;
}

// Computes intersection between a sphere and a ray for shadow ray intersection.
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax )
{
    // A vector from sphere center towards ray origin
    vec3 V = ray.o - sph.center;
    float a0 = dot(V,V) - sph.radius * sph.radius;
    float DdotV = dot(ray.d, V);

    if (DdotV <= 0.0) {
    	// When the ray shoots towards the sphere
    	float discr = DdotV * DdotV - a0;
    	if (discr >= 0.0) {
    		float t = -DdotV - sqrt(discr);
    		return (t >= tmin && t<= tmax);
    	}
    }
	return false;
}

// Computes intersection between a cone and a ray.
bool IntersectCone( in Cone_t cone, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal ) 
{
    vec3 co = ray.o - cone.c;

    float a = dot(ray.d, cone.v)*dot(ray.d, cone.v) - cone.cosa * cone.cosa;
    float b = 2. * (dot(ray.d, cone.v) * dot(co, cone.v) - dot(ray.d, co) * cone.cosa * cone.cosa);
    float c = dot(co, cone.v) * dot(co, cone.v) - dot(co, co) * cone.cosa * cone.cosa;

    float det = b * b - 4. * a * c;
    if (det < 0.) return false;

    det = sqrt(det);
    float t1 = (-b - det) / (2. * a);
    float t2 = (-b + det) / (2. * a);

    t = t1;
    if (t < 0. || t2 > 0. && t2 < t) t = t2;
    if (t < 0.) return false;

    vec3 cp = ray.o + t * ray.d - cone.c;
    float h = dot(cp, cone.v);
    if (h < 0. || h > cone.h) return false;

    hitPos = ray.o + t * ray.d;
    hitNormal = normalize(cp * dot(cone.v, cp) / dot(cp, cp) - cone.v);

    return (t >= tmin && t<= tmax);
}

// Computes intersection between a cone and a ray for shadow ray intersection.
bool IntersectCone( in Cone_t cone, in Ray_t ray, in float tmin, in float tmax )
{
    vec3 co = ray.o - cone.c;

    float a = dot(ray.d, cone.v)*dot(ray.d, cone.v) - cone.cosa * cone.cosa;
    float b = 2. * (dot(ray.d, cone.v) * dot(co, cone.v) - dot(ray.d, co) * cone.cosa * cone.cosa);
    float c = dot(co, cone.v) * dot(co, cone.v) - dot(co, co) * cone.cosa * cone.cosa;

    float det = b * b - 4. * a * c;
    if (det < 0.) return false;

    det = sqrt(det);
    float t1 = (-b - det) / (2. * a);
    float t2 = (-b + det) / (2. * a);

    float t = t1;
    if (t < 0. || t2 > 0. && t2 < t) t = t2;
    if (t < 0.) return false;
    vec3 cp = ray.o + t * ray.d - cone.c;
    float h = dot(cp, cone.v);
    if (h < 0. || h > cone.h) return false;

    return (t >= tmin && t<= tmax);
}

// Computes (I_a * k_a) + k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ].
vec3 PhongLighting( in vec3 L, in vec3 N, in vec3 V, in bool inShadow, 
                    in Material_t mat, in Light_t light )
{
    if ( inShadow ) {
        return light.I_a * mat.k_a;
    }
    else {
        vec3 R = reflect( -L, N );
        float N_dot_L = max( 0.0, dot( N, L ) );
        float R_dot_V = max( 0.0, dot( R, V ) );
        float R_dot_V_pow_n = ( R_dot_V == 0.0 )? 0.0 : pow( R_dot_V, mat.n );

        return light.I_a * mat.k_a + 
               light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
    }
}

// Casts a ray into the scene and returns color computed at the nearest hit position.
vec3 CastRay( in Ray_t ray, 
              out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg ) 
{
    // Find whether and where the ray hits some object. 
    bool hasHitSomething = false;
    // The ray parameter t at the nearest hit point.
    float nearest_t = DEFAULT_TMAX;
    // 3D position of the nearest hit point.   
    vec3 nearest_hitPos; 
    // Normal vector at the nearest hit point.             
    vec3 nearest_hitNormal;
    // MaterialID of the object at the nearest hit point.          
    int nearest_hitMatID;             

    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    // Try interesecting input ray with all the planes and spheres,
    // and record the front-most (nearest) interesection.
    for (int i = 0; i < NUM_SPHERES; i++ ) {
    	// Try intersect with spheres
    	temp_hasHit = IntersectSphere(Sphere[i], ray, DEFAULT_TMIN, DEFAULT_TMAX,
                      temp_t, temp_hitPos, temp_hitNormal);

    	// If intersect with spheres, compare and find the nearest
    	if (temp_hasHit && temp_t < nearest_t) {
    		hasHitSomething = true;
    		nearest_t = temp_t;
    		nearest_hitPos = temp_hitPos;
    		nearest_hitNormal = temp_hitNormal;
    		nearest_hitMatID = Sphere[i].materialID;
    	}

    }

    for (int i = 0; i < NUM_PLANES; i++ ) {
    	// Try intersect with planes
    	// Use nearest_t as input tmax to determine whether the intersection point
    	//with planes is the nearest
    	temp_hasHit = IntersectPlane(Plane[i], ray, DEFAULT_TMIN, nearest_t,
                      temp_t, temp_hitPos, temp_hitNormal);

    	// If hit, then now the hit point is nearer than spheres if any
    	if (temp_hasHit && temp_t < nearest_t ) {
    		hasHitSomething = true;
    		nearest_t = temp_t;
    		nearest_hitPos = temp_hitPos;
    		nearest_hitNormal = temp_hitNormal;
    		nearest_hitMatID = Plane[i].materialID;
    	}

    }

    for (int i = 0; i < NUM_CONES; i++ ) {
    	// Try intersect with cones
    	// Use nearest_t as input tmax to determine whether the intersection point
    	//with planes is the nearest
    	temp_hasHit = IntersectCone(Cone[i], ray, DEFAULT_TMIN, nearest_t,
                      temp_t, temp_hitPos, temp_hitNormal);

    	// If hit, then now the hit point is nearer than spheres if any
    	if (temp_hasHit && temp_t < nearest_t ) {
    		hasHitSomething = true;
    		nearest_t = temp_t;
    		nearest_hitPos = temp_hitPos;
    		nearest_hitNormal = temp_hitNormal;
    		nearest_hitMatID = Cone[i].materialID;
    	}

    }
    // One of the output results.
    hasHit = hasHitSomething;
    if ( !hasHitSomething ) return BACKGROUND_COLOR;

    vec3 I_local = vec3( 0.0 );

    // For each light source, make a shadow ray, and check if the shadow ray
    // intersects any of the objects.
    Ray_t ShadowRay[NUM_LIGHTS];
    bool inshadow[NUM_LIGHTS];
    for (int i = 0; i < NUM_LIGHTS; i++ ) {
    	// Make a shadow ray (from surface to light) for each light
    	ShadowRay[i].o = nearest_hitPos;
    	ShadowRay[i].d = normalize(Light[i].position - ShadowRay[i].o);

    	for (int j = 0; j < NUM_SPHERES; j++ ) {
    		// If already in shadow, inshadow[i] becomes inaccessible
    		if(!inshadow[i]) {
    			// The point is in shadow if the shadow ray intersects with anything
    		inshadow[i] = IntersectSphere(Sphere[j], ShadowRay[i], DEFAULT_TMIN, 
    			distance(nearest_hitPos, Light[i].position));
    		}
    	}
    	// If not occluded, consider intersection with planes
    	if (!inshadow[i]) {
    		for(int j = 0; j < NUM_PLANES; j++ ) {
    			// If already in shadow, inshadow[i] becomes inaccessible
    			if(!inshadow[i]) {
    				inshadow[i] = IntersectPlane(Plane[j], ShadowRay[i], DEFAULT_TMIN, 
    				distance(nearest_hitPos, Light[i].position));
    			}
    		}
    	}

    	// If not occluded, consider intersection with cones
    	if (!inshadow[i]) {
    		for(int j = 0; j < NUM_CONES; j++ ) {
    			// If already in shadow, inshadow[i] becomes inaccessible
    			if(!inshadow[i]) {
    				inshadow[i] = IntersectCone(Cone[j], ShadowRay[i], DEFAULT_TMIN, 
    				distance(nearest_hitPos, Light[i].position));
    			}
    		}
    	}

    	I_local += PhongLighting( ShadowRay[i].d, nearest_hitNormal, - ray.d, inshadow[i], 
    		Material[nearest_hitMatID], Light[i] );
    }


    // Populate output results.
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;

    return I_local;
}

// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitScene();

    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // Position the camera.
    vec3 cam_pos = vec3( 4. * cos(iTime / 2.) + 1., 10.0, 1. * sin(iTime / 2.) + 3.);
    vec3 cam_lookat = vec3( 0., 0., 0. );
    vec3 cam_up_vec = vec3( 0.0, 0.0, 1.0 );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis) );
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis));

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    pRay.d = normalize( pixel_pos.x * cam_x_axis  +  pixel_pos.y * cam_y_axis  +  pixel_pos_z * cam_z_axis );


    // Start Ray Tracing.
    // Use iterations to emulate the recursion.
    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay = pRay;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ ) 
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = CastRay( nextRay, hasHit, hitPos, hitNormal, k_rg );

        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        nextRay = Ray_t( hitPos, normalize( reflect(nextRay.d, hitNormal) ) );
    }

    fragColor = vec4( I_result, 1.0 );
}

//threshold for hit testing
const float epsilon = 1.0;

// Distance field of a sphere at c with radius r, sampled at p.
float sphere(vec3 c, float r, vec3 p) {
	return length(p - c) - r;
}
// The Scene
float distanceField(vec3 pos) {
    pos.x = pos.x+20.0*cos(0.1*pos.x+2.31);
    pos.y = pos.y + 20.0*sin(0.1*pos.y+4.0*iTime);
    //pos.x = mod(pos.x, 300.0);
	float s1 = sphere(vec3(400.0,150.0*cos(iTime*2.0),0.0), 250., pos);
    float s2 = sphere(vec3(-400.0,150.0*sin(iTime*2.0),0.0), 250., pos);
    vec3 pos2 = vec3(pos.x * cos(iTime*0.1) - pos.y * sin(iTime*0.1), pos.x * sin(iTime*0.1) + pos.y * cos(iTime*0.1), pos.z);
    pos2.y*=1.0-0.1*cos(iTime*3.0);
    float s3 = sphere(vec3(250.0,250.0*sin(iTime*2.0),300.0), 200., pos2);
	return min(min(s1,s2),s3);
}

// Approximates the normal vector at pos.
vec3 distanceFieldNormal(vec3 pos) {
	// Method of central differences.
	const float s = 1. / 2.;

	vec3 n = vec3(distanceField(pos + vec3(s, 0, 0)) - distanceField(pos - vec3(s, 0, 0)),
				  distanceField(pos + vec3(0, s, 0)) - distanceField(pos - vec3(0, s, 0)),
				  distanceField(pos + vec3(0, 0, s)) - distanceField(pos - vec3(0, 0, s)));
	return normalize(n);
}


// Light source intensity
const float i_a = 0.3;
const float i_d = 0.6;
const float i_s = 0.4;

// Material reflectiveness and color
const float k_a = 0.6;
const float k_d = 0.7;
const float k_s = 0.8;
const float alpha = 8.;
const vec3 matColor = vec3(1, 0.2, 0.7);

const vec3 lightpos = vec3(0.0, 1.0, -1.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float near = -iResolution.x * 1.2 / 2.;
	float far  =  iResolution.x * 1.2 / 2.;

	vec3 cameraPosition = vec3(0, 0, -1);

	vec3 pos = vec3(fragCoord.xy - iResolution.xy / 2., near);

	vec3 npos = pos / iResolution.x;

	vec3 dir = normalize(npos - cameraPosition);
	vec3 toEye = -dir;
	fragColor = vec4(0.0, 0.0, 0.0, 1);
    int stepsTaken = 0;
    for (int steps = 0; steps < 50; ++steps) {
		stepsTaken = steps;
		float d = distanceField(pos);

		if (d < epsilon) {
            vec3 normal = distanceFieldNormal(pos);
			vec3 nl_m = normalize(lightpos);
			vec3 r_m = -reflect(nl_m, normal);

            float ambient = i_a * k_a;
            float k_d_2 = 0.5+0.5*cos(pos.z*0.2);
			float diffuse = i_d * k_d_2 * max(dot(nl_m, normal), 0.);
			float spec    = i_s * k_s * pow(max(dot(r_m, toEye), 0.), alpha);

            vec3 light = matColor * (ambient + diffuse) + spec;
            light.x += 0.1*cos(pos.x*0.1);
            light.y += 0.5*cos((pos.y*pos.x*0.01));

			fragColor = vec4(light, 1);
			return;
		} else {
			pos += d * dir;
		}
	}
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);

    // heart shape
    p *= 0.6;
    p.y = -0.1 - p.y*1.2 + abs(p.x)*(1.0-abs(p.x));
    float r = length(p);
	float d = 0.5;


	// heart color
    float mX = (sin(iTime * 0.5) + 1.0) * 0.2;
    float mY = (cos(iTime * 0.5) + 1.0) * 0.2;
	vec3 hcol = vec3((fragCoord.xy)/(iResolution.y),mX);
    for (int i = 0; i < 100; i++){
    	hcol.xzy = vec3(1.3,0.999,0.7)*(abs((abs(hcol)/dot(hcol,hcol)-vec3(1.0,1.0,mY))));
    }

    // apply one more iteration for background color
    vec3 bcol = vec3(0.0);
    bcol.xzy = vec3(1.3,0.999,0.7)*(abs((abs(hcol)/dot(hcol,hcol)-vec3(1.0,1.0,mY))));

    bcol.r *= 0.5;
    hcol.r *= 2.0;

    vec3 col = mix( bcol, hcol, smoothstep( -0.15, 0.15, (d-r)) );

    fragColor = vec4(col,1.0);
}

mat2 rotationMatrix(float theta)
{
    return mat2(
        cos(theta),-sin(theta),
        sin(theta),cos(theta)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 originalUV = uv;
    uv.x *= iResolution.x / iResolution.y;
    uv = fract(uv * 4.) * 2. - 1.;

    for(int i = 0; i < 6; i++)
        uv *= rotationMatrix(acos(dot(uv,uv)) + iTime * float(i));

    vec3 col = vec3(uv,distance(uv,originalUV));


    fragColor = vec4(col,1.0);
}

