Shape storm(vec3 c){
  Shape shape;
  float wall;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Shape Attributes
  float twist = 30.; // Hurricane piece Repeat
  float wallOffset = 4.; // Column Offset, affects camera view


  // Stems
  vec3 i = c;
    i.yz *= rot(c.y*0.1+1.5); //Shows top-view of hurricane
    i.xz *= rot(c.y*.1+iTime*.1); //Spins hurricane, last float controls speed
    float detail = pModPolar(i.xz, twist*1.1); 
    float x = mix(0., 1., mod(detail, 7.6)); // Split column orbs
    i.x -= wallOffset-.5; // Offset columns
  	float stemWidth = .7; //Oscillates width of columns
  	wall = pole(i.xz, stemWidth+.06*sin(i.y*x));


  vec4 color = vec4(.7, .6, .7, .5);
		//Oscillates colors through most of R-B, and down to black
  shape.dist = wall;
  shape.color = color;

  float pattern = cos(sin(c.z*5.)) / cos(sin(c.y*2.));
  shape.color /= pattern;

  return shape;
}


Shape map(vec3 c){
  Shape hurricane = storm(c);
  return hurricane;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(cos(iTime)*.1, sin(iTime)*.1, -11.);
    //Above trig functions rotate camera around perimeter of hurricane
    //Also effect the speed of movement around the hurricane
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(.7, .7, .7, .85); //Sets a cloudy/stormy gray background

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.04){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.015){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}

