  w.x = abs(w.x)+1.;
  w.y = abs(w.y);
  float snow = sphere(w-vec3(4., 3., 0.), .4);

  // Snowball
  q.xy *= rot(radians(35.)*sin(iTime)+.5);
  float snowball = sphere(q-vec3(6., -2., 0.), 1.25);

  // Ground
  vec4 gColor = vec4(2., 2., 2., 1.);
  float ground = fBox(g-vec3(0., -10., 0.), vec3(20., 2., 0.));

  shape.dist = min(shape.dist, head);
  shape.dist = min(shape.dist, body);
