  shape.dist = max(shape.dist, -mouth);
  shape.dist = min(shape.dist, hatBottom);
  shape.dist = min(shape.dist, hatTop);
  shape.dist = min(shape.dist, clouds);
  shape.dist = min(shape.dist, snow);
  shape.dist = min(shape.dist, snowball);

  shape.dist = fOpUnionColumns(shape.dist, ground, 3., 3.); // float fOpUnionColumns(float a, float b, float r, float n)

  //shape.color = hColor;

    shape.color = mix(hColor, bColor, mixColors(body, head, 0.));    
    shape.color = mix(tColor, shape.color, mixColors(shape.dist, bottom, 0.));
  	shape.color = mix(uColor, shape.color, mixColors(shape.dist, button1, 0.));
    shape.color = mix(uColor, shape.color, mixColors(shape.dist, button2, 0.));
    shape.color = mix(aColor, shape.color, mixColors(shape.dist, arms, 0.));
  	shape.color = mix(eColor, shape.color, mixColors(shape.dist, eyes, 0.));
