    shape.color = mix(rColor, shape.color, mixColors(shape.dist, hatBottom, 0.));
    shape.color = mix(rColor, shape.color, mixColors(shape.dist, hatTop, 0.));
  	shape.color = mix(gColor, shape.color, mixColors(shape.dist, ground, 0.));
  	shape.color = mix(wColor, shape.color, mixColors(shape.dist, snow, 0.));
  	shape.color = mix(cColor, shape.color, mixColors(shape.dist, clouds, 0.));
	shape.color = mix(wColor, shape.color, mixColors(shape.dist, snowball, 0.));

  return shape;
