#define LINE_SIZE 2.0
#define GRID_LINE_SIZE 1.0
#define GRID_AXIS_SIZE 3.0
#define GRID_LINES 1.0
#define GRAD_OFFS vec2(0.0001 * ZOOM, 0.0)
#define GRAD(f, p) (vec2(f(p) - f(p + GRAD_OFFS.xy), f(p) - f(p + GRAD_OFFS.yx)) / GRAD_OFFS.xx)
#define PLOT(f, c, d, p) d = mix(c(d, p), d, smoothstep(0.0, (LINE_SIZE / iResolution.y * ZOOM), abs(f(p) / length(GRAD(f,p)))))

#define PI (atan(1.0) * 4.0)
#define ZOOM 10.0

float Func(vec2 p)
{
	return float(!(
		(p.y > 1.0) &&
		(p.y - p.x * p.x > 0.0) &&
		(p.y < 5.0)
	));
}

float Target(vec2 p)
{
	return p.x * p.x * p.x + p.y * p.y;
}

vec3 Color(vec3 d, vec2 p)
{
	return d * 0.8 * (clamp(10.0 - abs(tan(iTime)) * Target(p), 0.0, 1.0)) + vec3(1, 0, 1);
}

// table properties
const int columns = 5;
const int rows = 6;
const int tableCol = 6;
const int[] table = int[](
14, 17, 25, 21, 19, 14, // 0
14,  4,  4,  4, 12,  4, // 1
31,  8,  4,  2, 17, 14, // 2
14, 17,  1,  6,  1, 31, // 3
 2, 31, 18, 10,  6,  2, // 4
14, 17,  1, 30, 16, 31, // 5
14, 17, 17, 30, 16, 14, // 6
 4,  4,  4,  2,  1, 31, // 7
14, 17, 17, 14, 17, 14, // 8
14,  1, 15, 17, 17, 14, // 9
12, 12,  0,  0,  0,  0, // .
 0,  0, 31,  0,  0,  0, // -
 4,  4, 31,  4,  4,  0);// +

vec2 uvmap(vec2 uv){
    return (2.*uv-iResolution.xy)/iResolution.y;
}

//print(uv, center, width, height, index)
float print(vec2 uv, vec2 c, float w, float h, int ind)
{
    float r = 0.;
    vec2 wh = vec2(w/float(columns),h/float(rows));
    vec2 o =  vec2(w,h)/2.; // origin
    vec2 bmin=c-o, bmax=c+o; // min and max boundaries

    // no need to render ouside boundaries.
    if(uv.x<bmin.x||uv.y<bmin.y|| 
       uv.x>bmax.x||uv.y>bmax.y) return 0.;

    c = bmin; // move center to the origin
    for(int row=0;row<rows;row++){
        int map = table[ind*tableCol+row];
        for(int col=0;col<columns;col++){
            vec2 p0 = c+wh*vec2(col,row), p1 = p0+wh;
            vec2 lb = step(p0,uv);
            vec2 ub = 1.-step(p1,uv);

            int shift = columns-col-1;
            if(((map&(1<<shift))>>shift)==1) 
                r += lb.x*lb.y*ub.x*ub.y;
        }
    }
    return r;
}

float printNumber(vec2 uv, vec2 c, float w, float h, float num, int decN, float spacing, bool center)
{
    w*=iResolution.y/iResolution.x; // keep aspect ratio
    spacing*=iResolution.y/iResolution.x;

    float r = 0.;
    bool neg = num<0.; num=abs(num);
    float cd = float(decN);
    float cn = max(ceil(log(num)/log(10.)),1.);
    float dec = fract(num)*pow(10., 6.0);

    if(center) // move to origin
    {
        c.x += (cn+cd-1.)*(w+spacing)/2.;
        if(decN>0) c.x+=spacing;
        if(neg)c.x+=(w+spacing)/2.;
    }
    else
    {
        c.x += (cn+cd-1.)*(w+spacing)+w;        
        if(decN>0) c.x+=spacing*2.;
        if(neg)c.x+=w+spacing;
    }

    // print fractions
    for(int i=0;i<decN;i++,dec/=10.){
        int val = int(mod(dec,10.));
        r += print(uv, c, w, h, val);
        c.x-=w+spacing; // move left
    }
    if(decN>0) // print dot separator
    {
        c.x+=spacing;
        r += print(uv, c, w, h, 10);
        c.x-=w;
    }
    // print whole part
    for(int i=0;i<int(cn);i++,num/=10.){
        int val = int(mod(num,10.));
        r += print(uv, c, w, h, val);
        c.x-=w+spacing; // move left
    }
    if(neg) r+=print(uv, c, w, h, 11); // print sign
    return r;
}

float grid(vec2 p)
{
	vec2 uv = mod(p,1.0 / GRID_LINES);

	float halfScale = 1.0 / GRID_LINES / 2.0;

	float gridRad = (GRID_LINE_SIZE / iResolution.y) * ZOOM;
	float grid = halfScale - max(abs(uv.x - halfScale), abs(uv.y - halfScale));
	grid = smoothstep(0.0, gridRad, grid);

	float axisRad = (GRID_AXIS_SIZE / iResolution.y) * ZOOM;
	float axis = min(abs(p.x), abs(p.y));
	axis = smoothstep(axisRad - 0.05, axisRad, axis);

	return min(grid, axis);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = ((gl_FragCoord.xy - iMouse.xy) / iResolution.y) * ZOOM;

	vec3 col = vec3(grid(uv) * 0.45 + 0.55);

		PLOT(Func, Color, col, uv);

    vec2 xx = fragCoord.xy / iResolution.y +
		vec2((iResolution.y - iResolution.x) /
			(2.0 * iResolution.y), 0.0);

	if (any(lessThan(abs(xx - 0.5), vec2(0.002))) &&
		((distance(vec2(0.5), xx) < 0.02))) {
			col *= 0.4;
	}

    float px = ((iResolution.x / 2.0 - iMouse.x) / iResolution.y) * ZOOM;
    col -= printNumber(uvmap(fragCoord.xy), vec2(-0.5, -0.90), 0.1, 0.1, px, 6, 0.1, true);
    float py = ((iResolution.y / 2.0 - iMouse.y) / iResolution.y) * ZOOM;
    col -= printNumber(uvmap(fragCoord.xy), vec2(0.5, -0.90), 0.1, 0.1, py, 6, 0.1, true);

	fragColor = vec4(col, 1.0 );
}

//i made this shader because shane's shader looked so fancy
//https://www.shadertoy.com/view/4t3BW4
//
//Multi-Scale Truchet Patterns  - Christopher Carlson
//https://christophercarlson.com/portfolio/multi-scale-truchet-patterns/
//Linking paper containing more detail:
//http://archive.bridgesmathart.org/2018/bridges2018-39.pdf

//some macros
#define subs 0.4
#define limit 5.0
//#define grid

//some constant macros
#define HASHSCALE3 vec3(.6531, .5563, .7498)

//hash function in hash without sine by Dave_Hoskins
//https://www.shadertoy.com/view/4djSRW
//it seems to make a weird truchet pattern
float hash13(vec3 p3)
{
	p3  = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z*15.3023+0.434);
}

float checktree(float i, vec2 r) {
    for (float j = 0.0; j < i; j++) {
        vec2 h = floor(r*exp2(j))*exp2(-j);
        float rand = hash13(vec3(h,int(j)));
        if (rand >= subs) {
            return j;
        }
    }
    return i;
}

float truchet(vec2 p, int type) {
    float len;
    if (type == 0)
    {
        p = p+step(p.x+p.y,0.0)-0.5;
        len = abs(length(p)-0.5)-0.1667;
    }
    else if (type == 1)
    {
        len = -length(abs(p)-0.5)+0.3333;
    }
    else if (type == 2)
    {
        vec2 dir;
        if (abs(p.x) > abs(p.y)) {
            dir = vec2(sign(p.x),0.0);
        } else {
            dir = vec2(0.0,sign(p.y));
        }
        len = abs(length(p-dir*0.5))-0.1667;
    }
    else if (type == 3)
    {
        vec2 dir;
        if (abs(p.x) > abs(p.y)) {
            dir = vec2(sign(p.x),0.0);
        } else {
            dir = vec2(0.0,sign(p.y));
        }
        len = min(abs(length(p-0.5)-0.5),abs(length(p-dir*0.5)))-0.1667;
    }
    else if (type == 4)
    {
        p = abs(p)-0.1667;

        len = -length(min(p,0.0))+max(min(p.x,p.y),0.0);
    }
    else if (type == 5)
    {
        p.y = abs(p.y);

        len = min(p.y,length(p-vec2(0,0.5)))-0.1667;
    }
    else if (type == 6)
    {
        p.x = abs(p.x);
        len = min(max(-p.y-0.1667,-length(p-0.5)+0.3333),length(p+vec2(0,0.5))-0.1667);      
    }
    return len;
}

float quadtruchet(vec2 p) {
    float len = 0.0;

    float j = checktree(limit, p);
    float i = j;

    //the position in the bottom left corner of the truchet cell
    vec2 fp = floor(p*exp2(i))*exp2(-i);

    //the local position on the truchet cell (always 0-1)
    vec2 lp = (p-fp)*exp2(i);

    float rand = fract(hash13(vec3(fp,-i))*10.0);
    vec2 q = lp-0.5;

    int celltype = int(rand*7.0);
    int rots = int(fract(rand*7.0)*4.0);

    for (int i = 0; i < rots; i++) {
        q = vec2(q.y,-q.x);
    }

    float l = truchet(q,celltype);

    len = l*exp2(-i)*(1.0-mod(i,2.0)*2.0);
    //len = l*exp2(-i);

    for (i = i; i <= limit; i++) {

        //the position in the bottom left corner of the truchet cell
        fp = floor(p*exp2(i))*exp2(-i);

        //the local position on the truchet cell (always 0-1)
        lp = (p-fp)*exp2(i);

        //check for the overlapping black dots
        vec2 p2 = p*exp2(i);
        vec2 fp2 = floor(p2-0.5);
        for(int x = -1; x <= 2; x++) {
            for(int y = -1; y <= 2; y++) {
                vec2 r = (fp2+vec2(x,y))*exp2(-i);
                //this branch doesn't do anything, but it skips the random() once
                if (r != fp)
                {
                    j = checktree(i, r);

                    if (i==j) {
                        float l = length(abs(p2-fp2-vec2(x,y)-0.5)-0.5)-0.3333;
                        float s = mod(i,2.0)*2.0-1.0;
                        len = min(l*exp2(-i),len*s)*s;
                    }
                }
            }
        }
	}
    return len;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates (from -1 to 1)
    float scale = 1.0;
    vec2 uv = scale*(fragCoord*2.0-iResolution.xy)/iResolution.y;

    //"world space" coordinates, always moves up to the right.
    vec2 p = uv+iTime*0.1+vec2(-24,21);

    float len = quadtruchet(p);

    float a = exp2(checktree(limit,p));

    #ifdef grid
    vec2 lp = abs(fract(p*a)*2.0-1.0);
    float b = max(lp.x,lp.y);
    a /= iResolution.y*0.2;
    b = max(b-1.0+1.0*a,0.0)/a;
    #endif

    //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
    vec3 col = 1.0 - sign(len)*vec3(0.1,0.4,0.7);
	col *= min(1.0 - exp(-6.0*abs(len)),0.8);
	col *= 0.8 + 0.2*cos(200.0*abs(len));
	col = mix( col, vec3(1), 1.0-min(abs(len)*0.25*iResolution.y,1.0) );

    #ifdef grid
    col += b;
    #endif

    fragColor = vec4(col,1);

	//fragColor = vec4(vec3(sqrt(len*iResolution.y/scale*0.5)), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord - iResolution.xy / 2.0;
    vec2 offset = vec2(-0.5);

    float col = 0.0;
    float t = iTime / 1.5;

    float scale = pow(3.0, mod(t, 2.0) + 1.0);
    float size = iResolution.y * scale;
    float rot = t / 2.0;

    pos = mat2(cos(-rot), sin(-rot), -sin(-rot), cos(-rot)) * pos;
    pos += offset * iResolution.y * (scale * 0.5 - 0.5);

    while(size > 1.0) {
        size /= 3.0;
        ivec2 ip = ivec2(round(pos / size));

        if(ip.x == 0 && ip.y == 0) {
            col = min(size*size, 1.0);
            break;
        } else {
            pos -= vec2(ip) * size;
        }
    }

    // Output to screen
    fragColor = vec4(col, col, col, 1.0);
}

float circle(in vec2 _st, in float _radius){
    vec2 l = _st-vec2(0.5);
    return 1.-smoothstep(_radius-(_radius*0.2),
                         _radius+(_radius*0.2),
                         dot(l,l)*4.0);
}

