    float size = sin(d * 5.0 + float(iFrame) * 0.05) + 1.1;

    //scale and gridifiy
    uv *= 18.;
    uv = fract(uv);

    vec3 col = vec3(circle(uv, size));

    fragColor = vec4(col,1.0);
}

