//optimization to skip major parts of the set if zoomed out
bool opt1(vec2 c)
{
    float a=(c.x-0.25);
    float y2 = c.y*c.y;
    float p=sqrt(a*a+y2);
    float b = c.x+1.;
    return (c.x < (p-2.*p*p+0.25)) || b*b+y2 < (1./16.);
}
//pretty standard mandelbrot iteration, with early exit for antialiasing
float Mandelbrot(vec2 c, float minIter)
{
    if (opt1(c)) return MaxIter; //try for early exit
    vec2 z = c;
    float i;
    for (i=0.;i<MaxIter;i++){
        if( dot(z,z)>(4.) ) break; //escapes to infinity
        if (minIter < i) {
            i=MaxIter; //early antialiasing exit
            break;
        }
        z = vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y) + c; //iterate mandelbrot formula
    }

    #ifdef Smooth
   	float sl = i - log2(log2(dot(z,z))) + 4.0; //IQs smoothing formula
    return sl; //this gives some banding with current aa method, but looks kinda cool too
    #else
    return i;
    #endif
}
//2d rotation
vec2 rotate (vec2 v,float r)
{
    float s = sin(r);
    float c = cos(r);
    mat2 rot = mat2(c,-s,s,c);
    return v*rot;
}
