void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	float fft = texelFetch(iChannel0,ivec2(1,0),0).x;

    vec3 col = vec3(0);
    float t = iTime*0.1;

    #ifdef StillImage
    t=200.;
    #endif
    //iterate between various locations over time
    int lidx=int(mod(floor(t*.3),7.));
    vec2 location=getLocation(lidx);
    vec2 colUV;
    //minIter is used to find the lowest iteration sample when antialiasing
    float minIter = MaxIter;
    float mandelColor = 0.;
     //fudge some zoom factor together
    float zoom = pow(((sin(t)*0.5+0.5)*.25+.001),1.8);
    //antialiasing loop
    for (float y=0.;y<aa;y++)
        for (float x=0.;x<aa;x++)
        {
		    // Normalized pixel coordinates (from -2 to 2)
            vec2 uv = fragCoord/(iResolution.xy+vec2(x,y)*aa_delta)*4.-2.;
            uv.x*=AspectRatio;
            uv=rotate(uv,t)*zoom+location;    
            float sampleIter = Mandelbrot(uv, minIter);//fetch mandelbrot sample

            if (sampleIter < minIter)
            {
                //store the minimum value found in antialiasing steps
                //this gives cleaner image then supersampling and averaging
                minIter = sampleIter; //update the new minimum iteration count found
                colUV = uv; //store the captured "uv" coordinate used in calculation for color calculation later
            }
        }

    if (minIter < MaxIter) //prevent flickering inside set
		mandelColor = minIter/MaxIter;
    //simple music reaction
    //mandelColor*=(0.5+fft);
    //pick some nice colors	
 	col = (0.5 + 0.5*cos(mandelColor*64.+colUV.xyy+vec3(.1,.2,.3)))*pow(mandelColor,0.05);
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
#define PHI 1.61803398875

// time (uniform)
#define u_t iTime
// resolution (uniform)
#define u_res iResolution

float sinStep(float x, float w) {
    float y = (x+(1./w)*sin(w*x));
    return y;
}

float fibonacciHash(float x) {
    return mod((1./PHI)*x,1.);
}

float cosInterp(float x, float p0, float p1) {
    return p0+(p1-p0)*0.5*(1.-cos(PI*fract(x)));
}

float linInterp(float x, float p0, float p1) {
    return p0+(p1-p0)*fract(x);
}

float distanceDet(vec2 p) {
    float d0 = distance(vec2(-1.,1.), p.xy),
    	  d1 = distance(vec2(1.,1.), p.xy),
    	  d2 = distance(vec2(-1.,1.), p.xy),
          d3 = distance(vec2(-1.,-1.), p.xy);

    mat2 m0 = mat2(d0, d1,
                   d2, d3);

    return determinant(m0);
}

float fHashNoiseF(float x) {
    // integer part of coordinates
    float x_i = floor(x);

    // hash
    float x0 = fibonacciHash(x_i),
    	  x1 = fibonacciHash(x_i+1.);

    float fx = cosInterp(x, x0, x1);
    return fx;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (-1.+2.*(fragCoord/u_res.xy))*(u_res.xy/u_res.xx);

    float r = distance(vec2(0.0),uv.xy);
    float theta = atan(uv.y/uv.x);

    float x_co = fHashNoiseF(8.*(distanceDet(uv.xy-2.))+4.*sinStep(u_t/8.,PI));
    float y_co = fHashNoiseF(8.*(distanceDet(uv.yx-2.))+4.*sinStep(u_t/8.+0.5,PI));

    float sum = 0.;
    float a = 0.;
    vec3 col = vec3(0.);
    for(float i = 1.; i < 128.; i++) {
        a = exp2(-(i-1.)*0.5);
        sum += a;
        col += a*cos(i*PI*cos(PI*(2.*r-u_t/4.+0.5*(x_co+y_co)*(1.+exp2(-6.+(r*r))*vec3(0.,0.5,1.)))));
    }
    col = 0.5*(1.+col)/sum;

    // Output to screen
    fragColor = vec4(col,1.0);
}

