Shape Hallway(vec3 c){
  Shape shape;
    vec4 color = vec4(.65);
    vec3 r = c;
    vec3 t = c;
    vec3 g = c;
    vec3 cut = c;
    vec3 s = c;
    vec3 l = c;

    c.z = repeat(c.z, 2.);
    r.z = repeat(r.z, 1.5);
    t.z = repeat(t.z, 3.);
    g.z = repeat(g.z, 2.);
    cut.z = repeat(cut.z, 2.);
    s.z = repeat(s.z, 2.);
    l.z = repeat(l.z, 2.);

    //Columns, connecting bar, and floor
    c.x = abs(c.x)-.5;
    vec4 colColor = vec4(1.);
    float c1 = sdCylinder(c-vec3(1.4, .0, 0.), vec3(.1, 1.1, 0.3)); //Columns
    float b1 = fBox(c-vec3(1.5, .8, 0.), vec3(.3, 0.1, 1.)); //Connecting Bar
    float fl = fBox(c-vec3(0., -1., 0.), vec3(1.8, .05, 1.)); //Floor

    //Column cutouts
    cut.x = abs(cut.x)-.5;
    float c2 = sdCylinder(cut-vec3(1.4, 0., -.2), vec3(.1, 1., .1));

    //Chandelier Hangings
    g.x = abs(g.x*2.)-.5;
    g.y -= .3;
    g.xy *= rot(g.x+radians(75.));
    vec4 hangColor = vec4(0., 0., 1., 0.);
    float g1 = fBox(g-vec3(.3, -.5, 0.), vec3(.05, 1.5, .04));

    //Roof
    r.xy *= rot(-r.x*.25);
    vec4 roofC = vec4(1., 0., 0., 1.);
    float ro = fBox(r-vec3(0., 1.5, 0.), vec3(1.2, .1, .8)); 

    //Side Streamers
    s.x = abs(s.x)-.54;
    s.yz *= rot(-s.z+radians(90.));
    vec4 streamers = vec4(abs(sin(iTime)), 0., cos(iTime), 1.);
    float s1 = fBox(s-vec3(1.5, 0., .1), vec3(.05, 1.5, .04));

    //Lights
    l.x = abs(l.x)-.33;
    vec4 lColor = vec4(1., 1., 0., 1.);
    float light = sphere(l-vec3(0., .75, 0.), .1);

    shape.dist = fOpUnionChamfer(c1, b1, .1);
    shape.dist = fOpUnionChamfer(ro, shape.dist, .01);
    shape.dist = fOpUnionChamfer(shape.dist, fl, .2);
    shape.dist = fOpUnionChamfer(shape.dist, g1, .25);
    shape.dist = max(shape.dist, -c2);
    shape.dist = fOpUnionStairs(shape.dist, s1, .2, .2);
    shape.dist = fOpUnionRound(shape.dist, light, .05);

    shape.color = color;
    shape.color = mix(colColor, roofC, mixColors(ro, c1, .01));
    shape.color = mix(hangColor, shape.color, mix(shape.dist, g1, .6));
    shape.color = mix(streamers, shape.color, mix(shape.dist, s1, .99));
    shape.color = mix(lColor, shape.color, mix(shape.dist, light, .999999999));

  return shape;
}


Shape map(vec3 c){
  Shape hall = Hallway(c);
  return hall;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., iTime);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.0001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;

  }
}

