void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;


  vec3 cam = vec3(0., 0., iTime);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); 
    if(c.dist < 0.001){
      fragColor = c.color*(1. - z); 
      break;
    }
    scene += f * c.dist;


  }
}

