{
	kf frame = evaluateFrame();

    vec3 cSum = vec3(0.0, 0.0, 0.0);

    for(int i = 0; i < AA; ++i)
    {
        for(int j = 0; j < AA; ++j)
        {
            vec2 o = vec2(float(i),float(j)) / float(AA) - 0.5;
            vec2 p = (-iResolution.xy + 2.0 * (fragCoord+o)) / iResolution.y;
            vec3 ro = frame.pos;
            float cr = frame.rotation;
            vec3 cw = normalize(frame.dir);
            vec3 cp = vec3(sin(cr), cos(cr), 0.0);
            vec3 cu = normalize(cross(cw ,cp));
            vec3 cv = normalize(cross(cu, cw));
            mat3 ca = mat3(cu, cv, cw);
            vec3 rd = ca * normalize(vec3(p.xy, 2.0 * frame.fov));

            cSum += render(ro, rd, p, frame);
        }
    }

    cSum /= float(AA*AA);

    #ifndef OVERRIDE_FRAME
    cSum.rgb *= frame.fade;
    #endif

    cSum = pow(cSum, vec3(0.4545));
    cSum = cSum * 1.2 - 0.1;

    vec2 uv = fragCoord.xy / iResolution.xy;
	uv *=  1.0 - uv.yx;

    cSum *= clamp(0.0, 1.0, pow(uv.x*uv.y * 5.0, 0.1));

    float n = texture(iChannel0, fragCoord.xy/iChannelResolution[0].xy).r;
    cSum.rgb += mix(-3.0 / 255.0, 3.0 / 255.0, n);

    fragColor = vec4(cSum, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*4.0;

    float fft=texture(iChannel0,vec2(440.0/11025.0,0.6)).r;
            +texture(iChannel0,vec2(200.0/11025.0,0.2)).r;
            +texture(iChannel0,vec2(125.0/11025.0,1)).r;
    		+texture(iChannel0,vec2(1000.0/11025.0,1)).r;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*tan(iTime+fft+uv.yxy+vec3(0,4,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define Pi     3.14159265359
#define Tau    6.28318530718
#define Rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

#define RadiusIncrease 0.08



float TriangleSd(vec2 uv, float radius)
{
	return max(0.866025*abs(uv.x) + 0.5*uv.y, -uv.y) - 0.5*radius;
}

float Triangle(vec2 uv)
{
    uv = Rot(-0.3*iTime) * uv;
    return smoothstep(0.782, 1.0, 1.0-TriangleSd(uv, 0.5 + 0.1*cos(iTime)));
}

float SpiralTurn(vec2 uv, float turn)
{
    // Polar coordinates:
    float r = length(uv);
    float a = Pi+atan(uv.y, uv.x);

    // Turn angle:
    a += Tau*turn;

    // Polar function:
    float tr = Triangle(uv);
    float am = 0.35*tr*smoothstep(0.0, 0.1, r);
    float p = RadiusIncrease*(a/Tau + am*sin(-4.0*iTime+0.54*a*a));

    return r-p;
}


float SpiralTurnDe(vec2 uv, float turn)
{
    // IQ's |f|/|Grad(f)| distance estimator:
    float f = SpiralTurn(uv, turn);
    vec2 eps = vec2(0.00005, 0);
    vec2 grad = vec2(
        SpiralTurn(uv + eps.xy, turn) - SpiralTurn(uv - eps.xy, turn),
        SpiralTurn(uv + eps.yx, turn) - SpiralTurn(uv - eps.yx, turn)) / (2.0*eps.x);

    return f/length(grad);
}


float Spiral(vec2 uv, float eps)
{
    // Modulation source:
    float tr = Triangle(uv);

    // Split in concentric rings:
    float r = length(uv);
    float turn = floor(r/RadiusIncrease);

    // Draw a spiral contained in a ring + adjacent ones to handle overlap:
    float thick = 0.0040*tr;
    float aa = 1.2*eps;
    float d;
    d  = smoothstep(thick, thick+aa, abs(SpiralTurnDe(uv, turn)));
    d *= smoothstep(thick, thick+aa, abs(SpiralTurnDe(uv, turn+1.0)));
    d *= smoothstep(thick, thick+aa, abs(SpiralTurnDe(uv, turn-1.0)));
    return d;
}


// Randomly dither colors for smoother gradients
void Dither(inout vec4 fragColor, in vec2 fragCoord)
{
    // Position + Time Hash (based on Dave Hoskins hash33):
    vec3 p3 = fract(vec3(fragCoord, iFrame) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    vec3 hash = fract((p3.xxy + p3.yxx)*p3.zyx);

    // RGB Dithering:
    float softness = 255.0; // Should be 255. Lower value for grainy/fuzzy effect.
    fragColor.rgb = (floor(softness*fragColor.rgb) + step(hash, fract(softness*fragColor.rgb)))/softness;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized coordinates:
    float eps = 2.0/iResolution.y;
    vec2  uv =  eps*(fragCoord - 0.5*iResolution.xy);
    uv.x = -uv.x;

    //uv *= Rot(-0.15*iTime);

    // Polar:
    float r = length(uv);
    //float a = atan(uv.y, uv.x);


    // Back:
    vec3 back = vec3(0.20, 0.25, 0.3);
    // - Add vignetting:
    back *= mix(0.237, 1.0, pow(smoothstep(2.0, 2.0*0.45, r), 4.0*0.1637));


    // Disk:
    vec3 disk = vec3(0.904, 0.902, 0.827);

    // - Add Grid:
    float grid = mod(floor(r/RadiusIncrease), 2.0);
    disk = mix(disk, vec3(0.836,0.848,0.840), grid);

    // - Add inner shadow:
    disk *= mix(0.023, 1.0, pow(smoothstep(0.9, 0.75, r), 0.4));

    // - Add spiral:
    disk = mix(0.2*disk, disk, pow(Spiral(uv, eps), 0.45));


    // Combine Back and Disk:
    float mask = smoothstep(0.9+eps, 0.9, r);
    vec3 col = mix(back, disk, mask);


    // Output:
    fragColor = vec4(col,1.0);


    // Dither post-process:
    Dither(fragColor, fragCoord);
}

