// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec4 mat )
{

    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  lig = normalize(vec3(0.7,0.875,0.89));		// dir
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0;
    if( dif>0.01 )
        sha=calcSoftshadow( pos+0.01*nor, lig );
    lin += dif*vec3(4.00,4.00,4.00)*sha;

	float spec = pow(dif, 160.0) *mat.a;


    // ambient light
    //-----------------------------
    lin += vec3(0.50,0.50,0.50);


    // surface-light interacion
    //-----------------------------
    vec3 col = mat.xyz*lin;
    col+=spec;


    // fog    
    //-----------------------------
	col *= exp(-0.001*dis*dis);

    return col;
}

vec2 calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 50.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	//float res = -1.0;
    vec2 res = vec2(-1.0,0.0);
    float c = 0.0;

    for( int i=0; i<120; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
        vec2 res2 = doModel( ro+rd*t );
	    h = res2.x;
        c = res2.y;

        t += h*0.75;		// hack * for extreme bend
    }

    if( t<maxd )
    {
        res.x = t;
        res.y = c;
    }
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;


    vec2 uv3 = (fragCoord/iResolution.xy)-vec2(0.5,0.5);
	float distSqr = dot(uv3, uv3)+0.5;
	float vignette = 1.4 - distSqr;


    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------
  	vec3 col = mix( vec3(0.0,0.24, 0.0), vec3(0.1, 0.05, 0.3), fragCoord.y / iResolution.y );

	// raymarch
    vec2 res = calcIntersection( ro, rd ); 
    float t = res.x;
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec4 mat = doMaterial( pos, nor, res.y );

        col = doLighting( pos, nor, rd, t, mat );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
    col *= vignette;
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}

// Joint work with Dave Bachman. 
// In this test we count how many fund domain hops we need to do to get close to the origin
//in the Poincare ball, and color the sphere accordingly

vec4 c2q(in vec2 a){
    return vec4(a.x, a.y, 0.0, 0.0);
}

vec4 qonj(in vec4 q){
    return vec4(q.x, -q.y, -q.z, -q.w);
}

vec4 qinv(in vec4 q){
 	return qonj(q) / (q.x*q.x + q.y*q.y + q.z*q.z + q.w*q.w);
}

vec4 qmul(in vec4 p, in vec4 q){
    return vec4(p.x*q.x - p.y*q.y - p.z*q.z - p.w*q.w,
                p.x*q.y + p.y*q.x + p.z*q.w - p.w*q.z,
                p.x*q.z - p.y*q.w + p.z*q.x + p.w*q.y,
                p.x*q.w + p.y*q.z - p.z*q.y + p.w*q.x);
}

vec4 qdiv(in vec4 p, in vec4 q){
    return qmul(p, qinv(q));
}

vec4 qmob(in vec2[4] M, in vec4 z){ // see Ahlfors 1981 Mob tsfms p14
    vec4 a = c2q(M[0]);
    vec4 b = c2q(M[1]);
    vec4 c = c2q(M[2]);
    vec4 d = c2q(M[3]);
    return qdiv( qmul(a,z)+b, qmul(c,z)+d ); // if z.w = 0 then result.w = 0
}


const float sqrt3 = sqrt(3.0);
const vec2 w = vec2(0.5, 0.5*sqrt3);
const vec2 winv = vec2(0.5, -0.5*sqrt3);
const vec2 c0 = vec2(0.0,0.0);
const vec2 c1 = vec2(1.0,0.0);
const vec2 ci = vec2(0.0,1.0);
const vec4 qj = vec4(0.0,0.0,1.0,0.0);

float origin_distance(in vec4 q){
    //vec4 x  = qdiv(q-qj,c2q(c1)-qmul(q,qj));
    vec4 x = qmul(qinv(qmul(q,qj)-c2q(c1)),qj-q);
    return length(x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 t1[4];
	t1[0] = c1;
	t1[1] = -w;
	t1[2] = c1;
	t1[3] = winv;  // is this really the only way to assign values to the array??
    vec2 t1inv[4];
    t1inv[0] = winv;
    t1inv[1] = w;
    t1inv[2] = -c1;
    t1inv[3] = c1;
    vec2 t2[4];
    t2[0] = c1;
    t2[1] = w;
    t2[2] = c0;
    t2[3] = c1;
    vec2 t2inv[4];
    t2inv[0] = c1;
    t2inv[1] = -w;
    t2inv[2] = -c0;
    t2inv[3] = c1;
    vec2 t3[4];
    t3[0] = -winv;
    t3[1] = -w;
    t3[2] = w;
    t3[3] = -w - c1;
    vec2 t3inv[4];
    t3inv[0] = -w - c1;
    t3inv[1] = w;
    t3inv[2] = -w;
    t3inv[3] = -winv; //all these should have det 1 now

    vec2 p = vec2(0.1,0.001) + 1.0*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
    //vec4 q = vec4(p, 0.001+0.1*(0.75 + cos(iTime) + 0.25*cos(2.0*iTime)), 0.0);
	//vec4 q = vec4(0.0,1.0, 0.0001, 0.0);

    float ball_radius = 0.9999;

    vec4 q;
    vec3 col;

    if(length(p)<1.0){
		vec4 ball_pt = vec4(sqrt(1.0-length(p)*length(p)),p,0.0);
        ball_pt = vec4(ball_pt.x * cos(iTime) + ball_pt.y * sin(iTime), -ball_pt.x * sin(iTime) + ball_pt.y * cos(iTime), ball_pt.z, ball_pt.w);
        //ball_pt = (0.999-0.15*cos(iTime))*ball_pt;
        ball_pt = 0.999*ball_pt;
      	q = qdiv(ball_pt + qj,qmul(qj,ball_pt)+c2q(c1));
        int crossing_count = 0;
	    for(int i=0;i<512;i++){
    	    vec4 q1=qmob(t1,q);
        	float d1=origin_distance(q1);

	        vec4 q2=qmob(t2,q);
	        float d2=origin_distance(q2);

	        vec4 q3=qmob(t3,q);
    	    float d3=origin_distance(q3);

        	vec4 q1inv=qmob(t1inv,q);
        	float d1inv=origin_distance(q1inv);

	        vec4 q2inv=qmob(t2inv,q);
    	    float d2inv=origin_distance(q2inv);

	        vec4 q3inv=qmob(t3inv,q);
    	   	float d3inv = origin_distance(q3inv);

	        float dbest=d1;
    	    vec4 qbest=q1;
        	int crossing_change = 0;

	        if (d2 < dbest){
    	    	dbest = d2;
        	    qbest=q2;
            	crossing_change=-1;
	        }

    	    if (d3 < dbest){
        		dbest = d3;
            	qbest=q3;
      	      crossing_change=-1;
        	}

	        if (d1inv < dbest){
    	    	dbest = d1inv;
        	    qbest=q1inv;
            	crossing_change=0;
	        }

    	    if (d2inv < dbest){
        		dbest = d2inv;
            	qbest=q2inv;
           	 	crossing_change=1;
	        }

	        if (d3inv < dbest){
    	    	dbest = d3inv;
        	    qbest=q3inv;
            	crossing_change=1;
	        }

     	   if (dbest < origin_distance(q)){
        	    q=qbest;
            	crossing_count=crossing_count+crossing_change;
	        }
    	    else break;
	}                
    float c = 0.5+float(crossing_count)/10.0;
    col = vec3(c,c,c);
    }

    else{
        col = vec3(0.0,0.0,0.0);
    }

    fragColor = vec4(col,1.0);
}

