//Randomizing vectors, parametizes a vec2 to return a float
float random(vec2 v) {
    return fract(sin(dot(v*1., vec2(324.654, 156.546)))*46556.2);
}
//Rotating shape using matrix transformation
mat2 rot(float a) {
    float r = cos(a);
    float f = sin(a);
    return mat2(r, f, -f, r);
}
//Produces a cylinder, 
float fCylinder(vec3 p, float r, float h) {
    float d = length(p.xz) - r;
    d = max(d, abs(p.y) - h);
    return d;
}
//Produces a sphere
float sphere(vec3 v, float r) {
    return length(v) - r;
}
float vmax(vec3 v) {
    return max(max(v.x, v.y), v.z);
}
//Produces a box
float fBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}
//Mixing colors, where the 3rd parameter is the mixing ratio 
float mixColors(float r, float v, float z) {
    return clamp(.5 + .5*(v - r)/z, 0., 1.);
}
//Mixing shapes, or blending the pixels projected onto the screen
float mixShapes(float v, float f, float r) {
    float z = mixColors(v, f, r);
    return mix(f, v, z) - r*z*(1. - z);
}
//Produces a cone(circular base)
float fCone(vec3 p, float radius, float height) {
	vec2 q = vec2(length(p.xz), p.y);
	vec2 tip = q - vec2(0, height);
	vec2 mantleDir = normalize(vec2(height, radius));
	float mantle = dot(tip, mantleDir);
	float d = max(mantle, -q.y);
	float projected = dot(tip, vec2(mantleDir.y, -mantleDir.x));

	// distance to tip
	if((q.y > height) && (projected < 0.)) {
		d = max(d, length(tip));
	}

	// distance to base ring
	if ((q.x > radius) && (projected > length(vec2(height, radius)))) {
		d = max(d, length(q - vec2(radius, 0)));
	}
	return d;
}
float sdEllipsoid(in vec3 p, in vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0 - 1.0)/k1;
}

