//Changing coordinate system to polar coordinates, making it easy to stretch shapes
float pModPolar(inout vec2 v, float r) {
    float f = 6.28318/r;
    float z = atan(v.y, v.x) + f*.5;
    float m = floor(z/f);
    z = mod(z, f) - f*.5;
    v = vec2(cos(z), sin(z))*length(v);
    return m;
}
void pR45(inout vec2 p) {
    p = (p + vec2(p.y, -p.x))*sqrt(.5);
}
//Modifying the coordinate system
float pMod1(inout float p, float size) {
    float halfSize = size*.5;
    float c = floor((p + halfSize)/size);
    p = mod(p + halfSize, size) - halfSize;
    return c;
}
//Similar to smooth step function, removing edges of shapes
float fOpUnionRound(float a, float b, float r) {
	vec2 u = max(vec2(r - a,r - b), vec2(0));
	return max(r, min (a, b)) - length(u);
}
float fOpIntersectionRound(float a, float b, float r) {
	vec2 u = max(vec2(r + a,r + b), vec2(0));
	return min(-r, max (a, b)) + length(u);
}
float fOpDifferenceRound (float a, float b, float r) {
	return fOpIntersectionRound(a, -b, r);
}
//Rotating shapes on Y axis
vec3 rotateY(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    return vec3(ca*p.x + sa*p.z, p.y, -sa*p.x + ca*p.z);
}
//Blending shapes, merging them together while creating rippples
float fOpUnionColumns(float a, float b, float r, float n) {
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.x -= sqrt(2.)/2.*r;
        p.x += columnradius*sqrt(2.);
        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = length(p) - columnradius;
        result = min(result, p.x);
        result = min(result, a);
        return min(result, b);
    } else {
        return min(a, b);
    }
}
float sdTorus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}
float sdCappedCylinder(vec3 p, vec2 h) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float fOpDifferenceColumns(float a, float b, float r, float n) {
    a = -a;
    float m = min(a, b);
    if((a < r) && (b < r)) {
        vec2 p = vec2(a, b);
        float columnradius = r*sqrt(2.)/n/2.;
        columnradius = r*sqrt(2.)/((n - 1.)*2. + sqrt(2.));
        pR45(p);
        p.y += columnradius;
        p.x -= sqrt(2.)/2.*r;
        p.x += -columnradius*sqrt(2.)/2.;

        if(mod(n, 2.) == 1.) {
            p.y += columnradius;
        }
        pMod1(p.y, columnradius*2.);
        float result = -length(p) + columnradius;
        result = max(result, p.x);
        result = min(result, a);
        return -min(result, b);
    } else {
        return -m;
    }
}
//Projecting shapes
Shape character(vec3 c) {
    Shape shape;
    shape.dist = 1000.;
    shape.color = vec4(1.);
    //instiating vars
   	vec3 h = c; //head 
    vec3 be = c; //beak
    vec3 e = c; //eyes 
    vec3 p = c; //pupil
    vec3 br = c; //brows
    vec3 b = c; //body 
    vec3 f = c; //feathers/halo
    vec3 arone = c; //arms
    vec3 artwo = c;
    vec3 pro = c; //propeller 
    vec3 m = c;

    vec4 hColor = vec4(1., 1., 1., 1.);
    h.x *= cos(sin(h.x*3.))*.6;
    float head = sphere(h - vec3(0., 1., 0.), 1.);
    m.xy *= cos(sin(m.x*3.));
    float mouth = fBox(m - vec3(0., .6, -1.5), vec3(.1, .1, .1));
    be.xy *= rot(radians(270.));
    vec4 beColor = vec4(.9, 0., 0., .5);
    float beak = fCone(be - vec3(-.85, .8, -.4), .25, .5);
    p.x = abs(p.x) - .45;
    float pupil = sphere(p - vec3(0., .95, -1.25), .1);
    e.x = abs(e.x) - .085;
    float eyes = sphere(e - vec3(.4, 1., -1.), .15);
   	//float eyes = sphere(e - vec3(.4, 1., -1.), .2);
    //float pupil = sphere(p - vec3(.5, 1., -1.25), .07);
    br.xy *= rot(radians(350.));
    //br.x +=  sin(br.x*5. - iTime*1.5)*(2. - br.y)*.3;
    vec4 brColor = vec4(0., 0., 0., 1.);
    float brow = fBox(br - vec3(.3, 1.3, -1.), vec3(.25, .07, .25));
    arone.xy *= rot(radians(80.));
    vec4 aroneColor = vec4(0., 0., 0., 1.);
	float armone = sdEllipsoid(arone - vec3(-.5, .75, -1.), vec3(.5, .25, .5));
    artwo.xy *= rot(radians(100.));
    vec4 artwoColor = vec4(0., 0., 0., 1.);
    float armtwo = sdEllipsoid(artwo - vec3(-.5, -.75, -1.), vec3(.5, .25, .5));
    vec4 bColor = vec4(1., 1., .9, 1.);
    float body = sphere(b - vec3(0., -.5, 0.), 1.25);
	pro.x += sin(pro.x*15. - iTime*2.)*(2. - pro.y)*.03;
    pro.xy *= rot(radians(180.));
    vec4 proColor = vec4(1., 0., 0., .9);
    float propeller = fCone(pro - vec3(0., 1.6, 0.), .5, 1.);
    //Create effects of an Emperor penguin, crown on top of head
    //f.x = abs(f.x)-.1; 
  	//f.x = abs(f.x)-0.1; 
  	//f.x += sin(f.y*15. - iTime * 2.)*(2. - f.y)*.03; 
  	//float feathers = fCone(f + vec3(0., -1.25, 2.5), .14, .3); 
    f.xy *= rot(radians(5.*sin(iTime)));
    vec4 haColor = vec4(1., 1., 0., 1.);
    float halo = sdTorus(f - vec3(0., 2.25, 0.), vec2(.7, .1));
    shape.dist = head;
    //shape.dist = fOpUnionRound(shape.dist, beak, .15);
    shape.dist = min(shape.dist, eyes);
    shape.dist = min(shape.dist, pupil);
    //shape.dist = min(shape.dist, brow);
    shape.dist = fOpUnionRound(shape.dist, body, .1);
    //shape.dist = fOpUnionRound(shape.dist, feathers, .1);
    shape.dist = min(shape.dist, halo);
    shape.dist = min(shape.dist, armone);
    shape.dist = min(shape.dist, armtwo);
    shape.dist = min(shape.dist, propeller);
    shape.dist = min(shape.dist, mouth);


    shape.color = mix(bColor, hColor, mixColors(head, body, 0.));
    //shape.color = mix(shape.color, brColor, mixColors(brow, shape.dist, .1));
    shape.color = mix(shape.color, haColor, mixColors(halo, shape.dist, .1));
    //shape.color = mix(shape.color, beColor, mixColors(beak, shape.dist, .1));
    shape.color = mix(shape.color, aroneColor*10., mixColors(armone, shape.dist, .25));
    shape.color = mix(shape.color, artwoColor*10., mixColors(armtwo, shape.dist, .25));
    shape.color = mix(shape.color, proColor*10., mixColors(propeller, shape.dist, .1));
    return shape;
}

Shape map(vec3 c){
  Shape shape = character(c);
