	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float fOpUnionColumns(float a, float b, float r, float n) {
	if ((a < r) && (b < r)) {
		vec2 p = vec2(a, b);
		float columnradius = r*sqrt(2.)/((n-1.)*2.+sqrt(2.));
		pR45(p);
		p.x -= sqrt(2.)/2.*r;
		p.x += columnradius*sqrt(2.);
		if (mod(n,2.) == 1.) {
			p.y += columnradius;
		}
		// At this point, we have turned 45 degrees and moved at a point on the
		// diagonal that we want to place the columns on.
		// Now, repeat the domain along this direction and place a circle.
		pMod1(p.y, columnradius*2.);
		float result = length(p) - columnradius;
		result = min(result, p.x);
		result = min(result, a);
		return min(result, b);
	} else {
		return min(a, b);
	}
}

float fOpUnionStairs(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2.* s)) - s)));
}
float fOpDifferenceStairs(float a, float b, float r, float n) {
	return -fOpUnionStairs(-a, b, r, n);
}

//=======================

Shape environment(vec3 c){
  Shape shape;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Coordinate Systems
  vec3 g = c; // Ground
  vec3 l = c; // light
  vec3 ga = c; //Gates
  vec3 s = c; // Side
  vec3 t = c; //torch
  vec3 d = c; //door

  // Ground
  vec4 gColor = vec4(1., 0.5, 0.5, 1.0);//purple
  g.xy *= rot(radians(90.)); //rotate
  g.y = abs(g.y) - 4.; //mirror
  g.x = abs(g.x) - 4.5;//mirror

  float ground = fBox(g+vec3(2.,0.,2.), vec3(0.25, 3.25, 2000000.75));


  // light
  vec4 lColor = vec4(0.,0.,0.,1.);//black
  l.xy *= rot(radians(90.)); //rotate
  l.zy *= rot(radians(90.)); //rotate
  l.z = abs(l.x) - 5. ;//mirror
  pMod1(l.y, 2.);//distance



  float light = fCylinder(l+vec3(1.6,0.9,2.1), 1.,1.); 

  // Gates
  vec4 gaColor = vec4(0.1, 0.1, 0.1, 1.); //grey 
  ga.z = abs(ga.z) + 5.; //mirror
  ga.x = abs(ga.x) - cos(iTime)*2.8; //mirror
  pMod1(ga.z, 8.);//distance
  ga.xy *= rot(radians(90.));//rotate
  float gates = fBox(ga+vec3(0.,3.,1.5), vec3(2., 1., 0.)); 
  gates = fOpUnionStairs(ground, gates, 1., 4.);

  float b1 = fBox(c-vec3(1.,0.,0.), vec3(0.5, 0.1, 0.7));


  // Side 
  vec4 sColor = vec4(0., .0, .5, 1.0); //blue
  s.x = abs(s.x) - 6.; //mirror
  pMod1(s.z, 4.5); //distance
  float side = fBox(s+vec3(3.,1., -5.), vec3(.2, 1.5, 7.)); 
  side = fOpUnionColumns(ground, side,1., 4.); 

  //Torch
  vec4 tColor = vec4(1.,0.,0.,1.); //red
  t.y *= cos(sin(t.y*7.)*1.23);//stretch  
  //t.z = abs(t.z);//mirror
  t.x = abs(t.x)-2.8;//mirror
  pMod1(t.z, 3.);//distance
  float torch = sphere(t-vec3(0.,0.1,0.), .1);

  //door
  s.x = abs(s.x) - 5.; //mirror
  pMod1(d.z, 5.); //distance
  float door = fBox(s+vec3(2.,1., 0.), vec3(.5, 0.25, 0.3));


  shape.dist = min(ground, gates);
  shape.dist = min(shape.dist, side); 
  shape.dist = min(shape.dist, torch);
  shape.dist = fOpDifferenceStairs(shape.dist, door, .3,2.);  

  shape.color = mix(shape.color, lColor, mixColors(light, shape.dist, 0.1));
  shape.color = mix(shape.color, gColor, mixColors(ground, shape.dist, 0.1));
  shape.color = mix(shape.color, gaColor, mixColors(gates, shape.dist, 0.5));
  shape.color = mix(shape.color, sColor, mixColors(side, shape.dist, 0.3)); 
  shape.color = mix(shape.color, tColor, mixColors(torch, shape.dist, 1.)); 

  return shape; 
}


Shape map(vec3 c){
  Shape enviro = environment(c);
  return enviro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;

  vec3 cam = vec3(0., 0., iTime*3.);
  cam.xy *= rot(radians(90.));
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.0001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}

#define MAX_STEPS 64
#define EPSILON .001

#define M_PI 3.14159265358979

const vec3 LightSource = vec3(3.,20.5, 3.);

vec3 WaterColor = vec3(0.4, 0.9, 1);

const float WaterHeight = 0.;
const float MaxWaveAmplitude = 0.03;

const float HeightPool = 1.;
const float HalfSizePool = 3.;
const float DepthPool = 3.0;

struct MaterialInfo {
	vec3 Kd;
	float Shininess;
};

float CyclicTime()
{
	return mod(iTime, 30.);
}

float WaveAmplitude() {
	return MaxWaveAmplitude * exp(-CyclicTime() / 10.);
}

float WaterWave(vec3 a) {
    float wave = 0.0;
    vec3 org = a;
    float amp = WaveAmplitude();
    wave += amp * min(pow(distance(org, vec3(0.0)), -1.), 2.0) * sin((2. * a.x * a.x + 2. * a.z * a.z) - 15. * CyclicTime());
	return wave;
}

float BallOscillation() {
	return sin(5. * CyclicTime() + 4.) * exp(-CyclicTime() / 6.) + 0.3;
}

float PoolBottom(vec3 a) {
	return a.y + DepthPool + .01;
}

float BackWall(vec3 a) {
	return a.z + HalfSizePool + .01;
}

float LeftWall(vec3 a) {
	return a.x + HalfSizePool + .01;
}

float WaterSurface2(vec3 a) {
	vec3 sz = vec3(HalfSizePool, 0, HalfSizePool);
	return length(max(abs(a + vec3(0, 0.0, 0)) - sz, 0.));
}

float WaterSurface(vec3 a) {
	vec3 sz = vec3(HalfSizePool, 0, HalfSizePool);
	return length(max(abs(a + vec3(0, WaterWave(a), 0)) - sz, 0.));
}

float Pool(vec3 a) {
	return min(PoolBottom(a), min(LeftWall(a), BackWall(a)));
}

float Pool2(vec3 a) {
	return min(PoolBottom(a), min(LeftWall(a), BackWall(a)));
}

float Ball(vec3 a) {
	return length(a + vec3(0., BallOscillation(), 0.)) - 0.75;
}

float Scene(vec3 a) {
	return min(WaterSurface(a), min(Ball(a), Pool(a)));
}

bool IsWaterSurface(vec3 a)
{
	float closest = Ball(a);
	float dist = Pool(a);
	if (dist < closest) {
		closest = dist;
	}	
	dist = WaterSurface(a);
	if (dist < closest) {
		return true;
	}
	return false;
}

bool IsWater(vec3 pos)
{
	return (pos.y < (WaterHeight - MaxWaveAmplitude));
}

bool IsPool(vec3 pos)
{
    return Pool(pos) < 0.01;
}

bool IsBall(vec3 pos)
{
    return Ball(pos) < 0.01;
}

vec3 PoolColor(vec3 pos) {		
	if ((pos.y > HeightPool) || (pos.x > HalfSizePool) || (pos.z > HalfSizePool)) 
		return vec3(0.0);
	float tileSize = 0.2;
	float thickness = 0.015;
	vec3 thick = mod(pos, tileSize);
	if ((thick.x > 0.) && (thick.x < thickness) || (thick.y > 0.) && (thick.y < thickness) || (thick.z > 0.) && (thick.z < thickness))
		return vec3(1);
	return vec3(sin(floor((pos.x + 1.) / tileSize)) * cos(floor((pos.y + 1.) / tileSize)) * sin(floor((pos.z + 1.) / tileSize)) + 3.);
}

MaterialInfo Material(vec3 a) {
	MaterialInfo m = MaterialInfo(vec3(.5, .56, 1.), 50.);
	float closest = Ball(a);

	float dist = WaterSurface(a);
	if (dist < closest) {
		closest = dist;
		m.Kd = WaterColor;
		m.Shininess = 120.;
	}
	dist = Pool(a);
	if (dist < closest) {
		m.Kd = PoolColor(a);		
		m.Shininess = 0.;
	}
	return m;
}

vec3 Normal(vec3 a) {
	vec2 e = vec2(.001, 0.);
	float s = Scene(a);
	return normalize(vec3(
		Scene(a+e.xyy) - s,
		Scene(a+e.yxy) - s,
		Scene(a+e.yyx) - s));
}

float Occlusion(vec3 at, vec3 normal) {
	float b = 0.;
	for (int i = 1; i <= 4; ++i) {
		float L = .06 * float(i);
		float d = Scene(at + normal * L);		
		b += max(0., L - d);
	}
	return min(b, 1.);
}

vec3 LookAt(vec3 pos, vec3 at, vec3 rDir) {
	vec3 f = normalize(at - pos);
	vec3 r = cross(f, vec3(0., 1., 0.));
	vec3 u = cross(r, f);
	return mat3(r, u, -f) * rDir;
}

float Trace(vec3 rPos, vec3 rDir, float distMin) {
	float L = distMin;
	for (int i = 0; i < MAX_STEPS; ++i) {
		float d = Scene(rPos + rDir * L);
		L += d;
		if (d < EPSILON * L) break;
	}
	return L;
}

vec3 Lighting(vec3 at, vec3 normal, vec3 eye, MaterialInfo m, vec3 lColor, vec3 lPos) {
	vec3 lDir = lPos - at;

	vec3 lDirN = normalize(lDir);
	float t = Trace(at, lDirN, EPSILON*2.);
	if (t < length(lDir)) {
		vec3 pos = at + lDirN * t;
		if(!IsWaterSurface(pos))
			return vec3(0.);
	}
	vec3 color = m.Kd * lColor * max(0., dot(normal, normalize(lDir)));

	if (m.Shininess > 0.) {
		vec3 h = normalize(normalize(lDir) + normalize(eye - at));
		color += lColor * pow(max(0., dot(normal, h)), m.Shininess) * (m.Shininess + 8.) / 25.;
	}
	return color / dot(lDir, lDir);
}


float beforeWater(vec3 ro, vec3 ray)
{
    float t = 0.0;
    for (int i = 0; i < 64; i++) {
        float res = WaterSurface2(ro+ray*t);
        if( res < 0.0001 ) return t;
        t += res;
    }

    return -1.0;
}

float water(vec3 ro, vec3 ray)
{
    float t = 0.0;
    for (int i = 0; i < 128; i++) {
        float res = WaterSurface(ro+ray*t);
        if( res < 0.01 ) return t;
        t += res*0.5;
    }

    return -1.0;
}

float pool(vec3 ro, vec3 ray)
{
    float t = 0.0;
    for (int i = 0; i < 64; i++) {
        float res = Pool(ro+ray*t);
        if( res < 0.001 ) return t;
        t += res;
    }

    return -1.0;
}

float caustics(vec3 p, vec3 lp) {
    vec3 ray = normalize(p - lp);

    float d = beforeWater(lp, ray);
    vec3 inter = lp + ray * d;


    if (d < -0.5) {
    	return (0.0);
    }

    //d = water(lp, ray);
    //vec3 surfpos = lp + ray * d;
    vec3 surfpos = inter - vec3(0., WaterWave(inter) - 0.01, 0.);

    vec3 refractRay = refract(ray, vec3(0., 1., 0.), 1./1.333);
    float beforeHit = pool(inter, refractRay);
    vec3 beforePos = inter + refractRay * beforeHit;
    //return beforePos.y;
    float tp = (-beforePos.y - DepthPool) / refractRay.y;
    beforePos += refractRay * tp;

    refractRay = refract(ray, Normal(surfpos), 1./1.333);
    float afterHit = pool(surfpos, refractRay);
    vec3 afterPos = surfpos + refractRay * afterHit;
    tp = (-afterPos.y - DepthPool) / refractRay.y;
    afterPos += refractRay * tp;

    float beforeArea = length(dFdx(beforePos)) * length(dFdy(beforePos));
    float afterArea = length(dFdx(afterPos)) * length(dFdy(afterPos));
    return max(beforeArea / afterArea, .001);
}

vec3 Shade(vec3 rpos, vec3 rdir, float t)
{
	vec3 pos = rpos + rdir * t;
	vec3 nor = Normal(pos);

	bool waterSurface = IsWaterSurface(pos);
	bool water = IsWater(pos);
	vec3 waterSurfaceLight = vec3(0);
    vec3 light = vec3(50.);
	if (waterSurface)
	{
		vec3 refractionDir = refract(normalize(rdir), nor, 1./1.333);

		waterSurfaceLight = Lighting(pos, nor, rpos, Material(pos), light, LightSource);

		float wt = Trace(pos, refractionDir, 0.03);		
		pos += refractionDir * wt;
		nor = Normal(pos);
	}
	MaterialInfo mat = Material(pos);

	vec3 color = .11 * mat.Kd;


	if (water || waterSurface) {
        if (IsPool(pos) || (IsBall(pos) && water)) {
            color += Lighting(pos, nor, rpos, mat, light, LightSource) * vec3(caustics(pos, LightSource));
            color *= WaterColor;
        }
        if (waterSurface) {
			color += waterSurfaceLight;
        }
    } else {
        color += Lighting(pos, nor, rpos, mat, light, LightSource);
    }
	return color;
}

vec3 Camera(vec2 px) {
	vec2 uv = px.xy / iResolution.xy * 2. - 1.;	
	uv.x *= iResolution.x / iResolution.y;

    float x = M_PI * 0.15;
    float y = 4.;

	if (iMouse.z>=1.) {
    	x = (iMouse.x/iResolution.x - .5)*7.;
    	y = (iMouse.y/iResolution.y)*7.;
    	x = clamp(x, -0.2, 1.8);
    }

	vec3 rayStart = vec3(sin(x) * 10.0, y * 1.5, cos(x) * 10.0);
	vec3 rayDirection = LookAt(rayStart, vec3(0, sin(iTime * 0.5) -1.5, 0), normalize(vec3(uv, -3.)));

	float path = Trace(rayStart, rayDirection, 0.);	
	return Shade(rayStart, rayDirection, path);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec3 col = Camera(fragCoord.xy);
	fragColor = vec4(col, 0.);
}

float SKY = -1.;
float FLOOR = 0.;

float sdPlane(vec3 p, float s, float h) {
    return s * p.y + h;
}

float sdCylinder(vec3 p) {
    return length(p.xz - vec2(0, .2)) - .1;
}

float sMin(float a, float b, float k) {
    // https://www.iquilezles.org/www/articles/smin/smin.htm
    float h = clamp(.5 + .5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

vec2 opU(vec2 dm1, vec2 dm2) {
    // https://www.shadertoy.com/view/Xds3zN
    return dm1.x < dm2.x ? dm1 : dm2;
}

vec2 map(vec3 p) {
    p.x = mod(p.x, 2.0) - 0.5 * 2.0;
    p.z = mod(p.z, 2.0) - 0.5 * 2.0;
    vec2 dm;
    dm = opU(vec2(sdPlane(p, 1., 1.), FLOOR), vec2(sdPlane(p, -1., 1.), FLOOR));
    dm = vec2(sMin(dm.x, sdCylinder(p), 1.), FLOOR);
   	return dm;
}

vec2 rayMarch(vec3 ro, vec3 rd) {
    // Shortest distance and material key.
    vec2 dm;
    // Current step size.
    float s = 0.;
    for (int i = 0; i < 256; i++) {
        // Current position in the scene.
        vec3 p = ro + rd * s;
        dm = map(p);
        s += dm.x;
    }
    if (dm.x > 0.1) {
        // Our ray hasn't hit anything.
        dm.y = SKY;
    }
    return vec2(s, dm.y);
}

vec3 normal(vec3 p) {
    // Tetrahedron technique
    // http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
    float h = 0.0001;
	vec2 k = vec2(1, -1);
    return normalize(
        k.xxx * map(p + k.xxx * h).x +
        k.xyy * map(p + k.xyy * h).x +
        k.yxy * map(p + k.yxy * h).x +
        k.yyx * map(p + k.yyx * h).x);
}

// General lighting resource: http://www.sunandblackcat.com/tipFullView.php?l=eng&topicid=30
vec3 light(vec3 ro, vec3 p, vec3 l) {
	vec3 n = normal(p);

    // Material properties (chrome).
    // https://people.eecs.ku.edu/~jrmiller/Courses/672/InClass/3DLighting/MaterialProperties.html
    vec3 ka = vec3(.25);
    vec3 kd = vec3(.4);
    vec3 ks = vec3(.774597);
    float es = 76.8; // Larger -> smaller highlight.

    // Ambient.
    // https://en.wikipedia.org/wiki/Shading#Ambient_lighting
    float a = 0.5;

    // Diffuse.
    // https://en.wikipedia.org/wiki/Lambertian_reflectance
    float d = clamp(dot(n, normalize(l - p)), 0., 1.);

    // Specular.
    // https://en.wikipedia.org/wiki/Blinn%E2%80%93Phong_shading_model
    vec3 v = normalize(ro - l);
    vec3 h = normalize(v + l);
    float s;
    if (dot(n, l) > 0.) {
        // Surface is oriented to the light source.
        s = pow(clamp(dot(n, h), 0., 1.), es);
    } else {
        s = 0.;
    }

    return a * ka + d * kd + s * ks;
}

vec3 render(vec3 ro, vec3 rd) {
    vec3 c;
    // Render by coloring intersections.
    vec2 dm = rayMarch(ro, rd);
    if (dm.y == FLOOR) {
        // The intersection.
        vec3 p = ro + rd * dm.x;
        // The point light.
        vec3 l = vec3(2, 2, -2);
        c = light(ro, p, l);   
    } else {
        c = vec3(0);
    }
    // Gamma correction.
    return pow(c, vec3(0.4545));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Remap xy to [-1, 1] and correct aspect ratio.
    vec2 xy = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // z: Start behind the screen.
	vec3 ro = vec3(0, 0, iTime);
    // z: Go through the screen.
    vec3 rd = normalize(vec3(xy, 1));

    fragColor = vec4(render(ro, rd), 1);
}

/*
