// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  float l;
  float v = object(p, l);
  n.x = object( vec3(p.x+eps, p.y, p.z), l ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z), l ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps), l ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;
    // Don't start at the origin, instead move a little bit forward
    float t=rA;
    float l;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p, l);
    // Hit object
      if (v > 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += Epsilon;
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, out bool h,out int s, out float lam)
{
  h = false;
	lam = 1.0;
    // Don't start at the origin, instead move a little bit forward
    float t=rA;
	float l;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p, l);
    // Hit object
      if (v > 0.0)
      {
          s=i;
          h = true;
          lam = l;
          break;
      }
      // Move along ray
      //t += max(Epsilon,abs(v)/4.0);
      // Move along the ray but faster
      t += max(Epsilon,abs(v)/l);
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}

// Background color
vec3 background(vec3 rd)
{
    //return vec3(0, 0, 0);
  return mix(vec3(0.4, 0.3, 0.0), vec3(0.7, 0.8, 1.0), rd.y*0.5+0.5);
}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n)
{
  // point light
  //const vec3 lightPos = vec3(5.0, 5.0, 5.0);
  const vec3 lightPos = vec3(0.0, 0.0, 0.0);
  const vec3 lightColor = vec3(0.5, 0.5, 0.5);

  vec3 c = 0.25*background(-n);
  vec3 l = normalize(lightPos - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = 0.5*(1.0+dot(-n, l));

  c += diff*lightColor;

  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
    return vec3(3.*t, t, 3.*t);
   return vec3(t,0.25+0.75*t,0.5-0.5*t);
}


float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 pixel = (gl_FragCoord.xy / iResolution.xy)*2.0-1.0;
	//pixel*=2.0;
  // compute ray origin and direction
  float asp = iResolution.x / iResolution.y;
  vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -4.0));
  vec3 ro = vec3(0.0, 0.0, 25.0);

  // vec2 mouse = iMouse.xy / iResolution.xy;
  float a=3.14;
  // Uncomment to have the scene rotation
  a=iTime*0.25;
  ro = rotateY(ro, a);
  rd = rotateY(rd, a);

  // Trace ray
  bool hit;

  // Number of steps
  int s;
  float lam;
  float t = SphereTrace(ro, rd, hit,s, lam);
  vec3 pos=ro+t*rd;
  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pos);

    // Shade object with light
    rgb = Shade(pos, n);
    // Shade object with "lambda"
    // rgb = vec3(1.0/lam, 1.0/lam, 1.0/lam);
  }
  else
  {
      float star = rand(pixel);  
      if(star > 0.9985)
          rgb = vec3(1, 1, 1);
      else
          rgb = vec3(0, 0, 0);
  }

  // Add step shading to have a cool dusty effect
  rgb += ShadeSteps(s);

  fragColor=vec4(rgb, 1.0);
}

// Info on Hilbert curves: http://mathworld.wolfram.com/HilbertCurve.html
// https://arxiv.org/pdf/1109.2323.pdf
// 
// Inspiration from https://www.shadertoy.com/view/XtjXW3


#define AA 1   // make this 1 is your machine is too slow

//------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = (p-a), ba = (b-a);
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

float sdCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float length2( vec2 p )
{
	return sqrt( p.x*p.x + p.y*p.y );
}

float length6( vec2 p )
{
	p = p*p*p; p = p*p;
	return pow( p.x + p.y, 1.0/6.0 );
}

float length8( vec2 p )
{
	p = p*p; p = p*p; p = p*p;
	return pow( p.x + p.y, 1.0/8.0 );
}

//------------------------------------------------------------------

float opS( float d1, float d2 )
{
    return max(-d2,d1);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opRotXZ(vec3 p)
{	
    float angle = 3.1415/2.0;
	return vec3(cos(angle) * p.x + sin(angle) * p.z, p.y, -sin(angle) * p.x + cos(angle) * p.z);
}

vec3 opRotYZ(vec3 p)
{	
    float angle = 3.1415/2.0;
	return vec3(p.x,cos(angle) * p.y + sin(angle) * p.z, -sin(angle) * p.y + cos(angle) * p.z);
}


vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}

vec3 opTwist( vec3 p )
{
    float  c = cos(10.0*p.y+10.0);
    float  s = sin(10.0*p.y+10.0);
    mat2   m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

//------------------------------------------------------------------

vec2 hilbert(in vec3 center, float scale)
{
	vec2 o =         vec2( sdCapsule(   center ,vec3(-.5,-.5, .5)*scale, vec3(-.5,-.5,-.5)*scale, 0.01  ), 31.9 );
	     o = opU( o, vec2( sdCapsule(   center ,vec3( .5,-.5,-.5)*scale, vec3( .5, .5,-.5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3( .5, .5,-.5)*scale, vec3( .5, .5, .5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3( .5, .5, .5)*scale, vec3(-.5, .5, .5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3(-.5, .5, .5)*scale, vec3(-.5, .5,-.5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3(-.5, .5,-.5)*scale, vec3(-.5,-.5,-.5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3( .5,-.5, .5)*scale, vec3( .5,-.5,-.5)*scale, 0.01  ), 31.9 ) );
	     o = opU( o, vec2( sdCapsule(   center ,vec3( .5,-.5,-.5)*scale, vec3( .5,-.5, .5)*scale, 0.01  ), 31.9 ) );
	return o;
}

vec2 hilbertBlock(in vec3 botLeft, in vec3 topRight, float scale)
{
	vec3 center = botLeft + (topRight - botLeft) / 2.;
	vec3 offset = (topRight - botLeft) * scale;	
	vec3 offsetSub1 = offset / 2.; offsetSub1.z*=-1.0;
	vec3 offsetSub2 = offset / 2.; offsetSub2.z*=-1.0; offsetSub2.y*=-1.0;
	vec3 offsetSub3 = offset / 2.; offsetSub3.z*=-1.0; offsetSub3.x*=-1.0;

	vec2 o = 	    		hilbert( 	opRotYZ(opRotXZ(										center + offset * vec3(-1, 1,-1) )		)		,	 scale);
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub1) * vec3(-1, 1,-1), -(offset+offsetSub1) * vec3(-1, 1, 1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotXZ(opRotYZ(opRotXZ(opRotXZ(						center + offset * vec3(-1, 1, 1) )		)))		,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub2) * vec3(-1, 1, 1), -(offset+offsetSub2) * vec3(-1,-1, 1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotXZ(opRotYZ(opRotXZ(opRotXZ(						center + offset * vec3(-1,-1, 1) )		)))		,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub1) * vec3(-1,-1, 1), -(offset+offsetSub1) * vec3(-1,-1,-1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotYZ(opRotYZ(										center + offset * vec3(-1,-1, -1) 		))		,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub3) * vec3(-1,-1,-1), -(offset+offsetSub3) * vec3( 1,-1,-1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotYZ(opRotYZ(										center + offset * vec3( 1,-1,-1) 		))		,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub1) * vec3( 1,-1,-1), -(offset+offsetSub1) * vec3( 1,-1, 1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotXZ(opRotYZ(opRotYZ(opRotYZ(						center + offset * vec3( 1,-1, 1) 		))))	,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub2) * vec3( 1,-1, 1), -(offset+offsetSub2) * vec3( 1, 1, 1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotXZ(opRotYZ(opRotYZ(opRotYZ(						center + offset * vec3( 1, 1,  1) 		))))	,	 scale));
		 o = opU( o,vec2( 	sdCapsule( 															center, -(offset+offsetSub1) * vec3( 1, 1, 1), -(offset+offsetSub1) * vec3( 1, 1, -1), 0.01), 71.9 ) );
		 o = opU( o,		hilbert( 	opRotYZ(opRotXZ(opRotXZ(opRotXZ(						center + offset * vec3( 1, 1,-1) 		))))	,	 scale));

	return o;
}

vec2 map( in vec3 pos )
{

	vec3 center = pos - vec3(0,.7, 0);												
	vec2 o = 			hilbert (center,1.2);										// slightly oversized to show the idea
		 //o = opU( o, 	hilbert(opRep(center,vec3(.25,.25,.25)),.125) );            // repeating does not include rotation :/?
		 o = opU( o, 	hilbertBlock( center               - vec3(.5), center               + vec3(.5),.25) );
		 //o = opU( o, 	hilbertBlock( center + vec3(1,0,0) - vec3(.5), center + vec3(1,0,0) + vec3(.5),.25) ); //next block



	vec2 res = opU( vec2( sdPlane(     pos),1. ),
					o);

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 0.2;
    float tmax = 20.0;

#if 1
    // bounding volume
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0004*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
    /*
	vec3 eps = vec3( 0.0005, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
	*/
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in vec2 p )
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
        if( m<1.5 )
        {

            float f = checkersGradBox( 5.0*pos.xz );
            col = 0.3 + f*vec3(0.1);
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
        vec3  hal = normalize( lig-rd );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.2, 0.2, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

        dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
        dom *= calcSoftshadow( pos, ref, 0.02, 2.5 );

		float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
        lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.40*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;
		col += 10.00*spe*vec3(1.00,0.90,0.70);

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.0002*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

		// camera	
        vec3 ro = vec3( 4.5*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y, 0.5 + 4.5*sin(0.1*time + 6.0*mo.x) );
        vec3 ta = vec3( -0.5, -0.4, 0.5 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.2) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}

