const float far = 1000.;
#define EPSILON 0.00001

//iq's signed-box distance function
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdBoxR(vec3 p, vec3 b) {
    p = p * ROT;
    return sdBox(p, b);
}

vec3 normalBoxR(vec3 p, vec3 b) {
     float dx = sdBoxR(vec3(p.x + EPSILON, p.y, p.z), b) - sdBoxR(vec3(p.x - EPSILON, p.y, p.z), b);
    float dy = sdBoxR(vec3(p.x, p.y + EPSILON, p.z), b) - sdBoxR(vec3(p.x, p.y - EPSILON, p.z), b);
    float dz = sdBoxR(vec3(p.x, p.y, p.z + EPSILON), b) - sdBoxR(vec3(p.x, p.y, p.z - EPSILON), b);
    return vec3(dx, dy, dz);
}



vec3 normalBox(vec3 p, vec3 b) {
    float dx = sdBox(vec3(p.x + EPSILON, p.y, p.z), b) - sdBox(vec3(p.x - EPSILON, p.y, p.z), b);
    float dy = sdBox(vec3(p.x, p.y + EPSILON, p.z), b) - sdBox(vec3(p.x, p.y - EPSILON, p.z), b);
    float dz = sdBox(vec3(p.x, p.y, p.z + EPSILON), b) - sdBox(vec3(p.x, p.y, p.z - EPSILON), b);
    return vec3(dx, dy, dz);
}


//Color palette function taken from iq's shader @ https://www.shadertoy.com/view/ll2GD3
#define  pal(t) ( .5 + .5* cos( 6.283*( t + vec4(0,1,2,0)/3.) ) )

//rgb to grey scale
vec3 greyScale(vec3 color, float lerpVal) {

    float greyCol = 0.3 * color.r + 0.59 * color.g + 0.11 * color.b;
    vec3 grey = vec3(greyCol, greyCol, greyCol);
    vec3 newColor = mix(color, grey, lerpVal);
    return newColor;
}

