    }
    else 
    {
    	up = normalize(cross(vec3(0., 0., 1.), nc));
    }
    vec3 right;
  	if(abs(dot(up, nc)) > 1. - EPSILON 
      || abs(dot(-up, nc)) > 1. - EPSILON ) 
    {
