#define PI 3.14159265359

float circle(in vec2 pos, in vec2 uv, in float radius)
{
    float dist = distance(pos,uv);
    return 1.0 - smoothstep(radius - 0.01,radius, dist);
}

float oscillation(in float angle, in float freq, in float rand)
{
    return sin(mod( sign(rand - 0.5) * angle + iTime,2. *PI) * freq * floor(rand * 10.));
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = iResolution.xy / min(iResolution.x, iResolution.y);
    uv *= aspect;

    vec2 center = vec2(0.5) * aspect;
    vec2 pos = uv - center;
    float angle = atan(pos.x,pos.y)+PI;

    // Background color
    vec3 background = vec3(0.0, 0.0, 0.0);
    vec3 col = background;

    // Information for the loop
    const float numberOfCircle = 10.;
    const float numberOfOscillation = 10.;

    for(float i = 0.; i < numberOfCircle; i++)
    {
       	float ste = 1.0/numberOfCircle;

        // Information for the function
        float osci = 0.;
        float amplitude = 0.01;

        for(float j = 0.; j < numberOfOscillation; j++)
        {
            float freq = 1.0;
            osci += oscillation(angle,freq,rand(vec2(j ,i )));
        } 

        // Information about circle
        vec3 circleColor = vec3(ste * i,  ste * i, 1.0);
        float radius = 0.45 - i * (ste/2.) + osci * amplitude;

		// Creation of the circle
        col = mix(col,circleColor,circle(center,uv,radius));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846264338327950
#define TWO_PI PI * 2.0

// All components are in the range [0…1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord - .5 *iResolution.xy) / iResolution.y;
    float angle = atan(pos.y, pos.x) / PI;
	float arms = fract(angle * 3.0);
    arms = abs(arms-0.5)*2.0;

    float dist = length(pos);

    dist = mix(dist,pow(dist,0.5),1.0);

    dist -= iTime*0.1;

    float green = sin((dist + arms*0.5) * 20.0)*0.5+0.5;
    green += sin((dist - arms*0.5 + sin(iTime*0.4)) * 20.0);
    green = clamp(green,0.0,1.0);

    float pink = sin((dist - arms*0.5) * 15.0)*0.5+0.5;
    pink += sin((dist + arms*0.5) * 15.0);
    pink = clamp(pink,0.0,1.0);

    vec3 color = vec3(0.1,0.05,0.8);

    color = mix(color,vec3(1.0,0.2,0.8),pink);
    color = mix(color,vec3(0.2,1.0,0.05),green);

    color = rgb2hsv(color);
    color.r += sin(iTime*0.3);
    color = hsv2rgb(color);

    // Output to screen
    fragColor = vec4(color,1.0);
}

