/**
 * Part 3 Challenges
 * - Make the camera move up and down while still pointing at the cube
 * - Make the camera roll (stay looking at the cube, and don't change the eye point)
 * - Make the camera zoom in and out
 */

const int MAX_MARCHING_STEPS = 1024;
const float MIN_DIST = 0.0;
const float MAX_DIST = 1000.0;
const float EPSILON = 0.0001;
const float PI = 3.1415926535897932384626433832795;

/**
 * Signed distance function for a cube centered at the origin
 * with width = height = length = 2.0
 */
float cubeSDF(vec3 p) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 p) {
    return length(p) - 5.0;
}

vec3 barycentric(vec3 p, vec3 a, vec3 b, vec3 c) {
 vec3 v0 = b - a, v1 = c - a, v2 = p - a;
 float d00 = dot(v0, v0);
 float d01 = dot(v0, v1);
 float d11 = dot(v1, v1);
 float d20 = dot(v2, v0);
 float d21 = dot(v2, v1);
 float denom = d00 * d11 - d01 * d01;
 float v = (d11 * d20 - d01 * d21) / denom;
 float w = (d00 * d21 - d01 * d20) / denom;
 float u = 1.0 - v - w;
 return vec3(u, v, w);
}

float dot2( in vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float tetraSDF(vec3 p, vec3 a, vec3 b, vec3 c, vec3 d) {
    float[4] lengths = float[4](length(a - p), length(b - p), length(c - p), length(d - p));
    int max_index = 0;
    float max_dist = -1.0;
    for (int i = 0; i < 4; i++) {
        if (lengths[i] > max_dist) {
            max_dist = lengths[i];
            max_index = i;
        }
    }
    if (max_index == 0) {
        return udTriangle(p, b, c, d);
    } else if (max_index == 1) {
        return udTriangle(p, a, c, d);
    } else if (max_index == 2) {
        return udTriangle(p, a, b, d);
    } else if (max_index == 3) {
        return udTriangle(p, a, b, c);
    }
}

float groundSDF(vec3 p, float level) {
    return (p.y - level);
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 p) {
    return min(tetraSDF(p, vec3(0,0,0), vec3(5,0,0), vec3(0,5,0), vec3(0,0,-5)), groundSDF(p, -1.0));
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the eye to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

/**
Returns a number to multiply the illumination by.
k is softness of shadows
*/
float softShadowMarch(vec3 eye, vec3 marchingDirection, float start, float end, float k) {
    float depth = start;
    float res = 1.0;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return 0.0;
        }
        depth += dist;
        //res = min(res, k*dist/depth);
        if (depth >= end) {
            return res;
        }
    }
    return res;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    float lightToPointDist = length(p - lightPos);
    float res = softShadowMarch(lightPos, normalize(p - lightPos),
                                EPSILON, lightToPointDist - 12.0*EPSILON, 50.0);
	//res = 1.0;

    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return res * lightIntensity * (k_d * dotLN);
    }
    return res * lightIntensity * (k_d * dotLN + 0.0 * k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(10.0 * sin(1.8*iTime),
                          9.0,
                          8.0 * cos(1.8*iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(10.0 * sin(1.3 * iTime),
                          15.0 * cos(1.3 * iTime),
                          8.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);

    //vec3 light3Pos = vec3(0,1.0,0.0);
    //vec3 light3Intensity = vec3(5, 1, 0);

    //color += phongContribForLight(k_d, k_s, alpha, p, eye,
                            //      light3Pos,
                           //       light3Intensity); 
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(23.0 * sin(0.5 * iTime), 4., 23.0 * cos(0.5 * iTime));

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 2.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    vec3 K_a = vec3(1.2, 1.2, 1.4);
    vec3 K_d = vec3(0.8, 0.8, 0.8);
    vec3 K_s = vec3(0.1, 0.1, 0.1);
    float shininess = 2.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

// "Sliding Penguins" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrEllipsDf (vec3 p, vec3 r);
float PrTorusDf (vec3 p, float ri, float rc);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
vec3 Noisev3v2 (vec2 p);
float Fbm2s (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_PENG 3

mat3 pngMat[N_PENG], pMat;
vec3 pngPos[N_PENG], pPos, qHit, sunDir, trkA, trkF;
float dstFar, tCur, fAng;
int idObj;
bool doSh;
const float pi = 3.14159;

vec3 TrackPath (float t)
{
  return vec3 (dot (trkA, sin (trkF * t)), 0.5 * cos (0.007 * t) * cos (0.01 * t), t);
}

vec3 TrackDir (float t)
{
  return vec3 (dot (trkF * trkA, cos (trkF * t)), 0., 1.);
}

vec3 TrackAcc (float t)
{
  return vec3 (dot (trkF * trkF * trkA, - sin (trkF * t)), 0., 0.);
}

float GrndHt (vec2 p)
{
  float w;
  w = p.x - TrackPath (p.y).x;
  return SmoothMin (8. * Fbm2s (0.05 * p), 0.1 * w * w - 0.5, 0.5) + Fbm2s (0.1 * p);
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 250; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0. || s > dstFar) break;
    sLo = s;
    s += max (0.01 * s, 0.4 * h);
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy),
     GrndHt (p.xz + e.yx)), e.x).xzy);
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float PengDf (vec3 p, float dMin)
{
  vec3 q;
  float d, dh;
  p.xz *= -1.;
  q = p;
  d = PrEllipsDf (q.xzy, vec3 (1.3, 1.2, 1.4));
  q.y -= 1.5;
  dh = PrEllipsDf (q.xzy, vec3 (0.8, 0.6, 1.3));
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.3, 2., -0.4);
  d = SmoothMin (d, max (dh, - PrCylDf (q, 0.15, 0.3)), 0.2);
  DMINQ (1);
  q = p;
  q.yz -= vec2 (1.6, -0.6);
  d = max (PrEllipsDf (q, vec3 (0.4, 0.2, 0.6)), 0.01 - abs (q.y));
  DMINQ (2);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.3, 2., -0.4);
  d = PrSphDf (q, 0.15);
  DMINQ (3);
  q = p;
  q.x = abs (q.x);
  q.xy -= vec2 (0.6, -1.05);
  q.yz = Rot2D (q.yz, -0.5 * pi);
  q.y -= -0.6;
  d = PrCylDf (q.xzy, 0.12, 0.7);
  DMINQ (4);
  q -= vec3 (0.1, -0.67, -0.4);
  q.xz = Rot2D (q.xz, -0.07 * pi);
  d = PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05));
  q.z -= 0.5;
  q.xz = Rot2D (q.xz, 0.15 * pi);
  q.z -= -0.5;
  d = SmoothMin (d, PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05)), 0.05);
  q.z -= 0.5;
  q.xz = Rot2D (q.xz, -0.3 * pi);
  q.z -= -0.5;
  d = SmoothMin (d, PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05)), 0.05);
  DMINQ (5);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (1.1, 0.3, -0.2);
  q.yz = Rot2D (q.yz, -0.25 * pi);
  q.xy = Rot2D (q.xy, fAng) - vec2 (0.1, -0.4);
  d = PrEllipsDf (q.xzy, vec3 (0.05, 0.25, 0.9));
  DMINQ (6);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float d, dMin;
  dMin = dstFar;
  for (int j = 0; j < N_PENG; j ++) {
    q = p - pngPos[j];
    d = PrSphDf (q, 3.);
    if (doSh || d < 0.1) dMin = PengDf (pngMat[j] * q, dMin);
    else dMin = min (dMin, d);
  }
  q = p;
  q.x -= TrackPath (p.z).x;
  q.y -= 2.;
  q.z = mod (q.z + 20., 40.) - 20.;
  d = 0.8 * PrTorusDf (q, 0.3, 7.);
  DMINQ (7);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float GrndSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  doSh = true;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    h = p.y - GrndHt (p.xz);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float f;
  rd.y = abs (rd.y) + 0.0001;
  ro.xz += 0.5 * tCur;
  f = Fbm2s (0.02 * (rd.xz * (100. - ro.y) / rd.y + ro.xz));
  col = vec3 (0.1, 0.2, 0.4);
  col = mix (col, vec3 (0.8), clamp (3. * (f - 0.5) * rd.y + 0.1, 0., 1.));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, vns;
  float dstGrnd, dstObj, gg, dx, sh;
  doSh = false;
  dstGrnd = GrndRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstGrnd) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj != 3) {
      if (idObj == 1) col4 = (qHit.z < -0.2 || qHit.z > 0.4 && abs (qHit.x) < 0.04 ||
         qHit.z < 0.4 && length (qHit.xy) < 0.2) ? vec4 (0.9, 0.9, 0.9, 0.1) :
         vec4 (0.15, 0.15, 0.2, 0.1);
      else if (idObj == 2) col4 = vec4 (1., 0.8, 0.2, 0.2);
      else if (idObj == 4) col4 = vec4 (0.8, 0.8, 0., 0.2);
      else if (idObj == 5) col4 = vec4 (0.9, 0.9, 0., 0.2);
      else if (idObj == 6) col4 = vec4 (0.2, 0.2, 0.25, 0.1);
      else if (idObj == 7) col4 = vec4 (0.3, 0.4, 0.7, 0.2);
      sh = ObjSShadow (ro, sunDir);
      col = col4.rgb * (0.2 + 0.1 * max (- dot (vn.xz, normalize (sunDir.xz)), 0.) +
         0.7 * sh * max (dot (vn, sunDir), 0.)) +
         sh * col4.a * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    } else col = SkyCol (ro, reflect (rd, vn));
  } else if (dstGrnd < dstFar) {
    ro += dstGrnd * rd;
    vn = GrndNf (ro);
    gg = smoothstep (0.6, 0.9, vn.y);
    vn = VaryNf (4. * ro, vn, 0.5);
    vns = normalize (Noisev3v2 (128. * ro.xz) - 0.5);
    vns.y = abs (vns.y);
    dx = abs (ro.x - TrackPath (ro.z).x);
    col = vec3 (1.) * mix (1.1, 1., gg) * (0.7 +
       0.3 * mix (Noisefv2 (vec2 (16. * dx, 0.5 * ro.z)), 1., smoothstep (0., 2., dx)));
    sh = min (GrndSShadow (ro, sunDir), ObjSShadow (ro, sunDir));
    col = col * (0.2 + 0.1 * max (- dot (vn.xz, normalize (sunDir.xz)), 0.) +
       0.7 * sh * max (dot (vn, sunDir), 0.)) +
       0.3 * sh * gg * smoothstep (0.3, 0.5, dot (vn, sunDir)) *
       pow (max (dot (normalize (sunDir - rd), vns), 0.), 8.);
  } else col = SkyCol (ro, rd);
  return pow (clamp (col, 0., 1.), vec3 (0.9));
}

void PengPM (float s, float dir)
{
  vec3 vel, va, acc, ort, cr, sr;
  pPos = TrackPath (s);
  vel = TrackDir (s);
  acc = TrackAcc (s);
  pPos.x -= 50. * acc.x;
  va = cross (acc, vel) / length (vel);
  ort = vec3 (-0.1 * dir, atan (vel.z, vel.x) - 0.5 * dir * pi,
     12. * dir * length (va) * sign (va.y));
  cr = cos (ort);
  sr = sin (ort);
  pMat = mat3 (cr.z, - sr.z, 0., sr.z, cr.z, 0., 0., 0., 1.) *
     mat3 (1., 0., 0., 0., cr.x, - sr.x, 0., sr.x, cr.x) *
     mat3 (cr.y, 0., - sr.y, 0., 1., 0., sr.y, 0., cr.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec3 rd, ro;
  vec2 canvas, uv;
  float spd;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  tCur = mod (tCur, 600.) - 300.;
  trkA = 5. * vec3 (1.9, 2.9, 4.3);
  trkF = 0.18 * vec3 (0.23, 0.17, 0.13);
  spd = 10.;
  for (int j = 0; j < N_PENG; j ++) {
    PengPM (spd * tCur + 15. * float (j + 1), 1.);
    pPos.y = GrndHt (pPos.xz) + 1.35;
    pPos.y += max (0., 0.5 * cos (0.13 * pPos.z) * cos (0.2 * pPos.z));
    pngPos[j] = pPos;
    pngMat[j] = pMat;
  }
  if (mod (0.1 * tCur, 2.) > 1.2) PengPM (spd * tCur, 1.);
  else PengPM (spd * tCur + 15. * float (N_PENG + 1), -1.);
  ro = pPos;
  ro.y += 3.;
  rd = normalize (vec3 (uv, 2.6)) * pMat;
  fAng = -0.2 * pi + 0.1 * pi * SmoothBump (0.25, 0.75, 0.1, mod (0.2 * tCur, 1.)) *
     sin (16. * pi * tCur);
  sunDir = normalize (vec3 (0., 1.5, 1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.3 * pi * sin (0.1 * tCur));
  dstFar = 250.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4f (float p)
{
  return fract (sin (p + vec4 (0., 1., 57., 58.)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

vec3 Noisev3v2 (vec2 p)
{
  vec4 h;
  vec3 g;
  vec2 ip, fp, ffp;
  ip = floor (p);
  fp = fract (p);
  ffp = fp * fp * (3. - 2. * fp);
  h = Hashv4f (dot (ip, vec2 (1., 57.)));
  g = vec3 (h.y - h.x, h.z - h.x, h.x - h.y - h.z + h.w);
  return vec3 (h.x + dot (g.xy, ffp) + g.z * ffp.x * ffp.y,
     30. * fp * fp * (fp * fp - 2. * fp + 1.) * (g.xy + g.z * ffp.yx));
}

float Fbm2s (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.75);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float scale = 1./min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord*scale;
    float t = iTime / 40.;

    vec2 fpos = fragCoord/iResolution.xy;
    float jitter = 20. * (fpos.x + fpos.y + fract(iTime));
    jitter = 2. * sin(iTime/16.18) * fract(jitter*jitter*jitter);

    int u = int(dot(uv, 300. * vec2(cos(t), sin(t))) + jitter);
    float i = float((u ^ u << 2) * 15 & 255)/255.0;

    fragColor = vec4(0.5 + 0.5*i*vec3(1,5,2),1.0);
}

#define PI 3.14159265
#define PI2 6.28318530

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

vec2 scale(vec2 v, vec2 s)
{
    mat2 m = mat2(s.x, 0.0, 0.0, s.y);
    return m * v;
}

float astroid(vec2 p, float n, float coeff,
              float a, float b, float c)
{
    float phi=atan(p.y, p.x);
    float r = length(p);
    float xmax = sqrt(-log(2.0*exp(-a*a)-1.0)/(b*b));
    float r0 = xmax*coeff;
    float eq=r0+(1.0/c)*sqrt(-log(2.0*exp(-a*a)-
          exp(-b*b*xmax*xmax*sin((phi-PI/2.0)*
          (n/2.0))*sin((phi-PI/2.0)*(n/2.0)))));
    return r-eq;
}

float sdEllipse( in vec2 p, in vec2 ab )
{
    p = abs(p); if( p.x > p.y ) {p=p.yx;ab=ab.yx;}
    float l = ab.y*ab.y - ab.x*ab.x;

    float m = ab.x*p.x/l;      float m2 = m*m; 
    float n = ab.y*p.y/l;      float n2 = n*n; 
    float c = (m2+n2-1.0)/3.0; float c3 = c*c*c;

    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;

    float co;
    if( d < 0.0 )
    {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = (ry+sign(l)*rx+abs(g)/(rx*ry)- m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow(abs(q+h), 1.0/3.0);
        float u = sign(q-h)*pow(abs(q-h), 1.0/3.0);
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx)+2.0*g/rm-m)/2.0;
    }

    vec2 r = ab * vec2(co, sqrt(1.0-co*co));
    return length(r-p) * sign(p.y-r.y);
}

float sdCaps( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));

    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;

    return dot(p, vec2(a,b) ) - r1;
}

float sdTri( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);

    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));

    return -sqrt(d.x)*sign(d.y);
}

float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.0*(fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 norm = fragCoord.xy/iResolution.xy;
    vec2 pnorm = 2.0*(fragCoord.xy-0.5*iResolution.xy)/iResolution.xy;

    vec2 starscale = scale(p, vec2(1.0, 1.0));

    //red star
    float star1_raw = astroid(starscale*3.5, 5.0, 0.09, 0.80, 0.13, 1.0);
    float star1 = smoothstep(0.01, 0.06, star1_raw);

    //black star
    float star2_raw = astroid(rotate(starscale*4.0, radians(180.0)), 5.0, 0.09, 0.79, 0.13, 1.0);
    float star2 = smoothstep(0.01, 0.06, star2_raw);

    //outline1
    float staroutline1 = 1.0-smoothstep(0.12, 0.15, star1_raw);
    float staroutline2 = 1.0-smoothstep(0.12, 0.15, star2_raw);
    float staroutline = clamp(staroutline1+staroutline2, 0.0, 1.0);

    //face
    float face_raw = sdEllipse(p, vec2(0.43, 0.57));
    face_raw = clamp(face_raw ,0.0, 1.0);
    float face = smoothstep(0.01, 0.02, face_raw);

    //outline2
    float commonoutline = (1.0-smoothstep(-1., 1.0, star1_raw))+
        				  (1.0-smoothstep(-1., 1.0, star2_raw))+
        				  (1.0-smoothstep(-0.05, 0.5, face_raw));
    commonoutline = clamp(commonoutline, 0.0, 1.0);

    //eyes
    vec2 eyepos = vec2(-0.25, -0.08);
    vec2 mirror = p;
    mirror.x=abs(mirror.x);
    vec2 eyecd = rotate(mirror+eyepos, radians(113.0));
    float eye = sdCaps(eyecd, 0.06, 0.09, 0.08);
    eye=smoothstep(0.01, 0.021, eye);

    //top triangles
    vec2 tri1pos = vec2(-0.2, -0.24);
    vec2 tri1cd = rotate(mirror+tri1pos, radians(110.0));
    float tri1 = sdTri(tri1cd, vec2 (0.04, 0.3));
    tri1=smoothstep(0.0, 0.01, tri1);

    //bottom triangles
    vec2 tri2pos = vec2(-0.1, 0.1);
    vec2 tri2cd = rotate(mirror+tri2pos, radians(210.0));
    float tri2 = sdTri(tri2cd, vec2 (0.02, 0.3));
    tri2=smoothstep(0.0, 0.01, tri2);

    //pulps

    float blink = (cos(iTime*3.0)*0.5+0.5)*0.3+0.5;
    vec2 pulppos = vec2(-0.15, -0.05);
    vec2 pulpcd = rotate(mirror+pulppos, radians(210.0));
    float pulp = sdCircle(pulpcd, 0.01);
    pulp = clamp(pulp, 0.0, 1.0);
    pulp = pow(pulp, 0.25);
    pulp = smoothstep(0.00, blink, pulp);

    //background

    float rnorm = clamp(length(pnorm), 0.0, 1.0);
    float r = atan(p.y, p.x);
    r += -iTime*0.1;
    float a = length(p);
    float strips = cos((r+a*2.)*8.0)*0.5+0.5;
    strips = smoothstep(0.47, 0.53, strips);
    float bgmixfact = pow(rnorm*0.8, 1.5);

    //mixing
    vec3 bg1 = vec3(0.447,0.680,0.700);
    vec3 bg2 = vec3(0.249,0.379,0.390);
    vec3 bgcolor = mix(bg1, bg2, strips)*1.0;
    vec3 bgmask = vec3(0.049,0.055,0.049);
    vec3 back = bgcolor;
    vec3 outlinecolor = vec3(0.137,0.155,0.142);
    vec3 staroutcolor = vec3(0.936,0.950,0.921);
    vec3 star1_color = vec3(0.855,0.324,0.316);
    vec3 star2_color = vec3(0.137,0.155,0.142);
    vec3 facecolor1 = vec3(0.950,0.920,0.881);
    vec3 facecolor2 = vec3(0.830,0.719,0.637);
    vec3 eyecolor = star2_color;
    vec3 tricolor = star1_color;
    vec3 facecolor = mix(facecolor1, facecolor2, pow(norm.x,3.));
    vec3 pulpcolor = vec3(0.960,0.230,0.138);
    vec3 lay0 = mix(back, outlinecolor, commonoutline);
    vec3 lay1 = mix(lay0, staroutcolor, staroutline);
    vec3 lay2 = mix(star1_color, lay1, star1);
    vec3 lay3 = mix(star2_color, lay2, star2);
    vec3 lay4 = mix(facecolor, lay3, face);
    vec3 lay5 = mix(eyecolor, lay4, eye);
    vec3 lay6 = mix(tricolor, lay5, tri1);
    vec3 lay7 = mix(tricolor, lay6, tri2);
    vec3 lay8 = mix(pulpcolor, lay7, pulp);
    vec3 lay9 = mix(lay8, bgmask, bgmixfact);
    vec3 col = vec3(lay9);

    fragColor = vec4(col,1.0);
}

float circle( vec2 p, vec2 center, float r )
{
    p = p - center;
    float len = abs( length( normalize( p ) * r )  - length( p )  );

    return pow( len, 0.2 );
}

float sphere( vec2 p, float r )
{
    return 0.8 * max( 0.0, r - length( p ) );
}

float pulse( vec2 p )
{
    return 1.2 + 0.2 * sin( length( p ) * 20. - iTime * 5.0 );
}

vec3 neon( float v )
{
    vec3 col = vec3( 1.9, 1.5, 1.7 );

    col = col * -v + col;
    col = col * col;
    col = col * col;

    return col;
}

vec2 rotate2( vec2 p, float r, float a )
{
    float c = cos( a );
    float s = sin( a );

    return mat2(
    c * r, s * r,
            -s * r, c * r ) * p;
}

vec3 hash(vec3 p3) {
    p3 = fract( p3 * vec3( .1031, .11369, .13787 ) );
    p3 += dot( p3, p3.yxz + 19.19 );
    return -1. + 2. * fract(vec3((p3.x + p3.y) * p3.z, (p3.x+p3.z) * p3.y, (p3.y+p3.z) * p3.x));
}

float noise_perlin( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

    vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

float noise4( vec3 p )
{
 float f;
    f  = 0.5000 * noise_perlin( p ); 
 f += 0.2500 * noise_perlin( p * 2. );
 f += 0.1250 * noise_perlin( p * 4. ); 
 f += 0.0625 * noise_perlin( p * 8. );
 return f;
}

float noise_sum_abs(vec3 p) {
    float f = 0.;
    p = p * 3.;
    f += 1.0000 * abs( noise_perlin( p ) );
    f += 0.5000 * abs( noise_perlin( p * 2. ) );
    f += 0.2500 * abs( noise_perlin( p * 3. ) );
    f += 0.1250 * abs( noise_perlin( p * 4. ) );
    f += 0.0625 * abs( noise_perlin( p * 5. ) );

    return f;
}


vec3 energy( vec2 p )
{

    float t = clamp( -p.x * p.x * 0.16 + 0.25, 0.0, 1.0 );

    float s =  sphere( p, 0.8 ) * pulse( p ) * 2.2;
    float s2 = 1.3 * sphere( p, 1.8 );

    float n = noise_sum_abs( vec3( p.xy, iTime * 0.04 ) * 2.0  );    

    float v = pow( abs( n * -t + s2 ), 1.0 );

    vec3 baseCol = vec3( 2.8, 1.5, 1.2 ) * s;

    return neon( v ) * baseCol;
}

vec3 lightningImpl( vec2 p )
{
    float t = clamp( -p.x * p.x * 0.26 + 0.25, 0.0, 1.0  );
    float n = noise4( vec3( p.xy, iTime ) * 8.0 );

    float v = pow( abs( p.y + n * -t  ), 0.2 );
    float sh = sphere( p, 0.8 );

    return neon( v ) * sh;
}

float cos_a = cos( 0.4 );
float sin_a = sin( 0.5 );


vec3 lightning( vec2 p )
{
    vec3 col;
    float cont_val = cos_a * sin_a * 1.3;

    for( int i = 0; i < 3; ++i )
    {
        col += lightningImpl( p );
        p = p * cont_val;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p =  2.0 * uv - 1.0;
    p.x *= iResolution.x / iResolution.y;

    fragColor.xyz = energy( p ) + lightning( p );
}

/* Zuul-ci pyramid logo in 3D

   Code is mainly based on
   https://www.shadertoy.com/view/Xds3zN
   Uploaded by iq in 2013-03-25

    and

   https://www.shadertoy.com/view/ldKGWW
   Uploaded by wjbgrafx on 2016-02-04


   More resources:
   http://www.iquilezles.org/www/material/nvscene2008/rwwtt.pdf
   http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
*/
// Set to 1 if too slow
#define AA 2

// Comment to remove scene elements
#define SKY
#define MONSTER
#define FLOOR

const vec3 cam = vec3(0., 0., -2.7 );
#define PI 3.141592653589793
#define PITCH 0.328
#define YAW -0.8
#define DEG60 0.866025

// Primitives
const mat3 rotX180 = mat3(1.0,     0.0,     0.0,
                          0.0,  cos(PI), sin(PI),
                          0.0, -sin(PI), cos(PI));

float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h);
}

vec2 opU(vec2 d1, vec2 d2) {
  return (d1.x<d2.x) ? d1 : d2;
}

float pMirror (inout float p, float dist) {
  float s = sign(p);
  p = abs(p) - dist;
  return s;
}

float sdPlane(vec3 p) {
  return p.y;
}

float sdTriPrism(vec3 p, vec2 h) {
  vec3 q = abs(p);
  return max(q.z - h.y,
         max(q.x * 0.4 + p.y * 0.5, -p.y) - h.x * 0.5);
}

float sdPrismZ(vec3 p, float angleRads, float height, float depth) {
  vec3 q = abs(p);
  return max(q.z - depth,
         max(q.x * angleRads + p.y * 0.5, -p.y ) - height * 0.5);
}

float sdPrismX(vec3 p, float angleRads, float height, float depth) {
    vec3 q = abs(p);
    return max(q.x - depth,
           max(q.z * angleRads + p.y * 0.5, -p.y) - height * 0.5 );
}

float sdPyramid(vec3 p, float angleRads, float height, float depth) {
  vec3 q = abs(p);
  return max(sdPrismX(p, angleRads, height, depth),
             sdPrismZ(p, angleRads, height, depth));
}

float sdBox(vec3 p, vec3 b) {
  vec3 d = abs(p) - b;
  return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float dot2(vec3 v) {
  return dot(v,v);
}

float udTriangle(vec3 p, vec3 a, vec3 b, vec3 c) {
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 ac = a - c; vec3 pc = p - c;
  vec3 nor = cross(ba, ac);

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

// Zuul logo primitives
float sdHollowPyramid(vec3 p) {
  float pyramid = sdPyramid(p, DEG60, 1.0, 1.0);
  pyramid = max(pyramid, -sdPyramid(p - vec3( 0.0, 0.,  0.5), DEG60, .9, .9));
  pyramid = max(pyramid, -sdBox(p, vec3(.45, 0.45, 0.45)));
  return pyramid;
}

float sdHollowBox(vec3 pos, vec3 size, float hole) {
  float box = sdBox(pos, size);
  box = max(box, -sdBox(pos, size * vec3(2., hole, hole)));
  box = max(box, -sdBox(pos, size * vec3(hole, hole, 2.)));
  return box;
}

float sdInnerFrame(vec3 pos) {
  float left  = sdHollowBox(pos + vec3(0.06, .1, .0), vec3(.39, .48, .45), 0.87);
  float right = sdHollowBox(pos + vec3(.0, .1, -.06), vec3(.45, .48, .39), 0.87);
  return left < right ? left : right;
}

float sdPilar(vec3 pos) {
  return sdBox(pos + vec3( .000, .15, -.424), vec3(.026, .35, .026));
}

float sdRoof(vec3 pos) {
 return sdTriPrism((pos - vec3(.44, .492, .0)) * rotX180, vec2(.05, .45));
}

float sdZuul(vec3 pos) {
  vec3 p = pos;
  pMirror(p.x, 0.0);
  pMirror(p.z, 0.0);
  float pyramid = sdHollowPyramid(p);
  float mainBox = sdHollowBox(p, vec3(.45, .517, .45), 0.87);
  float innerBox = sdInnerFrame(p);
  float lowerPlateau = sdBox(p - vec3(.0, .21, .0), vec3(.45, .026, .45));
  float pilar = sdPilar(p);
  float entranceWall = udTriangle(
      p, vec3(.78, -.45, .45), vec3(.39, -.45, .451), vec3(.39, .34, .451));
  float roof = sdRoof(p);

  float closer = pyramid;
  closer = smin(closer, lowerPlateau, .01);
  closer = smin(closer, entranceWall, .01);
  closer = closer < mainBox ? closer : mainBox;
  closer = closer < roof ? closer : roof;
  closer = closer < innerBox ? closer : innerBox;
  closer = closer < pilar ? closer : pilar;
  float socle = sdBox(pos + vec3(.0, .6, .0), vec3(2., .1, 2.));
  return max(closer, -socle);
}

#ifdef MONSTER
// Julia - Quaternion
// https://www.shadertoy.com/view/MsfGRr
const int numIterations = 11;

vec4 qsqr(vec4 a) {
  return vec4(a.x*a.x - a.y*a.y - a.z*a.z - a.w*a.w,
              2.0*a.x*a.y,
              2.0*a.x*a.z,
              2.0*a.x*a.w);
}

float juliaQ(vec3 p, vec4 c) {
  vec4 z = vec4(p, 0.0) * 3.6;
  float md2 = 20.0;
  float mz2 = dot(z, z);

  vec4 trap = vec4(abs(z.xyz), dot(z, z));

  for (int i=0; i<numIterations; i++) {
    md2 *= 4.0 * mz2;
    z = qsqr(z) + c;
    trap = min(trap, vec4(abs(z.xyz), dot(z, z)));
    mz2 = dot(z, z);
    if (mz2>4.0)
      break;
  }
  return 0.25 * sqrt(mz2 / md2) * log(mz2);
}
#endif

// The scene
vec2 scene(vec3 pos) {
  vec2 res = vec2(sdZuul(pos), 29.);
  #ifdef FLOOR
  float plane = sdPlane(pos + vec3(.0, 1., .0));
  // Does not work well, but tries to remove the floor when under it...
  if (plane > .0)
    res = opU(res, vec2(plane, 1.));
  #endif
  #ifdef MONSTER
  vec4 c = 0.45*cos(vec4(0.5,3.9,1.4,1.1) + (iTime*1.5)*vec4(1.2,1.7,1.3,2.5) ) - vec4(0.3,0.0,0.0,0.0);
  res = opU(res, vec2(juliaQ(pos + vec3(.0, .13, .0), c), 49.));
  #endif
  return res;
}

vec3 calcNormal(vec3 pos) {
  vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
  return normalize(e.xyy * scene(pos + e.xyy).x +
                   e.yyx * scene(pos + e.yyx).x +
                   e.yxy * scene(pos + e.yxy).x +
                   e.xxx * scene(pos + e.xxx).x);
}

float calcAO(vec3 pos, vec3 nor) {
  float occ = 0.0;
  float sca = 1.0;
  for (int i=0; i < 5; i++) {
    float hr = 0.01 + 0.12 * float(i) / 4.0;
    vec3 aopos = nor * hr + pos;
    float dd = scene(aopos).x;
    occ += -(dd - hr) * sca;
    sca *= 0.95;
  }
  return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

vec2 castRay(vec3 ro, vec3 rd) {
  const float tmin = 0.2;
  const float tmax = 20.0;
  float t = tmin;
  float m;
  for (int i=0; i < 300; i++) {
    vec2 res = scene(ro + rd * t);
    if (res.x < 0.0001 || t > tmax) break;
    t += res.x;
    m = res.y;
  }
  if (t > tmax) m = -1.0;
  return vec2(t, m);
}

// Improved soft shadow by Sebastian Aaltonen
// From: http://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow(vec3 ro, vec3 rd, float mint, float maxt) {
  float k = 32.;
  float res = 1.0;
  float ph = 1e20;
  for (float t=mint; t < maxt; ) {
    float h = scene(ro + rd * t).x;
    if (h < 0.001)
      return 0.0;
    float y = h * h / (2.0 * ph);
    float d = sqrt(h * h - y * y);
    res = min(res, k * d / max(0.0, t-y));
    ph = h;
    t += h;
  }
  return res;
}

#ifdef FLOOR
// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox(vec2 p) {
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;
}
#endif

#ifdef SKY
vec3 skyColor(vec3 rd) {
  float offset = (.95 - clamp(rd.y, 0.0, 0.2))*.7;
  return vec3(.5)+vec3(.5)*cos(6.28*(vec3(1.)*offset+vec3(0.0,0.10,0.20)));
}
#endif

vec3 render(vec3 ro, vec3 rd) {
  vec3 col = vec3(.0);
  #ifdef SKY
  col = skyColor(rd);
  #endif
  vec2 res = castRay(ro, rd);
  float t = res.x;
  float m = res.y;

  if (m > 0.) {
    vec3 pos = ro + t * rd;
    vec3 nor = calcNormal(pos);
    vec3 ref = reflect(rd, nor);
    col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
    #ifdef FLOOR
    if (m < 1.5) {
            float f = checkersGradBox( 5.0*pos.xz );
            col = 0.01 + f*vec3(0.1);
    }
    #endif
    // lightning
    float occ = calcAO(pos, nor);
    vec3  lig = normalize(vec3(0.1, 0.5, -0.6));
    vec3  hal = normalize(lig - rd);
    float amb = clamp(0.5 + 0.5 * nor.y, 0.0, 1.0);
    float dif = clamp(dot(nor, lig), 0.0, 1.0);
    float bac = clamp(dot(nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
    float dom = smoothstep(-0.2, 0.2, ref.y );
    float fre = pow(clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

    dif *= calcSoftshadow(pos, lig, 0.02, 2.5) * 0.2;
    dom *= calcSoftshadow(pos, ref, 0.02, 2.5);

    float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 16.0);
    spe *= dif * (0.04 + 0.96 * pow(clamp(1.0 + dot(hal, rd), 0.0, 1.0), 5.0));

    vec3 lin = vec3(0.0);
    lin += 3.30 * dif * vec3(1.00, 0.80, 0.55);
    lin += 0.40 * amb * vec3(0.40, 0.60, 1.00) * occ;
    lin += 0.40 * dom * vec3(0.40, 0.60, 1.00) * occ;
    lin += 0.50 * bac * vec3(0.25, 0.25, 0.25) * occ;
    lin += 0.25 * fre * vec3(1.00, 1.00, 1.00) * occ;
    col *= lin;
    col += 50.0 * spe * vec3(1.00, 0.90, 0.70);
    col = mix(col,  vec3(.0), 1.0 - exp(-0.0002 * t * t * t));
  }
  #ifdef SKY
  // fog
  float rayDist = length(ro + rd * t);
  col = mix( col, skyColor(rd), 1.0 - 1.0 / exp( rayDist * 0.05));
  #endif
  return vec3(clamp(col, 0.0, 1.0));
}

float smoothStair(float frame) {
  return frame - sin(frame) / 1.9;
}

mat3 camRotation() {
  float yaw, pitch;
  if( iMouse.z > 0.0 ) {
    yaw = (iMouse.x / iResolution.x - 0.5) * 4.;
    pitch = (iMouse.y / iResolution.y - 0.5) * 4.;
  } else {
    yaw = -.35 + smoothStair(iTime * 2.) / 2.;
    pitch = PITCH;
  }
  return mat3(1.0,        0.0,        0.0,
              0.0, cos(pitch), -sin(pitch),
              0.0, sin(pitch),  cos(pitch)) *
         mat3(cos(yaw),   0.0,  sin(yaw),
              0.0,        1.0,      0.0,
             -sin(yaw),   0.0,  cos(yaw));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  mat3 rot = camRotation();
  vec3 tot = vec3(0.0);
#if AA>1
  for( int m=0; m<AA; m++ )
  for( int n=0; n<AA; n++ ) {
    vec2 o = vec2(float(m), float(n)) / float(AA) - 0.5;
    vec2 uv = (gl_FragCoord.xy+o) / iResolution.xy*2.-1.;
#else
    vec2 uv = gl_FragCoord.xy / iResolution.xy*2.-1.;
#endif

    uv.y *= iResolution.y / iResolution.x;

    vec3 dir = normalize(vec3(uv, 1.)) * rot;
    vec3 pos = cam * rot;

    vec3 col = render(pos, dir);
    // gamma
    col = pow(col, vec3(0.4));

    tot += col;
#if AA>1
  }
  tot /= float(AA*AA);
#endif
  fragColor = vec4( tot, 1.0 );
}

