Shape vines(vec3 c){
  Shape shape;
  float vine;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Shape Attributes
  float twist = 10.; // Vine Repeat
  float vineOffset = 3.; // Leaf Offset


	vineOffset -= tan(iTime*0.5)+0.5;

  // Stems
  	vec3 i = c;
    i.xz *= rot(c.y*0.35+iTime); 
    i.y = repeat(i.y, .8); //repeat

    float detail = pModPolar(i.xz, twist); 

    //float x = mix(-2., 1., mod(detail, 1.));// Small Ripples
    //float x = mix(0., 1., pModPolar(i.xy, 10.)); 


    i.x -= vineOffset; // Offset Stems
  	float stemWidth = cos(iTime)*.9;
  	vine = pole(i.xz, stemWidth+0.02*sin(i.y));


  vec4 color = vec4(.1, .1, sin(iTime)*.5+0.8, 1.);

    float pattern = cos(sin(c.z*10.)) /
        cos(sin(c.y*2.));

  shape.dist = vine;
  shape.color = color;
  shape.color /= pattern;

  return shape;
}


Shape orb(vec3 c){
  Shape shape;
  float orb;
  shape.dist = 0.1; // Draw Distance
  shape.color = vec4(1.,0.5,1.,1.); // Initial Color

  // Stems
  vec3 i = c;
    i.xy *= rot(iTime); //rotate
    i.y = repeat(i.y, .8); //repeat
    i.x = repeat(i.x, 0.8);

    i.x = abs(i.x)-0.5; //duplicate
    i.z = abs(i.z)-0.5;
    orb = sphere(i, cos(sin(iTime)*1.25)*0.25);


  vec4 color = vec4(4.1, 0.75, 7.4, 1.);

  shape.dist = orb;
  shape.color = color;

  return shape;
}


Shape map(vec3 c){
  Shape vines = vines(c);
  Shape orbs = orb(c);

  float df = mixShapes(vines.dist, orbs.dist, 1.);
  vines.dist = df;

  vines.color = mix(vines.color, 
                    orbs.color*2., 
                    mixColors(orbs.dist, vines.dist, 1.));

  return vines;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., -10.);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.05){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.01){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    c.dist *= smoothstep(0., cos(iTime)*.2+.2, length(v.x/v.y)); //line in middle
    scene += f * c.dist;


  }
}

#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.x;

    vec2 points[1080];

    float offset = 0.05;
    float initial = 0.08;

    float i = 0.0;

    float speed = 500.0;

	for (int r = 0; r < 18; r++)
	{
		for (int a = 0; a < 60; a++)
		{
			float x = sin(PI / 6.0 * float(a) * iTime * float(r + 1)  / speed);
			float y = cos(-PI / 6.0 * float(a) * iTime * float(r + 1) / speed);
			vec2 p = vec2(x * (offset * float(r) + initial), y * (offset * float(r) + initial));
            i += smoothstep(0.005, 0.004, length(uv - p));
		}
	}

    // Output to screen
    fragColor = vec4(i);
}

#define PI 3.1415926535897932384626433832795
#define PHI 1.6180339887498948482


mat2 rotate(float theta) {
    mat2 m = mat2(cos(theta), sin(theta),
                  -sin(theta), cos(theta)
                 );
    return m;
}

float sinStep(float x, float w) {
    float y = (x+(1./w)*sin(w*x));
    return y;
}

float sinc(float x, float w) {
    float y = sin(w*x)/(w*x);
    return y;
}


float gaussianWindow1D(float x) {
    return exp(-(x*x));
}

float gaussianWindow2D(vec2 uv) {
    return exp(-(uv.x*uv.x+uv.y*uv.y));
}

float absPolyWindow(float x, float p) {
    return 1.-pow(abs(x),p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (-1.+2.*(fragCoord/iResolution.xy))*(iResolution.xy/iResolution.yy);

    float f = PI;
    float theta = 0.25*f*sinStep(iTime,f);
    uv *= rotate(theta);

    float df = distance(vec2(0.,0.), uv.xy);

    vec3 colOffset = (1.-df)*1.75*vec3(0.,1.,2.)*(0.5+0.5*cos(PI/2.+1.*f*iTime+0.25*f*sin(f*iTime)));

    //float win = clamp((1.-pow(df, 4.)),0.,1.);
   	float win = gaussianWindow1D(uv.y+uv.x);

    float sum = 0.;
    float a = 0.;
    float m = 16.;
    float zoom = (0.75-0.25*cos(f*iTime));
    vec3 col = vec3(0.,0.,0.);

    for (float i=1.; i<64.; i++) {
        a = (1./i)*(1.+(sinc(iTime,PI*i*m)))*0.5;
        sum += a;
        col += a*cos(f*((2.+cos(iTime*f/2.))+i*sinStep(iTime, f))+(16.*i*exp((2.*(uv.x*uv.y)*zoom))+colOffset));
    }

    col = (1.+col)*(1./sum)*win;
    // Output to screen
    fragColor = vec4(1.-col,1.0);
}

float hash(vec2 p) 
{
	return fract(sin(dot(p.xy, vec2(5.34, 7.13)))*5865.273458);   
}

vec2 hash2(vec2 p ) 
{
	return fract(sin(vec2(dot(p, vec2(123.4, 748.6)), dot(p, vec2(547.3, 659.3))))*5232.85324);   
}

float noise(vec2 p) 
{
    vec2 n = floor(p);
    vec2 f = fract(p);
    f = f * f * f * (3.0 - 2.0 * f);
    vec2 add = vec2(1.0, 0.0);
    float h = mix( mix(hash(n+add.yy), hash(n+add.xy), f.x), 
                   mix(hash(n+add.yx), hash(n+add.xx), f.x), f.y);

    return h;
}

//inspiration and a few lines of code from IQ's Pirates https://www.shadertoy.com/view/ldXXDj
float fbm(vec2 p) 
{
	float h = 0.0;
	float a = 0.5;
	for (int i = 0;i<4;i++) 
    {
      //h+=noise(p)*a;
		h+= texture(iChannel0, p).x*a; 
		p*=2.0;
		a*=0.5;
    }
	return h;
}

vec3 project(vec2 p) 
{
 	return vec3(p.x, p.y, sqrt(-(p.x*p.x+p.y*p.y-0.24)));   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     //set uv coordinates
	vec2 uv = (-iResolution.xy+2.0*fragCoord.xy) / iResolution.y;
    //project regular coordinates onto curved surface
    vec3 n = project(uv);
    //vec3 n = vec3(1.0f, 1.0f, 1.0f);

    //compute shading for curved moon
    float angle = iTime;
    vec3 sun = vec3(sin(angle*0.5)*2.0, 0.0, cos(angle*0.5)*2.0);
    //vec3 sun = vec3(0.0f, 1.0f, 0.0f);
    vec3 I = sun;
    I = normalize(I);
    float s = dot(n, I);
    s = clamp(s*1.9, 0.0, 1.0);

    //moon
    float dist = length(uv);
    vec3 moon = vec3(0.99, 0.99, 0.9);
    moon = moon*(1.0-0.2*smoothstep(0.4, 0.44, dist));
    //vec3 vtex = vec3(0.9+0.1*voronoi(uv*10.0));
    vec3 vtex = vec3(0.0f, 0.0f, 0.0f);
	fragColor = vec4(((mix(moon, vtex, fbm(uv*0.02))))*s, 1.0);   

}

#define FOG 1
#define SUNSET 0

float quintic(float x) {
 	return x*x*x*(6.*x*x-15.*x+10.);
}

const float fac = 43758.5453123;

float hash(float p) {
    return fract(fac*sin(p));
}

float noise(in vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p);

    float fac = 43758.5453123;
    const float upper = 100.;
    vec3 m = vec3(1., 10., 100.);//vec3(1., upper, upper*upper);
    vec2 o = vec2(1., 0.);

    float n000 = hash(dot((i + o.yyy), m));
    float n001 = hash(dot((i + o.xyy), m));
    float n010 = hash(dot((i + o.yxy), m));
    float n011 = hash(dot((i + o.xxy), m));
    float n100 = hash(dot((i + o.yyx), m));
    float n101 = hash(dot((i + o.xyx), m));
    float n110 = hash(dot((i + o.yxx), m));
    float n111 = hash(dot((i + o.xxx), m));

    float fx = quintic(f.x);
    float fy = quintic(f.y);
    float fz = quintic(f.z);

    float px00 = mix(n000, n001, fx);
    float px01 = mix(n010, n011, fx);

    float px10 = mix(n100, n101, fx);
    float px11 = mix(n110, n111, fx);

    float py0 = mix(px00, px01, fy);
    float py1 = mix(px10, px11, fy);

    return mix(py0, py1, fz);
}


float noise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 m = vec2(100., 1.);
    vec2 o = vec2(1., 0.);

    float n00 = hash(dot((i + o.yy), m));
    float n01 = hash(dot((i + o.xy), m));
    float n10 = hash(dot((i + o.yx), m));
    float n11 = hash(dot((i + o.xx), m));

    float fx = quintic(f.x);
    float px0 = mix(n00, n01, fx);
    float px1 = mix(n10, n11, fx);

    return mix(px0, px1, quintic(f.y));
}

float ocean(in vec2 p) {
    float f = 0.;

    float speed = .4;
    vec2 v01 = vec2( 1.,  0.) * iTime*speed;
    vec2 v02 = vec2( 0.,  1.) * iTime*speed;
    vec2 v03 = vec2( 1.,  1.) * iTime*speed;
    vec2 v04 = vec2(5.,  0.) * iTime*speed;
    vec2 v05 = vec2(8.,  0.) * iTime*speed;
    vec2 v06 = vec2(4., 1.) * iTime*speed;

    f += 0.50000*noise(p*1.0  + v01);
    f += 0.25000*noise(p*2.1  + v02);
    f += 0.12500*noise(p*3.9  + v03);
    f += 0.06250*noise(p*8.1  + v04);
    f += 0.03215*noise(p*15.8 + v05);
    f += 0.01608*noise(p*32.3 + v06);

    f = (3.-2.*f)*f*f;

    f = 0.5*f + 0.25*f*sin(iTime) + 0.5;

    return f;

}

float map(in vec3 p) {   
    float o = ocean(p.xz * 0.08) * 3.;
    return p.y + 0.5 + o;
}

float calcShadow(in vec3 ro, in vec3 rd, float tmax) {
    float r = 1.;
    float t = 0.;
    for(int i = 0; i < 128; i++) {
        float h = map(ro + t * rd);
        r = min(r, tmax*h/t);
        if (r < 0.01) break;
        if (t > tmax) break;
        t += h;
    }
    return clamp(r, 0., 1.);
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));

}


const vec3 SUN_DIR = normalize(vec3(-0.2, 0.15, -0.8));
#if SUNSET
const vec3 SUN_COL = vec3(0.9, 0.4, 0.2);
#else
const vec3 SUN_COL = vec3(0.95, 0.8, 0.85);
#endif

vec3 sky(in vec3 rd, vec3 sunDir, float fac) {
    rd.y = max(0., rd.y);

    #if SUNSET
    vec3 blue = vec3(0.2, 0.6, 0.9)-rd.y*0.85;
    vec3 sunset = mix(blue, SUN_COL*0.9, exp(-rd.y*8.));

    vec3 sun = 5.*pow(dot(sunDir, rd), 90.) * SUN_COL;
    return sunset * fac + sun;
    #else

    vec3 blue = 0.6* vec3(0.02, 0.09, 0.2) -rd.y*0.1;
    vec3 sunset = blue; //mix(blue, blue, exp(-rd.y*8.));

    vec3 sun = smoothstep(0.989, 0.99, dot(sunDir, rd)) * SUN_COL;
    return sunset + sun;

    #endif
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0., 0., 0.);
    vec3 ta = vec3(0., 0., 1000.);

    vec3 up = vec3(0., 1., 0.);
    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww, up));
    vec3 vv = normalize(cross(uu, ww));

    vec3 rd = normalize(vec3(p.x*uu + p.y*vv - 3.0*ww));

    float m = -1.;
    float t = 0.;
    float tmax = 300.;
    for(int i = 0; i<512; i++) {
        float h  = map(ro + rd * t);
        if ( h<0.01 ) { m = 1.; break; };
        if ( t>tmax ) break;
        t += h;
    }

    float sunsetFac = mod(iTime*0.02, 1.);
    vec3 sunDir = normalize(SUN_DIR + vec3(0., -sunsetFac*0.4,0.));
    vec3 skyCol = sky(rd, sunDir, 1.-sunsetFac);
    vec3 col = vec3(0.);

    if (m > 0.) {
        vec3 nor = calcNormal(ro + rd * t);
        vec3 ref = reflect(rd, nor);
        vec3 refCol = sky(ref, sunDir, 1.-sunsetFac);

        float d = dot(sunDir, nor);
        vec3 dif = refCol*clamp(d, 0., 1.);
        vec3 amb = vec3(0.01, 0.03, 0.08);
        vec3 spec = refCol*pow(clamp(d+0.9, 0.,1.), 200.0);

        col = amb + mix(dif, spec, 0.4);
    }

    // fade out the horizon
    col = mix(col, skyCol, pow(min(t, tmax)/tmax, 2.));

    // volumetrics
    #if FOG
    vec3 vol = vec3(0.);
    float den = 0.;
    float h = noise(p);
    float dh = 0.3*tmax / 32.0;

    for (int i = 0; i < 32; i++) {
        vec3 pos = ro + h*rd;
        vec3 dir = sunDir - pos;
        vec3 l = (SUN_COL*calcShadow(pos, normalize(dir), length(dir)));
        float d = noise(pos + 2.*vec3(iTime, -iTime, -iTime)) * exp(-0.85*pos.y);

        den += d*0.002;
        vol += l*den;

        h += dh;
        if (h > t) break;

    }
    col += pow(vec3(vol), vec3(.75))*0.1;
    #endif

    col = pow(col, vec3(0.4545));

    // fade out
    col *= smoothstep(0., 0.1, 1.-sunsetFac);
    // fade in
    col *= smoothstep(0., 0.01, sunsetFac);


    fragColor = vec4(col,1.0);
}

