Shape orbs(vec3 c){
  Shape shape;
  float s;
  shape.dist = 2000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color


  vec3 i = c;
    i.x = abs(i.x)-(2.5+cos(iTime)*.001);
    i.zx *= rot(c.x*0.1+iTime*0.5); // Twist Coord Sys
    i.yz *= rot(c.z*0.2+iTime*0.5);
    //i.xy *= rot(c.z*0.1+iTime*0.5);
    pModPolar(i.xy, 18.); // Twist Repeat
    i.x -= abs(cos(iTime*0.5))*3.; // Offset from origin
    i.y -= abs(cos(iTime)*.01);
    i.z -= abs(sin(iTime)*.01);
    //pMod1(i.z, 3.);
  	s = sphere(i, 10.); // Create sphere

    // Plug-in distance to object and color
    vec4 color = vec4(1.,1.,.95+(sin(iTime)*0.1),.5);
  	shape.dist = s;
  	shape.color = color;

  return shape;
}


// Map all objects within scene
Shape map(vec3 c){
  Shape orb = orbs(c);
  return orb;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(cos(iTime), sin(iTime), -10.); // Setup Camera
  vec3 f = normalize(vec3(v, 1.)); // Frustrum

  vec3 scene = cam;
  fragColor = vec4(0.); // Background Color

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.001){
      fragColor = c.color*(cos(z)); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist; // Step to SDF



    // Output to screen
    fragColor = vec4(.7+cos(iTime)*0.1,0.7+cos(iTime)*.1,1.,1.0);

  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

