vec3 col0 = vec3(0.839, 0., 0.223); //red
vec3 col1 = vec3(0., 0.517, 0.839); // blue
vec3 col2 = vec3(0.705, 1., 0.541); // green
const float third = 1./3.;
vec3 colRange(float t) {
    if (t <= third) {
        return mix(col0, col1, t/third);
    }
    if (t <= third*2.) {
        return mix(col1, col2, (t-third)/third);
    }
    return mix(col2, col0, (t-2.*third)/third);
}

const float R2 = 1000.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pointZoomed = vec2(-0.743643887037151, 0.13185790420533);;
    // Normalized pixel coordinates (from -1.0 to 1.0 for x)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.y *= iResolution.y/iResolution.x;

    float n;
    vec2 zn;
    float twoPown = 1.;
    float maxIter = 100.;
    float sqModulus;

    float maxX = 1.5/exp(mod(10.*iTime/100., 18.));
    vec2 pointC = maxX * uv + pointZoomed;
    vec3 col = vec3(0.);
    bool isInSet = true;

    // points of the set known
    float va = pointC.x - .25;
    float p2 = va*va + pointC.y*pointC.y;
    if ( pointC.x > sqrt(p2) - 2.*p2 + .25 || (pointC.x+1.)*(pointC.x+1.)+pointC.y*pointC.y > 1./16.){

        zn = vec2(0.);
        n = 0. ;
        for(int i=1; n<maxIter; i++) {
            n += 1.;
            twoPown = twoPown*2.;
            zn = vec2(zn.x*zn.x - zn.y*zn.y, 2.*zn.x*zn.y) + pointC;
            sqModulus = dot(zn, zn);
            if (sqModulus > 1000000.) {
                isInSet = false;
                float V = log(sqModulus)/twoPown;
        		col = colRange(mod(log(V)/10. - iTime, 1.));
                break;
            }
        }
    }


    // Output to screen
    fragColor = vec4(col, 1.0);
}

/**
 * Fractal Brownian Motion
 *
 * Reference: https://thebookofshaders.com/13/
 * 
 * See also: http://www.iquilezles.org/www/articles/morenoise/morenoise.htm
 *         : http://www.iquilezles.org/www/articles/warp/warp.htm
 */

const vec3 mixColor1 = vec3(0.8, 0.35, 0.12);
const vec3 mixColor2 = vec3(0.3, 0.75, 0.69);

#define NUM_OCTAVES 5


// Get random value
float random(in vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// Get noise
float noise(in vec2 st)
{
    // Splited integer and float values.
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i + vec2(0.0, 0.0));
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // -2.0f^3 + 3.0f^2
    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

// fractional brown motion
//
// Reduce amplitude multiplied by 0.5, and frequency multiplied by 2.
float fbm(in vec2 st)
{
	float v = 0.0;
    float a = 0.5;

    for (int i = 0; i < NUM_OCTAVES; i++)
    {
    	v += a * noise(st);
        st = st * 2.0;
        a *= 0.5;
    }

    return v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate normalized UV values.
    vec2 st = fragCoord / iResolution.xy;

    vec3 color = vec3(0.0);

    vec2 q = vec2(0.0);
    q.x = fbm(st + vec2(0.0));
    q.y = fbm(st + vec2(1.0));

    // These numbers(such as 1.7, 9.2, etc.) are not special meaning.
    vec2 r = vec2(0.0);
    r.x = fbm(st + (4.0 * q) + vec2(1.7, 9.2) + (0.15 * iTime));
    r.y = fbm(st + (4.0 * q) + vec2(8.3, 2.8) + (0.12 * iTime));

    // Mixed color by 'q' and 'r'.
    color = mix(color, mixColor1, clamp(length(q), 0.0, 1.0));
    color = mix(color, mixColor2, clamp(length(r), 0.0, 1.0));

    // Calculate by 'r' is that getting domain warping.
    float f = fbm(st + 4.0 * r);

    // f^3 + 0.6f^2 + 0.5f
    float coef = (f * f * f + (0.6 * f * f) + (0.5 * f));
    color *= coef;

    fragColor = vec4(color, 1.0);
}

// Simplified by CJ.


// @lsdlive

// This was my shader for the shader showdown at Outline demoparty 2018 in Nederland.
// Shader showdown is a live-coding competition where two participants are
// facing each other during 25 minutes.
// (Round 1)

// I don't have access to the code I typed at the event, so it might be
// slightly different.

// Original algorithm on shadertoy from fb39ca4: https://www.shadertoy.com/view/4dX3zl
// I used the implementation from shane: https://www.shadertoy.com/view/MdVSDh

// Thanks to shadertoy community & shader showdown paris.

