mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

vec2 path(float t) {
	float a = sin(t*.2 + 1.5), b = sin(t*.2);
	return vec2(2.*a, a*b);
}

float g = 0.;
float de(vec3 p) {
	p.xy -= path(p.z);

	float d = -length(p.xy) + 4.;// tunnel (inverted cylinder)

//	p.xy += vec2(cos(p.z + iTime)*sin(iTime), cos(p.z + iTime));
//	p.z -= 6. + iTime * 6.;
//	d = min(d, dot(p, normalize(sign(p))) - 1.); // octahedron (LJ's formula)
	// I added this in the last 1-2 minutes, but I'm not sure if I like it actually!

	// Trick inspired by balkhan's shadertoys.
	// Usually, in raymarch shaders it gives a glow effect,
	// here, it gives a colors patchwork & transparent voxels effects.
	g += .01 / (.01 + d * d);
	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;

	float dt = iTime * 1.;
	vec3 ro = vec3(0, 0, -5. + dt);
	vec3 ta = vec3(0, 0, dt);

	ro.xy += path(ro.z);
	ta.xy += path(ta.z);

	vec3 fwd = normalize(ta - ro);
	vec3 right = cross(fwd, vec3(0, 1, 0));
	vec3 up = cross(right, fwd);
	vec3 rd = normalize(fwd + uv.x*right + uv.y*up);

	rd.xy *= r2d(sin(-ro.x / 3.14)*.3);

	// Raycast in 3d to get voxels.
	// Algorithm fully explained here in 2D (just look at dde algo):
	// http://lodev.org/cgtutor/raycasting.html
	// Basically, tracing a ray in a 3d grid space, and looking for 
	// each voxel (think pixel with a third dimension) traversed by the ray.
	vec3 p = floor(ro) + .5;
	vec3 mask;
	vec3 drd = 1. / abs(rd);
	rd = sign(rd);
	vec3 side = drd * (rd * (p - ro) + .5);

	float t = 0., ri = 0.;
	for (float i = 0.; i < 1.; i += .01) {
		ri = i;

		/*
		// sphere tracing algorithm (for comparison)
		p = ro + rd * t;
		float d = de(p);
		if(d<.001) break;
		t += d;
		*/

		if (de(p) < 0.) break;// distance field
							  // we test if we are inside the surface

		mask = step(side, side.yzx) * step(side, side.zxy);
		// minimum value between x,y,z, output 0 or 1

		side += drd * mask;
		p += rd * mask;
	}
	t = length(p - ro);

	vec3 c = vec3(1) * length(mask * vec3(1., .5, .75));
	c = mix(vec3(.2, .2, .7), vec3(.2, .1, .2), c);
	c += g * .4;
	c.r += sin(iTime)*0. + .42*sin(p.z*.25 - iTime * 0.);// red rings
	c = mix(c, vec3(.2, .1, .2), 1. - exp(-.001*t*t));// fog

	fragColor = vec4(c, 1.0);
}

vec3 striation(in vec2 uv, in vec3 color, in vec2 angle) {
    int u = int(dot(uv, angle));
    return float((u ^ u << 2) * 15 & 255) / 255. * color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime / 10.;
    vec2 r = vec2(cos(t), sin(t));
    vec2 uvf = mat2(r, -r.y, r.x) * fragCoord/mod(t/2., 6.);
	ivec2 uv = ivec2(floor(uvf));

    int mask = 4 + (int(iTime / 2.) & 7) << 2;

    vec2 fpos = uvf/iResolution.xy;
    float jitter = 20. * (fpos.x + fpos.y + fract(iTime));
    jitter = .4 * fract(jitter*jitter*jitter);

    vec3 bg = vec3(.1, .1, .1),
    	warp = striation(vec2(float((uv.x & -2) ^ 112) + mod(uvf.x, 2.) + jitter, uvf.y),
        	             vec3(8. * uvf/iResolution.xy, 1.), vec2(.8, .1)),
        weft = striation(vec2(uvf.x, float((uv.y & -2) ^ 84) + mod(uvf.y, 2.) + jitter),
                	     vec3(1,3,2), vec2(.1, .7));

    fragColor = vec4(((uv.x^uv.y) & mask) == 0
                     ? 1 == ((uv.x ^ uv.x >> 1) & 1) ? warp : bg
                     : 1 == ((uv.y ^ uv.y >> 1) & 1) ? weft : bg,
                     1.0);
}

#define PI 3.1415926

float sdCircle( vec2 p, float r )
{
 	float param = 1.0 - (length(p) - r);

    param = smoothstep(0.0, 0.1, param);
    return param;;
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 scene(vec2 uv, float time)
{
    float dist = (2.97 - 2.97*cos(min(PI,time)))/2.0;
    float radius = 0.500; // circles just touching
    float circles = 12.0;
    float alpha = 0.20; // color intensity

    radius += smoothstep(4.6, 5.0, time)*0.615 +
        smoothstep(6.5, 6.7, time)*0.47 + 
        smoothstep(8.5, 8.7, time)*0.57 + 
        smoothstep(9.5, 12.0, time)*12.0;

    vec3 col = vec3(0.0);

    for(int i = 0; i < int(circles); i++) {
        float x = dist*cos(2.0*PI*float(i)/circles);
        float y = dist*sin(2.0*PI*float(i)/circles);
        vec2 pos = uv/0.1;
        pos.x += x;
        pos.y += y;
        float hue = float(i)/circles;

        float sdf = clamp(sdCircle(pos, radius), 0.0, alpha);
        col = col + hsv2rgb(vec3(hue, 0.8, sdf));
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    uv *=1.20;

    float period = 23.0;
    float rot = iTime*0.2;
    uv *= mat2(cos(rot), -sin(rot), sin(rot), cos(rot));

    float time = mod(iTime, period);

    vec3 white = scene(uv, time);
    vec3 black = vec3(1.0) - scene(uv, time-period/2.0);

    // fade in the first pass and fade back to normal state on last pass
    white =  mix(vec3(0.0), white,
                 smoothstep(0.0, 1.0,time) -
                 smoothstep(period-1.0, period,time)
                );

    // transition between "scenes"
    vec3 col = mix(white, black, smoothstep(0.0, 0.1, -sin(2.0*PI*time/period)));

    fragColor = vec4(col, 1.0);
}

float N21(vec2 p) {
	p = fract(p * vec2(233.34, 851.73));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p) {
	float n = N21(p);
    return vec2(n, N21(p + n));
}

vec2 getPos(vec2 id, vec2 offset) {
	vec2 n = N22(id + offset);
    float x = cos(iTime * n.x);
    float y = sin(iTime * n.y);
    return vec2(x, y) * 0.4 + offset;
}

float distanceToLine(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - t * ba);
}

float getLine(vec2 p, vec2 a, vec2 b) {
	float distance = distanceToLine(p, a, b);
    float dx = 15./iResolution.y;
    return smoothstep(dx, 0., distance) * smoothstep(1.2, 0.3, length(a - b));
}

float layer(vec2 st) {
    float m = 0.;
    vec2 gv = fract(st) - 0.5;
    vec2 id = floor(st);
    // m = gv.x > 0.48 || gv.y > 0.48 ? 1. : 0.;
    // vec2 pointPos = getPos(id, vec2(0., 0.));
    // m += smoothstep(0.05, 0.03, length(gv - pointPos));

    float dx=15./iResolution.y;
    // m += smoothstep(-dx,0., abs(gv.x)-.5);
    // m += smoothstep(-dx,0., abs(gv.y)-.5);
    // m += smoothstep(dx, 0., length(gv - pointPos)-0.03);

    vec2 p[9];
    int i = 0;
    for (float x = -1.; x <= 1.; x++) {
        for (float y = -1.; y <= 1.; y++) {
        	p[i++] = getPos(id, vec2(x, y));
        }
    }

    for (int j = 0; j <= 8; j++) {
    	m += getLine(gv, p[4], p[j]);
        vec2 temp = (gv - p[j]) * 20.;
        m += 1./dot(temp, temp) * (sin(10. * iTime + fract(p[j].x) * 20.) * 0.5 + 0.5);

    }

    m += getLine(gv, p[1], p[3]);
    m += getLine(gv, p[1], p[5]);
    m += getLine(gv, p[3], p[7]);
    m += getLine(gv, p[5], p[7]);

    // m += smoothstep(0.05, 0.04, length(st - vec2(0., 0.)));
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float m = 0.;

    float theta = iTime * 0.1;
    mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
    vec2 gradient = uv;
    uv = rot * uv;

    for (float i = 0.; i < 1.0 ; i += 0.25) {
    	float depth = fract(i + iTime * 0.1);
        m += layer(uv * mix(10., 0.5, depth) + i * 20.) * smoothstep(0., 0.2, depth) * smoothstep(1., 0.8, depth);
    }

    vec3 baseColor = sin(vec3(3.45, 6.56, 8.78) * iTime * 0.2) * 0.5 + 0.5;

    vec3 col = (m - gradient.y) * baseColor;
    // Output to screen
    fragColor = vec4(col, 1.0);
}

float saturate(float x)
{
    return clamp(x, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1).
    vec2 uv = fragCoord/iResolution.xy;

    // Set the colors for the background and shape.
    vec4 bgColor = vec4(0.3, 0.3, 0.3, 1.0);
    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);

    // A factor to create some movement.
    float move = sin(iTime * 0.6);

    // Center of ring.
    vec2  absRingCenter = vec2(0.5, 0.5);

    // Move the ring into the screen center.
    vec2  ringCenter = absRingCenter * iResolution.xy;

    // Define the thickness of the ring scaled to x-axis.
    float ringThickness = 0.85;

    // Define the radius of the outside of the ring, scaled to the x-axis.
    float ringOuterRadius = 0.2 * iResolution.x * move * sign(move);

    // Define the inner radius base on the outer radius and ring thickness.
    float ringInnerRadius = ringOuterRadius * ringThickness;

    // Calculate the distance of the current frag to the ring center.
    vec2 vecFromCenter = fragCoord - ringCenter;

    // Convert the center point to a magnitude.
    float radius = length(vecFromCenter);

    // Calculate if a frag falls on the ring.
    float param = saturate(radius - ringInnerRadius) * saturate(ringOuterRadius - radius);

    // Calculate if a frag falls on the base.
    // Anti-aliasing fudge factor.
    float fudge = 0.994;    
    float leftBound = ringCenter.x - ringOuterRadius * fudge;
    float rightBound = ringCenter.x + ringOuterRadius * fudge;

    if ( (leftBound <= fragCoord.x && fragCoord.x <= rightBound) && 
          fragCoord.y <= ringCenter.y && (ringOuterRadius*0.9 <= radius) )
    {
        param = 1.0;
    }    

    // Lerp between the shapecolor and bg color based on on previous calculations.
    fragColor = mix(bgColor, color, param);

    // Approximate linear-to-sRGB conversion (improves anti-aliasing quality)
    fragColor.rgb = sqrt(fragColor.rgb);
}

/*

	Polar Grid Motion
	-----------------

	Utilizing a repeat polar grid to plot objects moving along a transcendental rose 
    curve path.

	I wanted to post something nice and simple that the average GPU could handle. This 
    was inspired in part by Vovosunt's "Dots and Spirals," but was based on Fabrice's 
    original "rosace 3" example. They say small things amuse small minds, and at the 
    time, that particular example kept me busy for ages. :)

	The particle movement is pretty standard. Each object moves in a circular path with a 
    varying radius and Z position based on transcendental functions according to angular 
    position. For anyone not quite familiar with it, I've provided some links below.

	The only mildly tricky bit was the rendering process. That involved keeping track of
	potentially overlapping objects, then sorting prior to rendering. There are three
	polar partitioned grids overlapping one another, but arranged radially and depthwise
	to look like they form a continuous closed curve. Potential overlapping objects
	consist of three overlapping polar cells (one for each loop) and each of their adjacent 
    polar neighbors (the two on either side). That's nine altogether, which is not very 
    taxing on the average GPU. Therefore, I wouldn't expect any frame rate problems.

	I've tried to keep things relatively simple, but there's still a bit of esoteric 
	dressing up code in there. For anyone who'd like to make something similar, you'd be 
    better off starting from scratch, then using this, Vovosunt's or Fabrices example as 
    a guide.

	Anyway, I'd call this a pseudo 3D example. At some stage, I'll try to come up with a
	raymarched variation. By the way, there's a "TRANSPARENT" and "RANDOM_VARIATION" 
	define below, for anyone bored enough to try them. :)


    Examples:

	// Really nice.
	Dots and Spirals - Vovosunt
	https://www.shadertoy.com/view/MltyzN

    // The original that I based this off of.
    rosace 3 ( 215 chars) - FabriceNeyret2 
