void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; //Puts the origin at the center of the screen

    float col;

    float lm = sin(iTime); //slope
    float lb = sin(iTime*0.8)/3.0; //intercept

//Slope/intercept declarations manipulate line
    //second line
    float lmp = 0.0-(1.0/lm); //calculates the slope of the perpendicular line
    float lbp = uv.y-lmp*uv.x; //and the intercept

    //Intersection
    float ix = (lbp-lb)/(lm-lmp); //Intersection Y
    float iy = lm*(ix)+lb; //Intersection X based off of the first equation
    //distance
    float dist = sqrt(pow((uv.x - ix),2.0)+pow((uv.y - iy),2.0));

    col = smoothstep(0.1,0.0,dist);

    fragColor = vec4(vec3(col),1.0); //OOH shiny
}

#define S(a,b,t) smoothstep(a,b,t)
#define WD 2.
#define nTime (iTime*0.6)+500.
#define FACT vec2(0.2,0.2)

float Dline(vec2 p, vec2 a, vec2 b) {
	// line drawing function from BigWings
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.1);
    return length(pa-ba*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.); // vec3(.6,.9,.02);

    for ( float i = 0.; i<10.; i+=0.5)
    {
    for ( float ii = 0.; ii<10.; ii+=0.6)
    {
    float d1 = sin(ii+nTime)+cos(i);
    float d2 = sin(nTime)-cos(i);
    float d3 = cos(nTime+i);
    float d4 = cos(nTime+ii)+sin(nTime+ii);

    float d = Dline(uv, vec2(d1,d2)*FACT, vec2(d3,d4)*FACT);

    float m = S(WD/200., (WD/200.)-(0.02), d)*WD;

    float col1 = (cos(nTime+ii)/2.+0.6);
    float col2 = (sin((ii+nTime))/2.+0.5);
    float col3 = (cos((i+nTime)/9.)/ii+0.5);

    col = max(col,vec3(m*col1,m*col2,m*col2));
    }
    }

    // Output to screen

    fragColor = vec4(col,1.0);
}

/* Emulate GL_CLAMP and GL_CLAMP_TO_BORDER on top of GL_CLAMP_TO_EDGE
 *
 * sampler needs to have GL_TEXTURE_WRAP_S and/or GL_TEXTURE_WRAP_T set to
 * GL_CLAMP_TO_EDGE for the emulated dimensions for this to work as expected.
 */

vec2 textureQueryLod(sampler2D sampler, vec2 P)
{
    vec2 pos = P * vec2(textureSize(sampler, 0)); // OpImageQuerySizeLod
    vec2 ddx = dFdx(pos);
    vec2 ddy = dFdy(pos);
    float tmp = max(dot(ddx, ddx), dot(ddy, ddy));
    float lambda = log2(tmp) * 0.5; // log2(x)/2 == log2(sqrt(x))
    // HACK: there's really much more that should be going on here, but we need to know about the sampler-state to figure that bit out.
    float lod = max(lambda, 0.0);
    return vec2(lod);
}

vec4 textureBorderClamp(sampler2D sampler, vec2 uv, vec2 size, vec4 borderColor, bvec2 wrap)
{
#if 1
    float lod = textureQueryLod(sampler, uv).y + 1.5 + cos(iTime); // OpImageQueryLod
    ivec2 size0 = textureSize(sampler, int(floor(lod))); // OpImageQuerySizeLod
    ivec2 size1 = textureSize(sampler, int(ceil(lod))); // OpImageQuerySizeLod
    size = mix(vec2(size0), vec2(size1), fract(lod));
    vec4 ret = textureLod(sampler, uv, lod); // OpImageSampleExplicitLod
#else
    vec4 ret = texture(sampler, uv); // OpImageSampleImplicitLod
#endif

    vec2 limit = vec2(1.0, 0.5); // 0.5 for GL_CLAMP, 1.0 for GL_CLAMP_TO_BORDER
    vec2 factors = clamp(0.5 + (abs(uv - 0.5) - 0.5) * vec2(size), vec2(0.0), limit);    
    if (wrap.x)
    	ret = mix(ret, borderColor, factors.x);
    if (wrap.y)
        ret = mix(ret, borderColor, factors.y);
    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y) + 0.5;

    vec4 borderColor = vec4(0.5 + 0.5 * cos(iTime), 0.5, 0.5 + 0.5 * sin(iTime), 1.0);
    fragColor = textureBorderClamp(iChannel0, uv, iChannelResolution[0].xy, borderColor, bvec2(true, true));
}

