Shape vines(vec3 c){ // Creates a vine shape (kind of an extension of Shape
  Shape shape; // Creates basic Shape first
  float vine;
  shape.dist = 100.; // Draw Distance
  shape.color = vec4(3.); // Initial Color

  // Shape Attributes
  float twist = 5.; // Vine Repeat
  float vineOffset = 5.; // Leaf Offset


  // Stems
    random(c);// Passes vec3 c through the random function first
  	vec3 i = c;
    i.xz *= rot(c.y*0.2+iTime*0.2); // Rotates the vines about the xz system.
    float detail = pModPolar(i.xz, twist); 
    float x = mix(5., 5.1, mod(detail, 1.)); // Small Ripples
    i.x -= vineOffset; // Offset Stems
  	float stemWidth = .15;
    //stemWidth/= 10000000.;
  	vine = pole(i.xz, stemWidth+0.02*sin(i.y*x));


  vec4 color = vec4(0.4, 1.0, 0.4, 10.); // Initialize vine color

  shape.dist = vine;
  shape.color = color;

  return shape; 
}

Shape orb(vec3 c){
  Shape shape;
  float orb;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Stems
  vec3 i = c;
    //i.xz *= rot(iTime);
    i.y = repeat(i.y, 2.5); // Repeats the orbs in they y direction every 2.5 pixels.
    i.y = abs(i.y)-2.5;
    i.x = abs(i.x)-1.5;
    orb = sphere(i, cos(sin(iTime)*0.25)*2.25);


  vec4 color = vec4(0.8, 1., 0.8, 1.); // Initializes color

  shape.dist = orb;
  shape.color = color;

  return shape;
}


Shape map(vec3 c, vec3 c1){
  Shape vines = vines(c); // Create vines
  Shape orbs = orb(c); // Create orbs

  float df = mixShapes(vines.dist, orbs.dist, 1.); // Mix vines and orbs
  vines.dist = df;

  vines.color = mix(vines.color, 
                    orbs.color*0.8, 
                    mixColors(orbs.dist*=0.9, vines.dist, 0.0)); // Mixes the colors 
	return vines; // Returns everything as one 

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y; // Normalize 


  vec3 cam = vec3(0., sin(iTime)-3.5, -10.); // Dictates where the camera is/will move. 
  random(cam+0.5);
  vec3 f = normalize(vec3(v, .9));
  random(f);
  vec3 scene = cam;
  random(scene);
  vec3 scene1 = cam *= 5.; 
  fragColor = vec4(0.2);

  // Ray Marcher
  for(float z = 0.001 ; z <= 10.; z += 0.1){
    Shape c = map(scene, scene1); // Calc SDF
    if(c.dist < 0.4){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}

// Slow, high-quality 2D hash adapted from nimitz's
// WebGL2 hash collection
// (https://www.shadertoy.com/view/Xt3cDn)
vec2 hash22(uvec2 p)
{
    const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
    uint h32 = p.y + PRIME32_5 + p.x*PRIME32_3;
    h32 = PRIME32_4*((h32 << 17) | (h32 >> (32 - 17))); //Initial testing suggests this line could be omitted for extra perf
    h32 = PRIME32_2*(h32^(h32 >> 15));
    h32 = PRIME32_3*(h32^(h32 >> 13));
    h32 = h32^(h32 >> 16);
    uvec2 rz = uvec2(h32, h32*48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU)) / float(0x7fffffff);
}

// Specialty Perlin ease-in/out function
vec2 soften(vec2 t)
{
    vec2 t3 = t * t * t;
    vec2 t4 = t3 * t;
    vec2 t5 = t4 * t;
    return 6.0f * t5 - 
           15.0f * t4 +
           10.0f * t3;
}

// Perlin noise function, taking corner + pixel positions as input
float Perlin(vec2 uv)
{
    // Find corner coordinates
    vec4 lwrUpr = vec4(floor(uv), ceil(uv));
    mat4x2 crnrs = mat4x2(lwrUpr.xw, lwrUpr.zw,
                          lwrUpr.xy, lwrUpr.zy);

    // Generate gradients at each corner
    mat4x2 dirs = mat4x2(hash22(uvec2(floatBitsToUint(crnrs[0]))),
                         hash22(uvec2(floatBitsToUint(crnrs[1]))),
                         hash22(uvec2(floatBitsToUint(crnrs[2]))),
                         hash22(uvec2(floatBitsToUint(crnrs[3]))));

    // Shift gradients into [-1...0...1]
    dirs *= 2.0f;
    dirs -= mat4x2(vec2(1.0f), vec2(1.0f), 
                   vec2(1.0f), vec2(1.0f));

    // Normalize
    dirs[0] = normalize(dirs[0]);
    dirs[1] = normalize(dirs[1]);
    dirs[2] = normalize(dirs[2]);
    dirs[3] = normalize(dirs[3]);

    // Find per-cell pixel offset
    vec2 offs = mod(uv, 1.0f);

    // Compute gradient weights for each corner; take each offset relative
    // to corners on the square in-line
    vec4 values = vec4(dot(dirs[0], (offs - vec2(0.0f, 1.0f))),
                       dot(dirs[1], (offs - vec2(1.0f))),
                       dot(dirs[2], (offs - vec2(0.0f))),
                       dot(dirs[3], (offs - vec2(1.0f, 0.0f))));

    // Return smoothly interpolated values
    vec2 softXY = soften(offs);
    return mix(mix(values.z, 
                   values.w, softXY.x),
               mix(values.x, 
                   values.y, softXY.x),
               softXY.y);
}

// Compute fractal noise for a given pixel position
//#define SCALING_FBM
float fbm(vec2 uv)
{
    const uint depth = 5u;
    const vec2 dFreq = vec2(1.01f, 1.08f);
    const float dAmpl = 1.1f;
    vec2 sfreq = vec2(1.01f); // Starting noise scaling frequency
    vec2 rfreq = vec2(1.1f); // Starting noise rotational frequency
    float ampl = 0.25f; // Starting noise intensity
    float f = 0.0f;
    for (uint i = 0u; i < depth; i += 1u)
    {
        f += ampl * abs(Perlin(uv));
        float fi = float(i);
        uv += vec2(cos(fi), 
                   sin(fi)) * (iTime * 0.25f) * rfreq;
        #ifdef SCALING_FBM
        	uv *= sfreq;
            sfreq *= dFreq + vec2(0.1f * float(i));
        #endif
        rfreq *= dFreq + vec2(0.1f * float(i));
        ampl *= dAmpl;
    }
    return f;
}

//#define NOISE_MIRR
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to [1.0 / [cellSize]])
    const float cellSize = 0.0825f;
    #ifdef NOISE_MIRR
    	float hAspect = (iResolution.x / iResolution.y) * 0.5f;
    	vec2 uv = abs((fragCoord / iResolution.y) - vec2(hAspect, 0.5f)) / cellSize;
    #else
    	vec2 uv = (fragCoord / iResolution.y) / cellSize;
    #endif
    // Generate per-channel fbm()
    // fbm(p + fbm(p + fbm(p)))
    // FBM recursion function from iq through the Book of Shaders:
    // http://www.iquilezles.org/www/articles/warp/warp.htm
    // https://thebookofshaders.com/13/
    float fbm0 = fbm(uv + vec2(
        			 fbm(uv + vec2(
                    	 fbm(uv)))));
    vec2 uu = uv + vec2(1.0f, 0.0f);
    float fbm1 = fbm(uu + vec2(
        			 fbm(uu + vec2(
                    	 fbm(uu)))));
    vec2 vv = uv + vec2(0.0f, 1.0f);
    float fbm2 = fbm(vv + vec2(
        			 fbm(vv + vec2(
                    	 fbm(vv)))));
	vec3 rgb = vec3(fbm0, fbm1, fbm2);

    // Output to screen
    fragColor = vec4(rgb, 1.0);
}

// Mark Serdtse
// Variation of distance field explanation 
// from https://thebookofshaders.com/07/?lan=ru

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = fragCoord/iResolution.xy;

  st.x -= 0.2; 
  st.x *= iResolution.x/iResolution.y;

  vec3 color = 0.5 + 0.5*cos(iTime+st.xyx+vec3(0,2,4));
  float d = 0.0;

  st = st *2.-1.;

  float sin_factor = sin(iTime/5.);
  float cos_factor = cos(iTime/5.);

  st = st* mat2(cos_factor, sin_factor, -sin_factor, cos_factor);


  d = length(abs(sin(abs(st*2.)+iTime))*(sin(abs(cos(st.x)*sin(st*5.))*.8)/2.));


  float mask = sin(d*50.0);

  color = color*mask;

  fragColor = vec4(color,1.0);

}

/*

	Tri Scale Truchet
	-----------------

	This is just a demonstration to show that it's possible to produce a
	multiscale Truchet pattern with non-overlapping tiles. The reason 
	that is of interest is that it's faster and can be more easily 
	adapted to a 3D situation. By the way, I'll put one of those together
	pretty soon.

	This was a pretty easy example to understand... before I kind of went 
    overboard and filled it with defines and esoteric aesthetic code.

	Thankfully, I quickly put together a minimal dual level Truchet example
	to accompany it, which is much easier to digest -- The link is below,
    for anyone interested.


    // Far less code, and much easier to understand.
	Minimal Dual-Level Truchet - Shane
    https://www.shadertoy.com/view/ltcfz2

	// More elaborate quadtree example.
	Quadtree Truchet - Shane
	https://www.shadertoy.com/view/4t3BW4

	// Abje always has an interesting way of coding things. :)
	black and white truchet quadtree - abje
	https://www.shadertoy.com/view/MtcBDM


*/


// Display the background grid lines.
#define SHOW_GRID

// Three levels or two. Comment it out for two.
#define TRI_LEVEL

// Various curve shapes, for anyone curious.
// Circle: 0, Octagon: 1, Dodecahedron: 2, Hexadecagon: 3
#define SHAPE 0

// Just the arcs.
//#define ARCS_ONLY

// vec2 to vec2 hash.
vec2 hash22(vec2 p){ 

    // Faster, but doesn't disperse things quite as nicely.
    return fract(vec2(262144, 32768)*sin(dot(p, vec2(57, 27))));

}

// Standard 2D rotation formula.
mat2 r2(in float a){ float c = cos(a), s = sin(a); return mat2(c, s, -s, c); }

// Cheap and nasty 2D smooth noise function with inbuilt hash function -- based on IQ's 
// original. Very trimmed down. In fact, I probably went a little overboard. I think it 
// might also degrade with large time values, but that's not an issue here.
float n2D(vec2 p) {

	vec2 i = floor(p); p -= i; p *= p*(3. - p*2.);  

	return dot(mat2(fract(sin(vec4(0, 27, 57, 84) + dot(i, vec2(27, 57)))*43758.5453))*
                vec2(1. - p.y, p.y), vec2(1. - p.x, p.x) );

}

// FBM -- 4 accumulated noise layers of modulated amplitudes and frequencies.
float fbm(vec2 p){ return n2D(p)*.533 + n2D(p*2.)*.267 + n2D(p*4.)*.133 + n2D(p*8.)*.067; }


// Distance formula with various shape metrics.
// See the "SHAPE" define above.
float dist(vec2 p){

    #if SHAPE == 0
    // Standard circular shaped curves.
    return length(p);
    #else
        p = abs(p);
        #if SHAPE == 1
        	// Octagon.
        	return max(max(p.x, p.y), (p.x + p.y)*.7071);
        #elif SHAPE == 2
        	// Dodecahedron.
        	vec2 p2 = p*.8660254 + p.yx*.5;
        	return max(max(p2.x, p2.y), max(p.x, p.y));
        #else
        	// Hexadecagon (regular, 16 sideds) -- There'd be a better formula for this.
        	vec2 p2 = r2(3.14159/8.)*p;
        	float c = max(max(p2.x, p2.y), (p2.x + p2.y)*.7071);
        	return max(c, max(max(p.x, p.y), (p.x + p.y)*.7071));
        #endif
    #endif

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){


    // Resolution restriction to avoid a blurry, bloated looking image in fullscreen,
    // This doesn't account for varying PPI, so if you went fullscreen on a high resolution
    // cell phone, you'd want a higher restriction number. Generally speaking, it's 
    // impossible to make a pixel-precise image look even roughly the same on all systems.
    // By the way, this was coded on a 17 inch laptop with a 1920 x 1080 resolution on the
    // 800 x 450 canvas.
    float iRy = min(iResolution.y, 800.); 

    // Screen coordinates.
    vec2 uv = (fragCoord - iResolution.xy*.5)/iRy;

    // Scaling and translation.
    vec2 oP = uv*4. + vec2(.5, iTime/2.);

    // Distance file values.
    vec4 d = vec4(1e5);

    // Initial cell dimension.
    float dim = 1.;

    // Random entries -- One for each layer. The X values represent the chance that
    // a tile for that particular layer will be rendered. For instance, the large
    // tile will have a 35% chance, the middle tiles, 70%, and the remaining smaller
    // tiles will have a 100% chance. I.e., they'll fill in the rest of the squares.

    vec2 rndTh[3] = vec2[3]( vec2(.35, .5), vec2(.7, .5), vec2(1, .5));

    // Set the second level random X value to "1." to ensure that the loop breaks on 
    // the second iteration... which is a long way to say, "Two levels only." :)
    #ifndef TRI_LEVEL
    rndTh[0].x = .5; rndTh[1].x = 1.;
    #endif


    // Grid line width, and a global diagonal-side variable.
    const float lwg = .015;
    float side = 1e5;

    // Random variable.
    vec2 rnd = vec2(0);


    for(int k=0; k<3; k++){

        // Base cell ID.
		vec2 ip = floor(oP*dim);

        // Unique random ID for the cell.
        rnd = hash22(ip);


		// If the random cell ID at this particular scale is below a certain threshold, 
        // render the tile.         
        if(rnd.x<rndTh[k].x){

            // Tile construction: By the way, the tile designs you use are limited by your imagination. 
        	// I chose the ones that seemed most logical at the time -- Arcs and grid vertice circles.

            // Local cell coordinate.
            vec2 p = oP - (ip + .5)/dim; // Equivalent to: mod(oP, 1./dim) - .5/dim;

            // Reusing "rnd" to calculate a new random number. Not absolutely necessary,
            // but I wanted to mix things up a bit more.
            rnd = fract(rnd*27.63 + float(k*57 + 1));

            // Grid lines.
 	        d.y = abs(max(abs(p.x), abs(p.y)) - .5/dim) - lwg/2.;


            // Use the unique random cell number to flip half the tiles vertically, which,
            // in this case, has the same effect as rotating by 90 degrees.
            p.y *= rnd.y<.5? 1. : -1.;


            // Arc width: Arranged to be one third of the cell side length. This is half that
            // length, but it gets doubled below.
            float aw = .5/3./dim;

            // Tile rendering: The arcs, circles, etc. I made the tiles up as I went along,
            // but it's just a positioning of arcs and circles, so I'm hoping it's pretty 
            // straight forward. 
            float c1 = abs(dist(p - vec2(.5)/dim) - .5/dim) - aw;

            // Arcs only, or a mixture of arcs and circles.
            #ifdef ARCS_ONLY
            float c2 = abs(dist(p - vec2(-.5)/dim) - .5/dim) - aw;
            #else
            float c2;
            if(fract(rnd.y*57.53 +.47)<.35) {
                c2 = dist(p - vec2(-.5, 0)/dim) - aw;
                c2 = min(c2, dist(p - vec2(0, -.5)/dim) - aw);
            }
            else c2 = abs(dist(p - vec2(-.5)/dim) - .5/dim) - aw;
            #endif

            // Combining the arc and\or circle elements.
            d.x = min(c1, c2);

            // Determining which side of the diagonal the blue neon tri-level lines are on.
            // That way, you can blink them individually.
            side = c1>c2? 0. : 1.57*(rnd.y*.5 + 1.);


            // Negate the arc distance field values on the second tile.
            d.x *= k==1? -1. : 1.;


            // Four mid border circles. There's some 90 degree rotation and repeat
            // trickery hidden in amongst this. If you're not familiar with it, it's
            // not that hard, and gets easier with practice.
            vec2 p2 = abs(vec2(p.y - p.x, p.x + p.y)*.7071) - .5*.7071/dim;
            p2 = vec2(p2.y - p2.x, p2.x + p2.y)*.7071;
            float c3 = dist(p2) - aw/2.; 



            // Placing circles at the four corner grid vertices. If you're only rendering
            // one level (rndTh[0].x=1.), you won't need them... unless you like them, I guess. :)
            p = abs(p) - .5/dim;
            if(k<2 && rndTh[0].x<.99) d.x = min(d.x, (dist(p) - aw));

            // Depending upon which tile scale we're rendering at, draw some circles,
            // or cut some holes. If you look at the individual tiles in the example,
            // you can see why.
            if(rndTh[1].x<.99){

                // Cut out some mid border holes on the first iteration. 
                if(k==0) d.x = max(d.x, -c3); 

                // On the middle iteration, cut out vertice corner holes.
                // On the other iterations, add smaller vertice holes.
                // I made this up as I went along, so there's probably a
                // more elegant way to go about it.
                if(k==1) d.x = max(d.x, -(dist(p) - aw));
            	else d.x = max(d.x, -(dist(p) - aw/2.));


            }


            // Increasing the overall width of the pattern slightly.
            d.x -= .01;

            // Since we don't need to worry about neighbors
            break;

        }

        // Subdividing. I.e., decrease the cell size by doubling the frequency.
        dim *= 2.;

    }



    // RENDERING.
    //
    // More complicated than you need to make it. Most of the following lines were 
    // coded on the fly for decorative purposes.

    // Background.
    vec3 bg = vec3(.1);//*vec3(1, .9, .95);
    //float pat =  clamp(sin((oP.x - oP.y)*6.283*iResolution.y/22.5) + .75, 0., 1.);
    //bg *= (pat*.35 + .65);
    float ns = fbm(oP*32.); // Noise.
    bg *= ns*.5 + .5; // Apply some noise to the background.

    // Scene color. Initiated to the background.
    vec3 col = bg;

    // Falloff variable.
    float fo;

    // Render the grid lines.
    fo = 4./iRy;
    #ifdef SHOW_GRID
    col = mix(col, vec3(0), (1. - smoothstep(0., fo*5., d.y - .01))*.5); // Shadow.
    col = mix(col, vec3(1), (1. - smoothstep(0., fo, d.y))*.15); // Overlay.
    #endif


    // Pattern falloff, overlay color, shade and electronic looking overlay.
    fo = 10./iRy/sqrt(dim);
    // Distance field color: I couldn't seem to make vibrant color work, so fell
    // back go greyscale with a dash of color. It's a cliche, but it often works. :)
    vec3 pCol = vec3(.3, .25, .275);
    float sh = max(.75 - d.x*10., 0.); // Distance field-based shading.
    sh *= clamp(-sin(d.x*6.283*18.) + .75, -.25, 1.) + .25; // Overlay pattern.


    // Drop shadow, edges and overlay.
    col = mix(col, vec3(0), (1. - smoothstep(0., fo*5., d.x))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., fo, d.x));
    col = mix(col, pCol*sh, 1. - smoothstep(0., fo, d.x + .015));

    // Darkening the rounded quads around the lit centers.
    col = mix(col, bg*sh, 1. - smoothstep(0., fo, max(d.x + .1, -(d.x + .14))));

    #ifdef TRI_LEVEL
        // Apply some blue blinking neon to the tri level pattern.
    	vec3 neon = mix(col, col*vec3(1.5, .1, .3).yxz*2., 1. - smoothstep(.7, .9, sin(rnd.y*6.283 + iTime*4. + side)));
    	col = mix(col, col*neon, 1. - smoothstep(0., fo, d.x + .16));
    #else
        // Apply some animated noisy reddish neon to the dual level pattern. 
    	vec3 neon = mix(bg, col*vec3(1.5, .1, .3)*2., smoothstep(-.5, .5, n2D(oP*3. + vec2(iTime*2.))*2. - 1.));
    	col = mix(col, neon, 1. - smoothstep(0., fo, d.x + .16));// + .125
    #endif



    // Add some subtle noise.        
    col *= ns*.25 + .75;


    // A bit of gradential color mixing.
    col = mix(col.xzy, col, sign(uv.y)*uv.y*uv.y*2. + .5);
    col = mix(col.xzy, col, (-uv.y*.66 - uv.x*.33) + .5);

    // Mild spotlight.
    col *= max(1.25 - length(uv)*.25, 0.);


    // Rough gamma correction.
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

float Sphere(vec2 pos){
    return smoothstep(length(pos)+0.5,length(pos),0.51);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec4 col = vec4(0.0);
    vec2 uv = fragCoord/iResolution.y - 0.5; // Ha!
    vec4 bg = vec4(.11, 0.352, 0.729,1.0);
    vec4 fl = vec4(.4,.6,0.1,1.0)*smoothstep(0.0,-0.05,uv.y+0.15);
    vec2 sPos = uv+sin(iTime)*0.5;
    vec4 sol = vec4(0.9, 0.725, 0.2,1.0)+Sphere(sPos)+
        mix(.1,.0,sin(iTime)*5.0);


    col += mix(col,bg,bg.a);
    col = mix(col,sol,sol.a);

    col = mix(col,fl ,fl.a);

    fragColor = vec4(col);
}

const vec2 REPS = vec2(3.0, 2.0);
const float AMP = .325;

vec2 hash(vec2 vec) {
    return fract(sin(vec2(dot(vec,vec2(622.1,311.7)),dot(vec,vec2(269.5,383.3))))*42758.5453);
}

float sineNoise(float x) {
    return (sin(3.0 * x) + sin(7.0 * x) + sin(9.0 * x) + sin(13.0 * x) + sin(17.0 * x) + sin(31.0 * x)) * 0.12 + 0.5;
}


vec4 drawSwirlyBits(vec2 uv, float t, vec2 uv_off, float seed,  vec3 color) {

    uv += uv_off;

    //scale the domain
    vec2 p = uv * REPS;

    // up and down ocilation of entire domain
    uv.y += cos((t + seed) * .99) * 0.10;

    // add a wave to domain
    p.y += sin((uv.x - t * 0.1) * 8.5) * AMP;
    p.x += cos((uv.y + t * 0.2) * 3.5) * AMP;

    // get index for each row and column
    vec2 index = floor(p);

    // repeat the domain
    p = fract(p);
    // range -1 to 1
    p = p * 2.0 - 1.0;

    // use atan2 to paramertize each cell with a cirle
    float spiral = atan(p.x, p.y);

    // get a random value per cell
    float rdm = hash(index + seed).x; 

    // shifts the clock for each cell
    float cell_time = t + rdm * 3.0;

    // radar scan line
    float epsilon = 2.5;
    float cur_spot = mod(cell_time, 6.28) - 3.14;
    float radar_line = abs(spiral - cur_spot) < epsilon ? (spiral - cur_spot) / epsilon : 0.0;
    radar_line = clamp(radar_line, 0.0, 1.0);
    radar_line = pow(radar_line, 3.5);
    radar_line = clamp(radar_line, 0.0, 1.0);

    // radar ring
    float delta = 0.09 + (rdm - 0.5)  * 0.05;
    float ring_cur = mod((-cell_time ) * (1.0 / 6.28), 1.0);
    // test making ring wavey 
    delta += sineNoise(spiral * .34 + seed + rdm)  * 0.05;
    // make a tadpole like profile modulating the radar line
    float profile = 1.0 - abs(1.0 - (radar_line * 1.5));
    // apply the profile to our delta
    delta *= profile ;
    float ring_gradient = length(p);
    float radar_ring = abs(ring_gradient - ring_cur) < delta ? 0.999 : 0.0;

    // get the little swimmy bits
    float bits = clamp(min(radar_line, radar_ring), 0.0, 1.0);

    // each cell is randomly more gray sinking into background
    bits *= rdm;

    // fade them smoothly into and out of existance by reshaping cur_spot
    // put in zero to one range
    float fade = (cur_spot + 3.1415) / 6.2831;
    // make linear symertrical
    fade = 1.0 - abs(1.0 - (fade * 2.0));
    // make non-linear so we can see the bit longer
    //fade = pow(fade, 0.45);
    // apply fade
    bits *= fade;

    // give the bits a whitish tip, fading out at the tail
    color = mix(color, vec3(1.0), bits) * bits;

    return vec4(color, bits);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;
	vec2 uv = (gl_FragCoord.xy / iResolution.xy);
     // correct for image aspect...this still doesn't look right to me
    float aspect =  iResolution.x / iResolution.y;
    uv.x *= aspect;
    // range -1 to 1
    vec2 p = uv * 2.0 - 1.0;
    // a very dark background gradient
    vec4 bg = vec4(0.0, uv.y * 0.03, (1.0 - length(p) / 1.8) * 0.07, 0.0);

    // compositing five instances of swirly bits
    vec4 swirly1 = drawSwirlyBits(uv * 0.65, t, vec2(0.5, 9.4), 12.0, vec3(0.0, 1.0, 0.0));
    // this one is transposed 90 degrees
    vec2 uv_tilted = vec2(uv.y, uv.x);
    vec4 swirly2 = drawSwirlyBits(uv_tilted, t * 1.25, vec2(33.0, 0.0), 0.1, vec3(0.4, 0.0, 1.0));
    vec4 swirly3 = drawSwirlyBits(uv * vec2(-1.5, 1.5), t * 1.5, vec2(0.0, 2.0), 42.0, vec3(0.8, .6, .0));
    // this one spins clockwise
    float slow = t * 0.1;
    mat2 rot1 = mat2(cos(-slow), sin(-slow), -sin(-slow), cos(-slow));
    vec2 uv_rot1 = rot1 * p;
    vec4 swirly4 = drawSwirlyBits(uv_rot1 * 0.35, t * 0.94, vec2(0.0, 2.0), 1.0, vec3(0.0, 1.0, 1.0));   
    // this one spins counter clockwise
    slow = slow * 1.2;
    mat2 rot = mat2(cos(slow), sin(slow), -sin(slow), cos(slow));
    vec2 uv_rot = rot * p;
    vec4 swirly5 = drawSwirlyBits(uv_rot * 0.7, t * 1.1, vec2(0.0, 2.0), 71.0, vec3(1.0, 0.0, 1.0));

    // doing a z composite on all 5
    if (swirly1.w > bg.w) {
        bg = swirly1;
    }
    if (swirly2.w > bg.w) {
        bg = swirly2;
    }
    if (swirly3.w > bg.w) {
        bg = swirly3;
    }
    if (swirly4.w > bg.w) {
        bg = swirly4;
    }
    if (swirly5.w > bg.w) {
        bg = swirly5;
    }

    // testing subtle usage 
    //bg = vec4(0.0, 0.1, 0.25, 0.0) + bg.w * 0.045;

    fragColor = vec4(bg.xyz, 1.0);
}

// Menger Sponge - iOS AR. Created by Reinder Nijhoff 2018
// Creative Commons Attribution-NonCommercial-ShareAlike 4.0 International License.
// @reindernijhoff
//
// https://www.shadertoy.com/view/XttfRN
//
// This is an experiment to create an "AR shader" by implementing the mainVR-function and 
// using the WebCam texture as background. If you view this shader with the Shadertoy iOS 
// app[1], you can walk around the cube to view it from all sides.
//
// If you don't have an iOS device (or if you don't have the app installed) you can find a
// screen capture of the shader in action here: https://youtu.be/7woT6cTx-bo.
//
// The SDF of this shader is based on the "Menger Sponge" shader by Íñigo Quílez:
// https://www.shadertoy.com/view/4sX3Rn
//
// [1] https://itunes.apple.com/us/app/shadertoy/id717961814
//

float sdBox( vec3 p, vec3 b ) {
  vec3  di = abs(p) - b;
  float mc = max(di.x,max(di.y,di.z));
  return min(mc,length(max(di,0.0)));
}

float boxIntersect( in vec3 ro, in vec3 rd, in vec3 rad ) {
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

	if( tN > tF || tF < 0.0) return 1e30;
	return tN;
}

float map( in vec3 p ) {
    float d = sdBox(p,vec3(1.0));
    float s = .5;
    for( int m=0; m<4; m++ ) {
        vec3 a = fract( p*s )-.5;
        s *= 3.;
        vec3 r = abs(1.-6.*abs(a));
        float da = max(r.x,r.y);
        float db = max(r.y,r.z);
        float dc = max(r.z,r.x);
        float c = (min(da,min(db,dc))-1.0)/(2.*s);

        if( c>d ) {
          d = c;
        }
    }
    return d;
}

float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax ) {
	float res = 1.0;
    float t = mint;
    float ph = 1e10; 
    for( int i=0; i<32; i++ ) {
		float h = map( ro + rd*t );
       	float y = h*h/(2.0*ph);
        float d = sqrt(max(0.,h*h-y*y));
        res = min( res, 8.0*d/max(0.0001,t-y) );
        ph = h;
        t += h;//min(h, .1);// clamp( h, 0.02, 0.10 );
        if( res<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float calcAO( in vec3 pos, in vec3 nor ) {
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ) {
        float hr = 0.01 + 0.5*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.9;
    }
    return clamp( 1. - 3.0*occ, 0.0, 1.0 );    
}

vec3 calcNormal(in vec3 pos) {
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy) - map(pos-eps.xyy);
    nor.y = map(pos+eps.yxy) - map(pos-eps.yxy);
    nor.z = map(pos+eps.yyx) - map(pos-eps.yyx);
    return normalize(nor);
}

vec4 tex3D( sampler2D sam, in vec3 p, in vec3 n ) {
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );

	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}

vec3 render( in vec3 ro, in vec3 rd, in vec2 uv, in sampler2D tex ) {
    ro *= 2.; // scale scene
    const float tmax = 100.;
    vec3 lightDir = normalize(vec3(0.7,1.,.2));
    float tmin = boxIntersect(ro, rd, vec3(1.));
    if (all(lessThan(abs(ro),vec3(1)))) tmin = 0.01;

    float t = tmin;
    for( int i=0; i<64; i++ ) {
	    float precis = 0.001*t;
	    float d = map( ro+rd*t );
        if( abs(d)<precis || t>tmax ) break;
        t += d;
    }

    vec3 col = texture(tex, uv).xyz;
    // Use mipmap level 9 to get an average environment color from the webcam texture
    // used for lighting.
    vec3 lightColor = pow(.25 + .75 * texelFetch(tex, ivec2(0), 9).rgb, vec3(2.2)) * 3.;

    if (t < tmax) {
        vec3 p = ro + t * rd;
  		vec3 n = calcNormal(p);
        vec3 ref = reflect(rd, n);

        float ao = .4 + .6 * calcAO(p, n);
        float sh = .4 + .6 * calcSoftshadow(p, lightDir, 0.005, 1.);

        float diff = max(0.,dot(lightDir,n)) * ao * sh;
        float amb  = (.4+.2*n.y) * ao * sh;
		float spe = pow(clamp(dot(ref,lightDir), 0., 1.),8.) * sh * .5;

        vec3 mat = tex3D(iChannel2, p, n).rgb;
        col = (amb + diff) * mix(vec3(.4,.6,.8),vec3(.1,.2,.3),mat.r) + spe * dot(mat,mat);
        col *= lightColor;
    }

    // gamma
    col = mix(col, sqrt(clamp(col,vec3(0),vec3(1))), .95);

    return clamp(col,vec3(0),vec3(1));
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr ) {
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    float a = .3 * iTime;
    vec3 ro = 2. * vec3( sin(a), .1, cos(a) );
    vec3 ta = vec3( 0.0, 0., 0.0 );

    mat3 ca = setCamera( ro, ta, 0.0 );
    vec3 rd = ca * normalize( vec3(p.xy,2.0) );

    vec3 col = render( ro, rd, fragCoord.xy/iResolution.xy, iChannel1 );
    fragColor = vec4(col,1.0);
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd ) {
    ro += vec3(0,0.5,1.5);
    vec3 col = render( ro, rd, fragCoord.xy/iResolution.xy, iChannel0 );
    fragColor = vec4(col,1.0);
}

