// Peuriere Romain

const int Steps = 1000;
const float Epsilon = 0.05; // Marching epsilon
const float T=0.5;

const float rA=10.0; // Maximum ray marching or sphere tracing distance from origin
const float rB=40.0; // Minimum

// Transforms
vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca*p.y - sa*p.z, sa*p.y + ca*p.z);
}

vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca*p.x + sa*p.z, p.y, -sa*p.x + ca*p.z);
}

vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca*p.x + sa*p.y, -sa*p.x + ca*p.y, p.z);
}


// Smooth falloff function
// r : small radius
// R : Large radius
float falloff( float r, float R )
{
  float x = clamp(r/R,0.0,1.0);
  float y = (1.0-x*x);
  return y*y*y;
}

// Primitive functions

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : large radius
float point(vec3 p, vec3 c, float e,float R)
{
    return e*falloff(length(p-c),R);
}

// Segment skeleton
// p : point
// c1 : 1st extremity of skeleton
// c2 : 2nd extremity of skeleton
// e : energy associated to skeleton
// R : large radius
float segment(vec3 p, vec3 c1, vec3 c2, float e, float R)
{
    vec3 unitVec = normalize(c2-c1);

    float val;

    float projectedDistance = dot((p-c1),unitVec);
    if (projectedDistance<0.0){
    	val = length(p-c1); 
    }else if (projectedDistance>length(c1-c2)){
     	val = length(p-c2);   
    }else{
    	val = sqrt(length(p-c1) * length(p-c1) - projectedDistance * projectedDistance);   
    }
    return e*falloff(val,R);
}

// Disc skeleton
// p : point
// center : center of the disc
// n : normal to the disc
// discRadius : disc radius
// e : energy associated to skeleton
// R : large radius
float disc(vec3 p, vec3 center, vec3 n, float discRadius, float e, float R)
{
    float distanceToCenter = length(center-p);

    n = normalize(n); // disc normal
    float verticalDistance = dot((p-center),n);
    float horizontalDistance = sqrt(distanceToCenter*distanceToCenter - verticalDistance*verticalDistance);

    float val;
    if (horizontalDistance<discRadius){
        val = abs(verticalDistance);
    }else{
     	val = sqrt( (horizontalDistance-discRadius)*(horizontalDistance-discRadius) + verticalDistance*verticalDistance);    
    }

    return e*falloff(val,R);
}

// Torus skeleton
// p : point
// center : center of the Torus
// n : normal to the Torus
// discRadius : Torus radius
// e : energy associated to skeleton
// R : large radius
float torus(vec3 p, vec3 center, vec3 n, float discRadius, float e, float R)
{
    float distanceToCenter = length(center-p);

    n = normalize(n); // disc normal
    float verticalDistance = dot((p-center),n);
    float horizontalDistance = sqrt(distanceToCenter*distanceToCenter - verticalDistance*verticalDistance);

    float val = sqrt( (horizontalDistance-discRadius)*(horizontalDistance-discRadius) + verticalDistance*verticalDistance);    


    return e*falloff(val,R);
}

// Cube skeleton
// p : point
// pmin : lower point of the cube
// pmax : upper point of the cube
// e : energy associated to skeleton
// R : large radius
float cube(vec3 p, vec3 pmin,vec3 pmax, float e, float R) 
{
  float dx = max(0.0 , max(pmin.x - p.x, p.x - pmax.x));
  float dy = max(0.0 , max(pmin.y - p.y, p.y - pmax.y));
  float dz = max(0.0 , max(pmin.z - p.z, p.z - pmax.z));

  float val = sqrt(dx*dx + dy*dy + dz*dz);    
  return e*falloff(val,R);
}

// Plan skeleton
// p : point
// c1 : 1st extremity of the plan diagonal
// c2 : 2nd extremity of the plan diagonal
// n : normal to the plan
// e : energy associated to skeleton
// R : large radius
float plan(vec3 p, vec3 c1, vec3 c2, vec3 n, float e, float R)
{
    //float v = plan(p, vec3(-2,-2,0), vec3(2,2,0), vec3(0,0,1),1.0, 2.0);
    vec3 c3,c4;

    vec3 diag = normalize(c2-c1);

    c3 = c1 + 0.5 * (c2-c1) + 0.5 * length(c2-c1) * normalize(cross(diag,n)); // corner right
    c4 = c1 + 0.5 * (c2-c1) - 0.5 * length(c2-c1) * normalize(cross(diag,n)); // corner left


    vec3 unitVec1 = normalize(c3-c1);
    vec3 unitVec2 = normalize(c4-c1);

    n = normalize(n);

    float val;

    float projectedDistance1 = dot((p-c1),unitVec1);
    float projectedDistance2 = dot((p-c1),unitVec2);

    if (projectedDistance1<0.0){
         if (projectedDistance2<0.0){
    		val = length(p-c1); 
         }else if (projectedDistance2>length(c1-c4)){
            val = length(p-c4);
         }else{
            val = sqrt(length(p-c1) * length(p-c1) - projectedDistance2 * projectedDistance2);
         }
    }else if (projectedDistance1>length(c1-c3)){
         if (projectedDistance2<0.0){
    		val = length(p-c3); 
         }else if (projectedDistance2>length(c3-c2)){
            val = length(p-c2);
         }else{
            val = sqrt(length(p-c3) * length(p-c3) - projectedDistance2 * projectedDistance2);
         }
    }else{
         if (projectedDistance2<0.0){
    		val = sqrt(length(p-c1) * length(p-c1) - projectedDistance1 * projectedDistance1);
         }else if (projectedDistance2>length(c1-c4)){
            val = sqrt(length(p-c4) * length(p-c4) - projectedDistance1 * projectedDistance1);
         }else{
            float projectedDistance3 = dot((p-c1),n);
            val = abs(projectedDistance3);
         }

    }
    return e*falloff(val,R);
}

// Triangle skeleton
// p : point
// c1 : 1st extremity of the triangle
// c2 : 2nd extremity of the triangle
// c3 : 3rd extremity of the triangle
// n : normal to the plan
// e : energy associated to skeleton
// R : large radius
float triangle(vec3 p, vec3 c1, vec3 c2, vec3 c3, float e, float R)
{
    vec3 n = normalize(cross((c2-c1),(c3-c1)));

    vec3 unitVec1 = normalize(c2-c1);
    vec3 unitVec2 = normalize(c3-c1);
    vec3 unitVec3 = normalize(c3-c2);

    float val;

    float sign1 = (p.x - c2.x) * (c1.y - c2.y) - (c1.x - c2.x) * (p.y - c2.y);
    float sign2 = (p.x - c3.x) * (c2.y - c3.y) - (c2.x - c3.x) * (p.y - c3.y);
    float sign3 = (p.x - c1.x) * (c3.y - c1.y) - (c3.x - c1.x) * (p.y - c1.y);


    if(sign1>=0.0 && sign2>=0.0 && sign3>=0.0){
        val = abs(dot((p-c1),n));
    }else{
        val = 10000.0;
    }
    return e*falloff(val,R);

}



// Blending
// a : field function of left sub-tree
// b : field function of right sub-tree
float Blend(float a,float b)
{
    return a+b;
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
    return max(a,b);
}

// Intersection
// a : field function of left sub-tree
// b : field function of right sub-tree
float Intersection(float a,float b)
{
    return min(a,b);
}

// Without
// a : field function of left sub-tree
// b : field function of right sub-tree
float Without(float a,float b)
{
    return min(a,2.0*T - b);
}

// Potential field of the object
// p : point
float object(vec3 p)
{
  	p.z=-p.z;
    // Point non tournant pour le plan
    vec3 pold = p;
    // Rotation du pneu
    p = rotateZ(p,iTime*4.0);

    //PNEU

    //torus principal
    float v = torus(p, vec3(0,0,-0.5), vec3(0,0,1), 3.5, 1.0, 2.0);
    v = Blend(v,torus(p, vec3(0,0,+0.5), vec3(0,0,1), 3.5, 1.0, 2.0));

    //bandes rainures
    v = Blend(v,torus(p, vec3(0,0,0), vec3(0,0,1), 4.6, -0.1, 0.1));
    v = Blend(v,torus(p, vec3(0,0,-0.7), vec3(0,0,1), 4.4, -0.1, 0.1));
    v = Blend(v,torus(p, vec3(0,0,+0.7), vec3(0,0,1), 4.4, -0.1, 0.1));
    v = Blend(v,torus(p, vec3(0,0,-0.9), vec3(0,0,1), 4.3, -0.1, 0.1));
    v = Blend(v,torus(p, vec3(0,0,+0.9), vec3(0,0,1), 4.3, -0.1, 0.1));

    // cotés (aplatissement)
    v = Blend(v,disc(p, vec3(0,0,+1.5), vec3(0,0,1), 3.5, -1.0, 0.5));
    v = Blend(v,disc(p, vec3(0,0,-1.5), vec3(0,0,1), 3.5, -1.0, 0.5));

    // chevrons
    /*
  	for(float i = 0.0; i < 2.0*3.14;i=i+0.1){
    	v = Blend(v,segment(p, rotateZ(vec3(-4.5,1.0,0.0),i), rotateZ(vec3(-4.5,0,-0.7),i), -0.1, 0.05));
    	v = Blend(v,segment(p, rotateZ(vec3(-4.5,1.0,0.0),i), rotateZ(vec3(-4.5,0,0.7),i), -0.1, 0.05));
     	v = Blend(v,segment(p, rotateZ(vec3(-4.3,0.0,-1.0),i), rotateZ(vec3(-4.0,0,-1.2),i), -0.1, 0.05));
	}
	*/

    // JANTE
    v = Union(v,disc(p, vec3(0,0,-1.2), vec3(0,0,1), 3.3, 1.0, 0.1));

    // Triangles de la jante
  	for(float i = 0.0; i < 2.0*3.14;i=i+1.04){
    	v = Without(v,triangle( p,  rotateZ(vec3(-1,-3,-1.3),i),  rotateZ(vec3(1,-3,-1.3),i),  rotateZ(vec3(0,-0.5,-1.3),i),1.0,0.5));

  	}
    // Centre de la jante
  	v = Union(v,disc(p, vec3(0,0,-1.1), vec3(0,0,1), 1.0, 1.0, 0.3));


  	for(float i = 0.0; i < 2.0*3.14;i=i+1.04){
    	//trou écrou
      	v = Without(v,point(p,  rotateZ(vec3(-0.5,-0.8,-1.3),i), 1.0, 0.3));
		// segment associé
      	v= Without(v,segment(p,rotateZ(vec3(-1.5,-2.6,-1.2),i), rotateZ(vec3(-0.7,-1.2,-1.2),i), 1.0 ,0.3));
 	 }

   	// Centre enfoncé
  	v = Blend(v,disc(p, vec3(0,0,-1.2), vec3(0,0,1), 0.5, -1.0, 0.2));

    // plan final
    v = Union(v,plan(pold,vec3(-4,-4.5,-4),vec3(4,-4.5,4),vec3(0,1,0),1.0,0.2));

  	return v-T;
}

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.0001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

    // Don't start at the origin, instead move a little bit forward
    float t=rA;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v > 0.0)
      {
          h = true;
          break;
      }
      // Move along ray
      t += Epsilon;
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

    // Don't start at the origin, instead move a little bit forward
    float t=rA;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v > 0.0)
      {
      	h = true;
      	break;
      }
      // Move along ray
      t += max(Epsilon,abs(v)/4.0);
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}


// Background color
vec3 background(vec3 rd)
{
  return mix(vec3(0.4, 0.3, 0.0), vec3(0.7, 0.8, 1.0), rd.y*0.5+0.5);
}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n)
{
  // point light
  const vec3 lightPos = vec3(5.0, 5.0, 5.0);
  const vec3 lightColor = vec3(0.5, 0.5, 0.5);

  vec3 c = 0.25*background(n);
  vec3 l = normalize(lightPos - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = 0.5*(1.0+dot(n, l));

  c += diff*lightColor;

  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return vec3(t,0.25+0.75*t,0.5-0.5*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 pixel = (gl_FragCoord.xy / iResolution.xy)*2.0-1.0;

  // compute ray origin and direction
  float asp = iResolution.x / iResolution.y;
  vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -4.0));
  vec3 ro = vec3(0.0, 0.0, 20.0);

  // vec2 mouse = iMouse.xy / iResolution.xy;
  float a=iTime*2.0;
  ro = rotateY(ro,- 0.90);
  rd = rotateY(rd, -0.90);
 // ro = rotateY(ro, a);
 // rd = rotateY(rd, a);

  // Trace ray
  bool hit;

  // Number of steps
  int s;

  float t = SphereTrace(ro, rd, hit,s);
  vec3 pos=ro+t*rd;
  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pos);

    // Shade object with light
    rgb = Shade(pos, n);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  //rgb = ShadeSteps(s);

  fragColor=vec4(rgb, 1.0);
}

// Set to true to see where the image is likely to be distorted
#define debug false

float tunnel(vec3 p) {
    float d = sqrt(p.x * p.x + p.y * p.y);
    return 10.0 - d;
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec2 rotate(vec2 p, float th) {
    return vec2(
        p.x * cos(th) - p.y * sin(th),
        p.x * sin(th) + p.y * cos(th)
    );
}

float disc(vec3 p) { 
    //p.x += cos(iTime) * 10.0;
    //p.y += sin(iTime) * 10.0;

    //p.yz = rotate(p.yz, iTime);

    p.xy = rotate(p.xy, iTime);

    return sdCappedCylinder(p, vec2(2.0, 0.1));
}

float scene(vec3 p) {
    //return tunnel(p);
    float d = tunnel(p);
    //d = min(d, disc(p + vec3(0.0, 0.0, -40.0)));
    d = min(d, disc(p + vec3(0.0, 0.0, -20.0)));
    //d = min(d, disc(p + vec3(0.0, 0.0, -80.0)));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    fragCoord.x -= iResolution.x / 2.;
    fragCoord.y -= iResolution.y / 2.;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 p = vec3(0.);   
    p.x += cos(iTime) * 5.0;
    p.y += sin(iTime*1.1) * 3.0;

    vec3 direction = normalize(vec3(fragCoord, iResolution.x));
    direction.x += cos(iTime) * .01;
    direction.y += sin(iTime) * .01;


    int maxSteps = min(500, int(floor(iTime)) * int(floor(iTime)));
    float dist = 1.;
    vec3 ray_t = p + dist * direction;
    while (maxSteps-- > 0) {
        float minDist = scene(ray_t);
        if (abs(minDist) < 0.001) {
            break;
        }
        dist += minDist;
        ray_t = p + dist * direction;
    }

    float d = dist;

    float fogginess = 1./exp(d*.02);

    col = vec3(fogginess*(0.5+0.5*sin(d+iTime*10.0)));

    if (debug)
        col.x -= float(maxSteps);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*20.0;

    // Time varying pixel color
    vec3 col =  0.5*tan(iTime+vec3(uv.x * uv.y/100.0, uv.x * uv.y/10.0, uv.x * uv.y)+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col, 1) * uv.x;
}

