#define time iTime
#define ITER 64.
#define PI 3.141592
#define beat (50./60.)

mat2 rot(float a)
{ return mat2 (cos(a),sin(a),-sin(a),cos(a));}


vec2 moda(vec2 p, float per)
{
    float a = atan(p.x,p.y);
    float l = length(p);
    a = mod(a-per/2., per) -per/2.;
    return vec2 (cos(a),sin(a))*l;
}

float cylY(vec3 p, float r, float h)
{
    return max(length(p.xz)-r, abs(p.y)-h);
}

float cyl (vec2 p, float r)
{return length(p)-r;}


float box (vec3 p, vec3 c)
{return length(max(abs(p)-c,0.));}

float gears(vec3 p, float c_size)
{
    float c = max(-cylY(p,0.4, 10.),cylY(p,1.,0.2));
    p.xz = moda(p.xz, 2.*PI/7.);
    p.x -= 1.;
    float b = box(p, vec3(0.2));
    return min(b,c);
}

float prim1 (vec3 p)
{
  	float c= cyl(p.xz, 0.3);

  	float p_mod = fract(p.y - 0.5);
  	float p_index = floor(p.y - 0.5);

	float t0 = floor(time*beat);
    float t1 = fract(time*beat);
    t1 = pow(t1, 6.);
    float anim = (PI/2.)*(t0+t1);

    p.y = p_mod - 0.5;
    if (mod(p_index, 2.)==0.) p.xz *= rot(anim);
    else p.xz *= rot(-anim);
    float g = gears(p, 0.);
    return min(c,g);
}


float SDF(vec3 p)
{
    vec3 pp = p;
    float per = 5.;
    p.xz = mod(p.xz - per/2., per) - per/2.;
    float pr1 = prim1(p);

    p = pp;
    float per2 = 10.;
    p.xy = mod(p.xy, per) - per/2.;
    p.yz *= rot(PI/2.);
    float pr2 = prim1(p);

    return min(pr1,pr2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy) -1.;
    uv.x *= iResolution.x/iResolution.y;


  	float detail = 80.;
  	uv = floor(uv*detail) / detail;

  	vec3 ro = vec3(-1.,0.001 + time,-2.); vec3 p = ro;
	vec3 rd = normalize(vec3(uv,1.));

    float shad = 0.;
        for (float i = 0.; i<ITER; i++)
        {
            float d = SDF(p);
            if (d<0.001)
            {
              shad = i/ITER;
            	break;
            }
       		p+=d*rd;
        }

    float t = length(ro-p);


	vec3 col = vec3(shad) * 0.3;
	col = mix(col, vec3(131.0,145.0,0.)/255.0, 1.-exp(-0.002*t*t));

    // Output to screen
    fragColor = vec4(col,1.0);
}

