vec4 iSphere( in vec3 ro, in vec3 rd, in vec3 ce, in float ra )
{
	vec3 oc = ro - ce;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - ra*ra;
	float h = b*b - c;
	if( h<0.0 ) return vec4(-1.0);
    float t = -b - sqrt( h );
	return vec4( t, (oc + t*rd)/ra );
}

vec4 iCylinder( in vec3 ro, in vec3 rd, 
                in vec3 pa, in vec3 pb, float ra, 
                out float v ) // extreme a, extreme b, radius
{
	v = 0.0;
    vec3 ba = pb-pa;

    vec3  oc = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoc = dot(ba,oc);

    float a = baba - bard*bard;
    float b = baba*dot( oc, rd) - baoc*bard;
    float c = baba*dot( oc, oc) - baoc*baoc - ra*ra*baba;
    float h = b*b - a*c;
    if( h<0.0 ) return vec4(-1.0);
    h = sqrt(h);
    float t = (-b-h)/a;

    // body
    float y = baoc + t*bard;
    if( y>0.0 && y<baba )
    {
        v = y/baba;
        return vec4( t, (oc+t*rd - ba*v)/ra );
    }

    return vec4(-1.0);
}


#define AA 2

vec3 path( in float h )
{
    return 0.5*sin(6.2831*h*vec3(3.0,2.0,5.0)+vec3(0.0,1.0,3.0)+0.5*iTime*vec3(1.1,1.3,1.7));
}

vec4 intersect( in vec3 ro, in vec3 rd, out float h )
{
    vec3  oce = path(0.0);
    vec4 tnor = vec4(1e20);
    const int num = 80; // make this bigger for more precision curve
    float hm = 0.0;
    for( int i=1; i<=num; i++ )
    {
        float h = float(i)/float(num);
        vec3 ce = path(h);
        float ra = 0.04;

        vec4 tmp; float tmpv;

        // links
        tmp = iCylinder( ro, rd, oce, ce, ra, tmpv );
        if( tmp.x>0.0 && tmp.x<tnor.x ) { tnor = tmp; hm=h+tmpv/float(num); }

        // points
        tmp = iSphere( ro, rd, oce, ra );
        if( tmp.x>0.0 && tmp.x<tnor.x ) { tnor = tmp; hm=h; }


        oce = ce;
    }

    // enable to render floor
    //float t = (-0.6-ro.y)/rd.y; if( t>0.0 && t<tnor.x ) { hm=0.4; tnor=vec4(t,0.0,1.0,0.0); }

    h = hm;
    return tnor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.01*iTime;
	vec3 ro = vec3( 1.0*sin(an), 0.4, 1.0*cos(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


        // raytrace
        float hm;
        vec4 tnor = intersect( ro, rd, hm );
        float t = tnor.x;

        // shading/lighting	
        vec3 col = vec3(0.015)*(1.0-0.15*dot(p,p));
        if( t<1e19 )
        {
            vec3 mate = 0.5 + 0.5*cos(2.0*hm*6.2831+vec3(0.0,2.0,3.0));

            vec3 pos = ro + t*rd;
            vec3 nor = tnor.yzw;
            const vec3 lig = vec3(0.8,0.6,0.0);
            vec3 hal = normalize(lig-rd);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            float spe = clamp(dot(nor,hal),0.0,1.0);
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            float kk; float sha = (intersect( pos+nor*0.001, lig, kk ).x<1e19) ? 0.0 : 1.0;
            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);

            col = vec3(0.05,0.1,0.15)*amb + vec3(1.0,0.8,0.5)*dif*sha;
            col *= mate;
            col += 0.5*pow(spe,32.0)*dif*sha;
            col += fre*fre*0.2*mate*(0.5+0.5*dif*sha);
        }

        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

