float gradientFn(in float x, in float low, in float high, in float target) {
    float b = ((low * target) - high) / (low - high);
    float m = (1.0 - b) / low;
    return m*x + b;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
